        let message = serde_json::json!({
            "GenServerCast": {
                "module": module,
                "function": function,
                "args": serde_json::from_str::<serde_json::Value>(args).unwrap_or_default()
            }
        });

        self.runtime.handle_message(&message.to_string()).await
    }
}

// Utility functions
