#!/bin/bash

# Katalyst WebAssembly Build Script
# Compiles all Rust modules to WebAssembly for multi-runtime deployment

set -e

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

echo -e "${GREEN}🚀 Building Katalyst WebAssembly Modules${NC}"

# Ensure we have the WASM target installed
echo -e "${YELLOW}📦 Installing WASM target...${NC}"
rustup target add wasm32-unknown-unknown
rustup target add wasm32-wasi

# Install wasm-pack if not present
if ! command -v wasm-pack &> /dev/null; then
    echo -e "${YELLOW}📦 Installing wasm-pack...${NC}"
    curl https://rustwasm.github.io/wasm-pack/installer/init.sh -sSf | sh
fi

# Install wasm-opt for optimization
if ! command -v wasm-opt &> /dev/null; then
    echo -e "${YELLOW}📦 Installing wasm-opt...${NC}"
    if command -v npm &> /dev/null; then
        npm install -g wasm-opt
    else
        echo -e "${RED}❌ npm not found. Please install wasm-opt manually for optimal builds.${NC}"
    fi
fi

# Create output directory
mkdir -p wasm-modules/dist

# Build Rust Core WASM module
echo -e "${YELLOW}🔧 Building Rust Core WASM module...${NC}"
cd wasm-modules/rust-core-wasm

# Build with wasm-pack for web target
wasm-pack build --target web --out-dir ../dist/rust-core-web --release --features web

# Build with wasm-pack for Node.js target
wasm-pack build --target nodejs --out-dir ../dist/rust-core-nodejs --release --features web

# Build with wasm-pack for bundler target  
wasm-pack build --target bundler --out-dir ../dist/rust-core-bundler --release --features web

cd ../..

# Optimize WASM files if wasm-opt is available
if command -v wasm-opt &> /dev/null; then
    echo -e "${YELLOW}⚡ Optimizing WASM files...${NC}"

    find wasm-modules/dist -name "*.wasm" -type f | while read -r wasm_file; do
        echo -e "  Optimizing: $wasm_file"
        wasm-opt -Oz --enable-bulk-memory --enable-reference-types "$wasm_file" -o "$wasm_file.tmp"
        mv "$wasm_file.tmp" "$wasm_file"
    done
fi

# Create TypeScript definitions for all WASM modules
echo -e "${YELLOW}📝 Generating TypeScript definitions...${NC}"
cat > wasm-modules/dist/katalyst-wasm.d.ts << 'EOF'
// TypeScript definitions for Katalyst WebAssembly modules

export interface KatalystContext {
  id: string;
  session_id: string;
  workspace: string;
  metadata: Record<string, any>;
  created_at: number;
}

export interface TaskResult {
  task_id: string;
  status: 'Pending' | 'Running' | 'Completed' | 'Failed' | 'Cancelled';
  result: any;
  duration_ms: number;
  error?: string;
}

export class WasmKatalystCore {
  constructor();
  createContext(workspace: string, metadata_json: string): string;
  getContext(context_id: string): string | undefined;
  processTask(context_id: string, task_data_json: string): Promise<string>;
  getTaskResult(task_id: string): string | undefined;
  listContexts(): string;
}

export function get_version(): string;
export function get_build_info(): string;
export function force_gc(): void;
export function get_memory_usage(): number;

// Runtime-specific exports
export namespace RustCore {
  export function init(): Promise<void>;
  export const core: WasmKatalystCore;
}
EOF

# Generate package.json for npm distribution
echo -e "${YELLOW}📦 Generating package.json for npm distribution...${NC}"
cat > wasm-modules/dist/package.json << EOF
{
