/**
 * Vercel Edge Function for Katalyst App
 * 
 * This function serves the main Katalyst application as a WASM module
 * running on Vercel's Edge Runtime.
 */

import { NextRequest, NextResponse } from 'next/server';

// Import our WASM runtime utilities
import { VercelWasmAdapter, type VercelEdgeConfig } from '../packages/runtime-utils/vercel-adapter.ts';

// Configuration for this edge function
const edgeConfig: VercelEdgeConfig = {
  wasmModules: ['katalyst-app', 'katalyst-rust-core'],
  cacheStrategy: 'moderate',
  timeout: 10,
  memory: 128,
  regions: ['iad1', 'sfo1', 'lhr1'], // US East, US West, London
};

// Initialize the WASM adapter
let wasmAdapter: VercelWasmAdapter | null = null;

async function ensureInitialized(): Promise<VercelWasmAdapter> {
  if (!wasmAdapter) {
    wasmAdapter = new VercelWasmAdapter(edgeConfig);
    await wasmAdapter.initialize();
  }
  return wasmAdapter;
}

export const config = {
  runtime: 'edge',
  regions: edgeConfig.regions,
};

export default async function handler(request: NextRequest) {
  const startTime = Date.now();

  try {
    // Ensure WASM adapter is initialized
    const adapter = await ensureInitialized();

    // Handle different types of requests
    const url = new URL(request.url);
    const pathname = url.pathname;
    const method = request.method;

    // API routes - delegate to WASM modules
    if (pathname.startsWith('/api/')) {
      const edgeHandler = adapter.createEdgeHandler('katalyst-app');
      return await edgeHandler(request, { startTime });
    }

    // Static app routes - render React SSR
    if (pathname.startsWith('/app') || pathname === '/') {
      return await handleAppRequest(adapter, request, { startTime });
    }

    // Health check endpoint
    if (pathname === '/health') {
      return NextResponse.json({
        status: 'healthy',
        timestamp: new Date().toISOString(),
        modules: edgeConfig.wasmModules,
        duration: Date.now() - startTime
      });
    }

    // Default 404 response
    return new NextResponse('Not Found', { status: 404 });

  } catch (error) {
    console.error('Edge function error:', error);

    return NextResponse.json(
      { 
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    );
  }
}

async function handleAppRequest(
  adapter: VercelWasmAdapter, 
  request: NextRequest,
  context: { startTime: number }
): Promise<NextResponse> {
  const url = new URL(request.url);
  const searchParams = Object.fromEntries(url.searchParams.entries());

  // Extract props from URL parameters or headers
  const props = {
    pathname: url.pathname,
    searchParams,
    userAgent: request.headers.get('user-agent') || '',
    timestamp: new Date().toISOString(),
    ...context
  };

  try {
    // Render the React app using WASM
    const html = await adapter.renderReactApp('katalyst-app', props);

    // Create response with proper headers
    const response = new NextResponse(html, {
      status: 200,
      headers: {
        'Content-Type': 'text/html',
        'Cache-Control': 'public, max-age=300, stale-while-revalidate=600',
        'X-Katalyst-App': 'edge-rendered',
        'X-Katalyst-Duration': `${Date.now() - context.startTime}ms`,
      }
    });

    return response;

  } catch (error) {
    console.error('App rendering error:', error);

