      "regions": ["iad1", "sfo1", "lhr1"]
    },
    "vercel-functions/katalyst-admin.ts": {
      "runtime": "edge", 
      "memory": 256,
      "maxDuration": 15,
      "regions": ["iad1"]
    }
  },
  "routes": [
    {
      "src": "/admin(.*)",
      "dest": "/vercel-functions/katalyst-admin.ts"
    },
    {
      "src": "/api/admin(.*)",
      "dest": "/vercel-functions/katalyst-admin.ts"
    },
    {
      "src": "/metrics",
      "dest": "/vercel-functions/katalyst-admin.ts"
    },
    {
      "src": "/modules(.*)",
      "dest": "/vercel-functions/katalyst-admin.ts"
    },
    {
      "src": "/api(.*)",
      "dest": "/vercel-functions/katalyst-app.ts"
    },
    {
      "src": "/app(.*)",
      "dest": "/vercel-functions/katalyst-app.ts"
    },
    {
      "src": "/health",
      "dest": "/vercel-functions/katalyst-app.ts"
    },
    {
      "src": "/(.*)",
      "dest": "/vercel-functions/katalyst-app.ts"
    }
  ],
  "headers": [
    {
      "source": "/api/(.*)",
      "headers": [
        {
          "key": "Access-Control-Allow-Origin",
          "value": "*"
        },
        {
          "key": "Access-Control-Allow-Methods", 
          "value": "GET, POST, PUT, DELETE, OPTIONS"
        },
        {
          "key": "Access-Control-Allow-Headers",
          "value": "Content-Type, Authorization, X-Requested-With"
        },
        {
          "key": "X-Katalyst-Runtime",
          "value": "multi-wasm"
        },
        {
          "key": "X-Katalyst-Version",
          "value": "1.0.0"
        }
      ]
    },
    {
      "source": "/admin/(.*)",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "no-cache, no-store, must-revalidate"
        },
        {
          "key": "X-Frame-Options",
          "value": "DENY"
        },
        {
          "key": "X-Content-Type-Options",
          "value": "nosniff"
        },
        {
          "key": "Referrer-Policy",
          "value": "strict-origin-when-cross-origin"
        }
      ]
    }
  ],
  "env": {
    "NODE_ENV": "production",
    "KATALYST_RUNTIME": "vercel-edge",
    "WASM_OPTIMIZATION": "balanced"
  },
  "build": {
    "env": {
      "NODE_ENV": "production",
      "RUST_TARGET": "wasm32-unknown-unknown",
      "DENO_RUNTIME": "true"
    }
  },
  "rewrites": [
    {
      "source": "/wasm/(.*)",
      "destination": "/static/wasm/$1"
    }
  ],
  "cleanUrls": true,
  "trailingSlash": false,
  "github": {
    "silent": true
  }
}

