import typescript from '@rollup/plugin-typescript';
import { readFileSync } from 'fs';

const pkg = JSON.parse(readFileSync('./package.json', 'utf8'));

export default [
  // Main library bundle
  {
    input: 'packages/runtime-utils/index.ts',
    output: [
      {
        file: pkg.main,
        format: 'cjs',
        sourcemap: true
      },
      {
        file: pkg.module,
        format: 'es',
        sourcemap: true
      }
    ],
    plugins: [
      typescript({
        declaration: true,
        declarationDir: './dist',
        rootDir: './packages'
      })
    ],
    external: [
      '@vercel/edge',
      'uuid',
      'wasm-bindgen'
    ]
  },

  // WASM utilities bundle
  {
    input: 'packages/runtime-utils/wasm-compat.ts',
    output: {
      file: './dist/wasm/index.js',
      format: 'es',
      sourcemap: true
    },
    plugins: [
      typescript({
        declaration: true,
        declarationDir: './dist/wasm',
        rootDir: './packages/runtime-utils'
      })
    ]
  },

  // Vercel adapter bundle
  {
    input: 'packages/runtime-utils/vercel-adapter.ts',
    output: {
      file: './dist/vercel/index.js',
      format: 'es',
      sourcemap: true
    },
    plugins: [
      typescript({
        declaration: true,
        declarationDir: './dist/vercel',
        rootDir: './packages/runtime-utils'
      })
    ],
    external: ['@vercel/edge']
  },

  // Edge runtime bundle
  {
    input: 'packages/runtime-utils/edge-runtime.ts',
    output: {
      file: './dist/edge/index.js',
      format: 'es',
      sourcemap: true
    },
    plugins: [
      typescript({
        declaration: true,
        declarationDir: './dist/edge',
        rootDir: './packages/runtime-utils'
      })
    ]
  }
];

