import { create } from 'zustand';
import { subscribeWithSelector } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';

// Katalyst Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { ThreadTask } from '@katalyst/multithreading';

// Medusa Types
import { Product, Cart, Customer, Order, Region } from '@medusajs/types';

// Storefront Types
interface StorefrontConfig {
  multithreading: {
    enabled: boolean;
    preferredPool: 'rayon' | 'tokio' | 'crossbeam';
  };
  ai: {
    enabled: boolean;
    provider: 'cloudflare' | 'openai';
  };
  payments: {
    providers: string[];
  };
}

interface ProductFilters {
  category?: string;
  priceRange?: [number, number];
  brand?: string;
  inStock?: boolean;
  rating?: number;
  tags?: string[];
}

interface SearchState {
  query: string;
  results: Product[];
  loading: boolean;
  filters: ProductFilters;
  facets: Record<string, any>;
  totalResults: number;
}

interface CartState {
  cart: Cart | null;
  loading: boolean;
  error: string | null;
}

interface UserState {
  customer: Customer | null;
  isAuthenticated: boolean;
  orders: Order[];
  loading: boolean;
}

interface StorefrontState {
  // Configuration
  config: StorefrontConfig | null;
  isInitialized: boolean;

  // Product Catalog
  products: Product[];
  featuredProducts: Product[];
  categories: any[];
  loading: {
    products: boolean;
    categories: boolean;
    search: boolean;
    cart: boolean;
  };

  // Search
  search: SearchState;

  // Shopping Cart
  cart: CartState;

  // User State
  user: UserState;

  // Performance Metrics
  metrics: {
    searchTime: number;
    loadTime: number;
    threadUtilization: number;
  };

  // Actions
  initializeStore: (config: StorefrontConfig) => void;

  // Product Actions
  loadProducts: (filters?: ProductFilters) => Promise<void>;
  loadFeaturedProducts: () => Promise<void>;
  loadProductById: (id: string) => Promise<Product | null>;

  // Search Actions
  searchProducts: (query: string, filters?: ProductFilters) => Promise<void>;
  updateSearchFilters: (filters: Partial<ProductFilters>) => void;
  clearSearch: () => void;

  // Cart Actions
  addToCart: (productId: string, quantity: number, variantId?: string) => Promise<void>;
  updateCartItem: (itemId: string, quantity: number) => Promise<void>;
  removeFromCart: (itemId: string) => Promise<void>;
  loadCart: () => Promise<void>;

  // User Actions
  login: (email: string, password: string) => Promise<void>;
  register: (userData: any) => Promise<void>;
  logout: () => void;
  loadUserOrders: () => Promise<void>;
}

export const useStorefrontStore = create<StorefrontState>()(
  subscribeWithSelector(
    immer((set, get) => ({
      // Initial State
      config: null,
      isInitialized: false,
      products: [],
      featuredProducts: [],
      categories: [],
      loading: {
        products: false,
        categories: false,
        search: false,
        cart: false,
      },
      search: {
        query: '',
        results: [],
        loading: false,
        filters: {},
        facets: {},
        totalResults: 0,
      },
      cart: {
        cart: null,
        loading: false,
        error: null,
      },
      user: {
        customer: null,
        isAuthenticated: false,
        orders: [],
        loading: false,
      },
      metrics: {
        searchTime: 0,
        loadTime: 0,
        threadUtilization: 0,
      },

      // Actions
      initializeStore: (config: StorefrontConfig) => {
        set((state) => {
          state.config = config;
          state.isInitialized = true;
        });
      },

      // Product Actions with Multithreading
      loadProducts: async (filters?: ProductFilters) => {
        const { config } = get();
        if (!config?.multithreading.enabled) {
          return loadProductsSync(set, filters);
        }

        set((state) => {
          state.loading.products = true;
        });

        const startTime = performance.now();

        try {
          // Use multithreading for product loading
          const { executeTask } = useMultithreadingStore.getState();

          const result = await executeTask({
            id: `load-products-${Date.now()}`,
            operation: 'load_products',
            data: { filters },
            priority: 'high',
            status: 'pending'
          });

          const products = result.result?.products || [];

          set((state) => {
            state.products = products;
            state.loading.products = false;
            state.metrics.loadTime = performance.now() - startTime;
          });
        } catch (error) {
          console.error('Failed to load products:', error);
          set((state) => {
            state.loading.products = false;
          });
        }
      },

      searchProducts: async (query: string, filters?: ProductFilters) => {
        const { config } = get();

        set((state) => {
          state.search.query = query;
          state.search.loading = true;
          state.search.filters = { ...state.search.filters, ...filters };
        });

        const startTime = performance.now();

        try {
          if (config?.multithreading.enabled) {
            // Multithreaded search with advanced filtering
            const { executeTask } = useMultithreadingStore.getState();

            const result = await executeTask({
              id: `search-products-${Date.now()}`,
              operation: 'search_products',
              data: { 
                query, 
                filters: get().search.filters,
                includeFacets: true 
              },
              priority: 'high',
              status: 'pending'
            });

            set((state) => {
              state.search.results = result.result?.products || [];
              state.search.facets = result.result?.facets || {};
              state.search.totalResults = result.result?.total || 0;
              state.search.loading = false;
              state.metrics.searchTime = performance.now() - startTime;
            });
          } else {
            // Fallback to synchronous search
            await searchProductsSync(set, query, filters);
          }
        } catch (error) {
          console.error('Search failed:', error);
          set((state) => {
            state.search.loading = false;
            state.search.results = [];
          });
        }
      },

      updateSearchFilters: (filters: Partial<ProductFilters>) => {
        set((state) => {
          state.search.filters = { ...state.search.filters, ...filters };
        });

        // Auto-search with new filters if there's a query
        const { query } = get().search;
        if (query.trim()) {
          get().searchProducts(query, get().search.filters);
        }
      },

      clearSearch: () => {
        set((state) => {
          state.search.query = '';
          state.search.results = [];
          state.search.filters = {};
          state.search.facets = {};
          state.search.totalResults = 0;
        });
      },

      loadFeaturedProducts: async () => {
        const { config } = get();

        if (config?.multithreading.enabled) {
          const { executeTask } = useMultithreadingStore.getState();

          const result = await executeTask({
            id: `featured-products-${Date.now()}`,
            operation: 'load_featured_products',
            data: { limit: 12 },
            priority: 'normal',
            status: 'pending'
          });

          set((state) => {
            state.featuredProducts = result.result?.products || [];
          });
        }
      },

      loadProductById: async (id: string) => {
        const { config } = get();

        if (config?.multithreading.enabled) {
          const { executeTask } = useMultithreadingStore.getState();

          const result = await executeTask({
            id: `product-${id}`,
            operation: 'load_product_by_id',
            data: { id },
            priority: 'high',
            status: 'pending'
          });

          return result.result?.product || null;
        }

        return null;
      },

      // Cart Actions
      addToCart: async (productId: string, quantity: number, variantId?: string) => {
        set((state) => {
          state.cart.loading = true;
          state.cart.error = null;
        });

        try {
          const { config } = get();

          if (config?.multithreading.enabled) {
            const { executeTask } = useMultithreadingStore.getState();

            const result = await executeTask({
              id: `add-to-cart-${Date.now()}`,
              operation: 'add_to_cart',
              data: { productId, quantity, variantId },
              priority: 'high',
              status: 'pending'
            });

            set((state) => {
              state.cart.cart = result.result?.cart || null;
              state.cart.loading = false;
            });
          }
        } catch (error) {
          set((state) => {
            state.cart.error = 'Failed to add item to cart';
            state.cart.loading = false;
          });
        }
      },

      updateCartItem: async (itemId: string, quantity: number) => {
        // Similar multithreaded implementation
      },

      removeFromCart: async (itemId: string) => {
        // Similar multithreaded implementation
      },

      loadCart: async () => {
        // Similar multithreaded implementation
      },

      // User Actions
      login: async (email: string, password: string) => {
        set((state) => {
          state.user.loading = true;
        });

        try {
          // Implement login logic
          set((state) => {
            state.user.isAuthenticated = true;
            state.user.loading = false;
          });
        } catch (error) {
          set((state) => {
            state.user.loading = false;
          });
        }
      },

      register: async (userData: any) => {
        // Implement registration logic
      },

      logout: () => {
        set((state) => {
          state.user.customer = null;
