import { useEffect, useState } from 'react';

// Katalyst Imports
import { useMultithreading, useAdvancedMultithreading } from '@katalyst/hooks/use-multithreading';
import { useAI } from '@katalyst/hooks/use-ai';
import { usePWA } from '@katalyst/hooks/use-pwa';

// Design System
import { HeroParallax } from '@katalyst/design-system/ui/hero-parallax';
import { BentoGrid, BentoGridItem } from '@katalyst/design-system/ui/bento-grid';
import { InfiniteMovingCards } from '@katalyst/design-system/ui/infinite-moving-cards';
import { StickyScrollReveal } from '@katalyst/design-system/ui/sticky-scroll-reveal';
import { FloatingDock } from '@katalyst/design-system/ui/floating-dock';
import { BackgroundBeams } from '@katalyst/design-system/ui/background-beams';
import { SpotlightNew } from '@katalyst/design-system/ui/spotlight-new';

// Components
import { ProductGrid } from '../components/ProductGrid';
import { PerformanceShowcase } from '../components/PerformanceShowcase';
import { AIRecommendations } from '../components/AIRecommendations';
import { LiveMetrics } from '../components/LiveMetrics';

// Store
import { useStorefrontStore } from '../store/storefront-store';

// Data
import { featuredProducts, testimonials, showcaseContent } from '../data/homepage-data';

export function HomePage() {
  const [isLoaded, setIsLoaded] = useState(false);

  // Katalyst Hooks
  const { executeTask, metrics, threadPools } = useMultithreading();
  const { createThreadPool } = useAdvancedMultithreading();
  const { generateRecommendations, isAIEnabled } = useAI();
  const { isInstalled, promptInstall } = usePWA();

  // Store
  const { 
    featuredProducts: products, 
    loadFeaturedProducts, 
    searchProducts,
    metrics: storeMetrics 
  } = useStorefrontStore();

  // Initialize page with multithreaded operations
  useEffect(() => {
    const initializePage = async () => {
      try {
        // Create dedicated thread pool for homepage
        const homepagePool = await createThreadPool({
          type: 'rayon',
          workerCount: 2,
          priority: 'high'
        });

        // Execute multiple operations in parallel
        const tasks = await Promise.allSettled([
          loadFeaturedProducts(),

          // Pre-load popular search results
          searchProducts('popular'),
          searchProducts('trending'),

          // Initialize AI recommendations
          isAIEnabled && generateRecommendations({
            userId: 'anonymous',
            context: 'homepage',
            limit: 6
          }),

          // Background performance optimization
          executeTask({
            id: 'homepage-optimization',
            operation: 'optimize_homepage_cache',
            data: { preloadImages: true, prefetchRoutes: true },
            priority: 'low',
            status: 'pending'
          })
        ]);

        console.log('Homepage initialized with parallel operations:', tasks);
        setIsLoaded(true);
      } catch (error) {
        console.error('Failed to initialize homepage:', error);
        setIsLoaded(true); // Show page anyway
      }
    };

    initializePage();
  }, [createThreadPool, loadFeaturedProducts, searchProducts, generateRecommendations, isAIEnabled, executeTask]);

  const heroProducts = featuredProducts.slice(0, 5).map(product => ({
    title: product.title,
    link: `/products/${product.id}`,
    thumbnail: product.thumbnail || '/placeholder-product.jpg',
  }));

  const bentoItems = [
    {
      title: "Multithreaded Product Search",
      description: "Experience lightning-fast search powered by Rust multithreading",
      header: <PerformanceShowcase feature="search" />,
      className: "md:col-span-2",
      icon: "🚀"
    },
    {
      title: "AI-Powered Recommendations", 
      description: "Get personalized product suggestions using Cloudflare AI",
      header: <AIRecommendations />,
      className: "md:col-span-1",
      icon: "🤖"
    },
    {
      title: "Real-Time Performance Metrics",
      description: "Monitor thread utilization and response times",
      header: <LiveMetrics />,
      className: "md:col-span-1", 
      icon: "📊"
    },
    {
      title: "Progressive Web App",
      description: "Install for offline shopping and native experience",
      header: (
        <div className="flex items-center justify-center h-full">
          <button 
            onClick={promptInstall}
            className="px-4 py-2 bg-primary text-primary-foreground rounded-lg hover:scale-105 transition-transform"
            disabled={isInstalled}
          >
            {isInstalled ? 'Already Installed' : 'Install App'}
          </button>
        </div>
      ),
      className: "md:col-span-2",
      icon: "📱"
    }
  ];

  if (!isLoaded) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
          <p className="mt-4 text-muted-foreground">
            Initializing multithreaded storefront...
          </p>
          <p className="text-sm text-muted-foreground">
            Active threads: {threadPools.length} | Tasks: {metrics?.totalTasks || 0}
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* Hero Section with Parallax */}
      <HeroParallax products={heroProducts}>
        <div className="text-center space-y-4 mb-8">
          <h1 className="text-4xl md:text-6xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-primary to-secondary">
            Katalyst E-Commerce
          </h1>
          <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
            Experience the future of online shopping with multithreaded performance, 
            AI-powered recommendations, and seamless user experience.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
            <div className="text-sm text-muted-foreground">
              Powered by {threadPools.length} thread pools
            </div>
            <div className="text-sm text-muted-foreground">
              Average response: {storeMetrics.searchTime.toFixed(2)}ms
            </div>
            <div className="text-sm text-muted-foreground">
              Thread utilization: {storeMetrics.threadUtilization.toFixed(1)}%
            </div>
          </div>
        </div>
      </HeroParallax>

      {/* Features Showcase */}
      <section className="py-20 relative">
        <SpotlightNew className="absolute inset-0" />
        <div className="container mx-auto px-4 relative z-10">
          <h2 className="text-3xl font-bold text-center mb-12">
            Katalyst Framework Features
          </h2>
          <BentoGrid className="max-w-4xl mx-auto">
            {bentoItems.map((item, i) => (
              <BentoGridItem
                key={i}
                title={item.title}
                description={item.description}
                header={item.header}
                className={item.className}
                icon={
                  <span className="text-2xl" role="img" aria-label={item.icon}>
                    {item.icon}
                  </span>
                }
              />
            ))}
          </BentoGrid>
        </div>
      </section>

      {/* Featured Products */}
      <section className="py-20">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold text-center mb-12">
            Featured Products
          </h2>
          <ProductGrid 
            products={products} 
            enableMultithreading={true}
            showPerformanceMetrics={true}
          />
        </div>
      </section>

      {/* Customer Testimonials */}
      <section className="py-20 relative overflow-hidden">
        <BackgroundBeams />
        <div className="container mx-auto px-4 relative z-10">
          <h2 className="text-3xl font-bold text-center mb-12 text-white">
            What Our Customers Say
          </h2>
          <InfiniteMovingCards
            items={testimonials}
            direction="right"
            speed="fast"
          />
        </div>
      </section>

      {/* Technology Showcase */}
      <section className="py-20">
        <div className="container mx-auto px-4">
          <StickyScrollReveal content={showcaseContent} />
        </div>
      </section>

      {/* Floating Action Dock */}
      <FloatingDock
        items={[
          {
            title: "Search",
            icon: "🔍",
            href: "/search",
          },
          {
            title: "Cart",
            icon: "🛒", 
            href: "/cart",
          },
          {
            title: "Account",
            icon: "👤",
            href: "/account",
          },
          {
            title: "Performance",
            icon: "📊",
            href: "#metrics",
          }
        ]}
        className="fixed bottom-6 left-1/2 transform -translate-x-1/2"
      />
    </div>
  );
}

