import { useEffect, useState, useCallback } from 'react';

// Katalyst Imports
import { useMultithreadingStore, useTaskQueue, useThreadPools } from '@katalyst/core/stores/multithreading-store';
import { useAdvancedMultithreading, useThreadMonitoring } from '@katalyst/hooks/use-multithreading';
import { ThreadPrimitive } from '@katalyst/core/native/thread-primitives';

// Components
import { Badge } from '@katalyst/design-system/ui/badge';
import { Button } from '@katalyst/design-system/ui/button';
import { Card } from '@katalyst/design-system/ui/card';

interface PerformanceShowcaseProps {
  feature: 'search' | 'checkout' | 'recommendations' | 'analytics';
}

interface PerformanceMetrics {
  executionTime: number;
  threadsUsed: number;
  throughput: number;
  memoryUsage: number;
  cpuUtilization: number;
}

export function PerformanceShowcase({ feature }: PerformanceShowcaseProps) {
  const [isRunning, setIsRunning] = useState(false);
  const [metrics, setMetrics] = useState<PerformanceMetrics | null>(null);
  const [demoResults, setDemoResults] = useState<any[]>([]);

  // Katalyst Multithreading Hooks
  const { executeTask, addTask } = useMultithreadingStore();
  const { createThreadPool, getThreadPoolStats } = useAdvancedMultithreading();
  const { startMonitoring, stopMonitoring, getMetrics } = useThreadMonitoring();

  const runPerformanceDemo = useCallback(async () => {
    if (isRunning) return;

    setIsRunning(true);
    setMetrics(null);
    setDemoResults([]);

    const startTime = performance.now();
    startMonitoring();

    try {
      let results: any[] = [];

      switch (feature) {
        case 'search':
          results = await runSearchDemo();
          break;
        case 'checkout':
          results = await runCheckoutDemo();
          break;
        case 'recommendations':
          results = await runRecommendationsDemo();
          break;
        case 'analytics':
          results = await runAnalyticsDemo();
          break;
      }

      const endTime = performance.now();
      const threadMetrics = getMetrics();

      setMetrics({
        executionTime: endTime - startTime,
        threadsUsed: threadMetrics.activeThreads,
        throughput: results.length / ((endTime - startTime) / 1000),
        memoryUsage: (performance as any).memory?.usedJSHeapSize || 0,
        cpuUtilization: threadMetrics.averageCpuUsage
      });

      setDemoResults(results);
    } catch (error) {
      console.error('Performance demo failed:', error);
    } finally {
      stopMonitoring();
      setIsRunning(false);
    }
  }, [feature, isRunning, startMonitoring, stopMonitoring, getMetrics]);

  const runSearchDemo = async (): Promise<any[]> => {
    // Create dedicated search thread pool
    const searchPool = await createThreadPool({
      type: 'rayon',
      workerCount: 4
    });

    // Simulate parallel search operations
    const searchQueries = [
      'electronics',
      'clothing',
      'books', 
      'sports',
      'home',
      'beauty'
    ];

    const searchTasks = searchQueries.map(query => 
      addTask({
        id: `search-${query}-${Date.now()}`,
        operation: 'product_search',
        data: { query, limit: 50, includeFilters: true },
        priority: 'high',
        threadId: `search-pool-${Math.floor(Math.random() * 4)}`
      })
    );

    // Execute all searches in parallel
    const results = await Promise.all(
      searchTasks.map(task => executeTask(task.id))
    );

    // Simulate aggregating results
    const aggregatedResults = results.flatMap(result => 
      result.result?.products || []
    );

    return aggregatedResults;
  };

  const runCheckoutDemo = async (): Promise<any[]> => {
    // Simulate parallel checkout operations
    const checkoutTasks = [
      'validate_inventory',
      'calculate_taxes',
      'process_payment',
      'update_inventory',
      'send_confirmation',
      'update_analytics'
    ];

    const tasks = checkoutTasks.map(operation => 
      addTask({
        id: `checkout-${operation}-${Date.now()}`,
        operation,
        data: { orderId: 'demo-order', amount: 99.99 },
        priority: 'high'
      })
    );

    const results = await Promise.all(
      tasks.map(task => executeTask(task.id))
    );

    return results.map(r => r.result);
  };

  const runRecommendationsDemo = async (): Promise<any[]> => {
    // Simulate AI recommendation processing
    const userProfiles = [
      { userId: 'user1', preferences: ['electronics', 'gaming'] },
      { userId: 'user2', preferences: ['fashion', 'beauty'] },
      { userId: 'user3', preferences: ['books', 'education'] },
      { userId: 'user4', preferences: ['sports', 'outdoor'] }
    ];

    const recommendationTasks = userProfiles.map(profile => 
      addTask({
        id: `recommendations-${profile.userId}-${Date.now()}`,
        operation: 'generate_recommendations',
        data: profile,
        priority: 'normal'
      })
    );

    const results = await Promise.all(
      recommendationTasks.map(task => executeTask(task.id))
    );

    return results.flatMap(r => r.result?.recommendations || []);
  };

  const runAnalyticsDemo = async (): Promise<any[]> => {
    // Simulate real-time analytics processing
    const analyticsOperations = [
      'user_behavior_analysis',
      'conversion_tracking', 
      'inventory_analytics',
      'performance_metrics',
      'fraud_detection',
      'recommendation_effectiveness'
    ];

    // Use SIMD operations for analytics
    const tasks = analyticsOperations.map(operation => 
      addTask({
        id: `analytics-${operation}-${Date.now()}`,
        operation: 'analytics_processing',
        data: { 
          operation,
          dataset: generateMockData(1000),
          useSimd: true 
        },
        priority: 'normal'
      })
    );

    const results = await Promise.all(
      tasks.map(task => executeTask(task.id))
    );

    return results.map(r => r.result);
  };

  const generateMockData = (count: number) => {
    return Array.from({ length: count }, (_, i) => ({
      id: i,
      timestamp: Date.now() + i * 1000,
      value: Math.random() * 100,
      category: ['A', 'B', 'C'][Math.floor(Math.random() * 3)]
    }));
  };

  const getFeatureDescription = () => {
    const descriptions = {
      search: 'Multithreaded product search with parallel filtering and faceting',
      checkout: 'Parallel checkout processing with inventory validation and payment',
      recommendations: 'AI-powered recommendation engine with concurrent user profiling',
      analytics: 'Real-time analytics with SIMD acceleration and parallel processing'
    };
    return descriptions[feature];
  };

  return (
    <Card className="h-full p-6">
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <h3 className="font-semibold capitalize">{feature} Performance</h3>
          <Badge variant={isRunning ? "secondary" : "default"}>
            {isRunning ? "Running..." : "Ready"}
          </Badge>
        </div>

        <p className="text-sm text-muted-foreground">
          {getFeatureDescription()}
        </p>

        <Button 
          onClick={runPerformanceDemo}
          disabled={isRunning}
          className="w-full"
        >
          {isRunning ? 'Running Demo...' : 'Run Performance Demo'}
        </Button>

        {metrics && (
          <div className="grid grid-cols-2 gap-4 mt-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">
                {metrics.executionTime.toFixed(0)}ms
              </div>
              <div className="text-sm text-muted-foreground">Execution Time</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">
                {metrics.threadsUsed}
              </div>
              <div className="text-sm text-muted-foreground">Threads Used</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-purple-600">
                {metrics.throughput.toFixed(1)}/s
              </div>
              <div className="text-sm text-muted-foreground">Throughput</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">
                {metrics.cpuUtilization.toFixed(1)}%
              </div>
              <div className="text-sm text-muted-foreground">CPU Usage</div>
            </div>
          </div>
        )}

        {demoResults.length > 0 && (
          <div className="mt-4">
            <div className="text-sm font-medium mb-2">
              Results: {demoResults.length} items processed
            </div>
            <div className="bg-muted rounded p-2 text-xs max-h-20 overflow-y-auto">
              {JSON.stringify(demoResults.slice(0, 3), null, 2)}
              {demoResults.length > 3 && '...'}
            </div>
          </div>
        )}

        <div className="flex flex-wrap gap-2 mt-4">
          <Badge variant="outline" className="text-xs">
            🦀 Rust NAPI-RS
          </Badge>
          <Badge variant="outline" className="text-xs">
            ⚡ Rayon Threading
          </Badge>
          <Badge variant="outline" className="text-xs">
            🔄 Tokio Async
          </Badge>
          <Badge variant="outline" className="text-xs">
            📊 SIMD Acceleration
          </Badge>
        </div>
      </div>
    </Card>
  );
}

