// Katalyst Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useAdvancedMultithreading } from '@katalyst/hooks/use-multithreading';
import { VirtualGrid } from '@katalyst/core/components/tanstack/virtual/VirtualGrid';

// Design System
import { Card } from '@katalyst/design-system/ui/card';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Button } from '@katalyst/design-system/ui/button';
import { Skeleton } from '@katalyst/design-system/ui/skeleton';

// Icons
import { ShoppingCart, Heart, Eye, Zap } from 'lucide-react';

// Types
interface Product {
  id: string;
  title: string;
  description: string;
  price: number;
  thumbnail: string;
  images: string[];
  categories: string[];
  rating: number;
  reviewCount: number;
  inStock: boolean;
  variants?: any[];
}

interface ProductGridProps {
  products: Product[];
  enableMultithreading?: boolean;
  showPerformanceMetrics?: boolean;
  onProductClick?: (product: Product) => void;
  onAddToCart?: (productId: string) => void;
  onToggleFavorite?: (productId: string) => void;
  gridCols?: number;
  enableVirtualization?: boolean;
}

interface RenderingMetrics {
  renderTime: number;
  threadsUsed: number;
  itemsRendered: number;
  virtualizedItems: number;
}

export function ProductGrid({
  products,
  enableMultithreading = true,
  showPerformanceMetrics = false,
  onProductClick,
  onAddToCart,
  onToggleFavorite,
  gridCols = 4,
  enableVirtualization = true
}: ProductGridProps) {
  const [isRendering, setIsRendering] = useState(false);
  const [renderingMetrics, setRenderingMetrics] = useState<RenderingMetrics | null>(null);
  const [favorites, setFavorites] = useState<Set<string>>(new Set());

  // Katalyst Multithreading
  const { executeTask } = useMultithreadingStore();
  const { createThreadPool } = useAdvancedMultithreading();

  // Memoized product processing with multithreading
  const processedProducts = useMemo(() => {
    if (!enableMultithreading) {
      return products;
    }

    // Process products in chunks using multithreading for heavy operations
    return products.map(product => ({
      ...product,
      // Add computed properties that might be expensive
      priceDisplay: new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'USD'
      }).format(product.price),
      averageRating: product.rating || 0,
      isOnSale: product.price < 50, // Example sale logic
      searchKeywords: generateSearchKeywords(product)
    }));
  }, [products, enableMultithreading]);

  // Multithreaded image preloading
  const preloadImages = useCallback(async (productChunk: Product[]) => {
    if (!enableMultithreading) return;

    try {
      await executeTask({
        id: `preload-images-${Date.now()}`,
        operation: 'preload_product_images',
        data: { 
          products: productChunk.map(p => ({ 
            id: p.id, 
            images: [p.thumbnail, ...p.images.slice(0, 2)] 
          }))
        },
        priority: 'low',
        status: 'pending'
      });
    } catch (error) {
      console.warn('Image preloading failed:', error);
    }
  }, [enableMultithreading, executeTask]);

  // Effect to handle multithreaded rendering
  useEffect(() => {
    if (!enableMultithreading || products.length === 0) return;

    const renderWithMultithreading = async () => {
      setIsRendering(true);
      const startTime = performance.now();

      try {
        // Split products into chunks for parallel processing
        const chunkSize = Math.ceil(products.length / 4);
        const chunks = [];
        for (let i = 0; i < products.length; i += chunkSize) {
          chunks.push(products.slice(i, i + chunkSize));
        }

        // Create thread pool for rendering operations
        const renderPool = await createThreadPool({
          type: 'rayon',
          workerCount: Math.min(4, chunks.length)
        });

        // Process chunks in parallel
        const chunkPromises = chunks.map((chunk, index) => 
          Promise.all([
            // Preload images for this chunk
            preloadImages(chunk),
            // Process product data
            executeTask({
              id: `process-chunk-${index}`,
              operation: 'process_product_chunk',
              data: { chunk, operations: ['pricing', 'availability', 'recommendations'] },
              priority: 'normal',
              status: 'pending'
            })
          ])
        );

        await Promise.all(chunkPromises);

        const endTime = performance.now();
        setRenderingMetrics({
          renderTime: endTime - startTime,
          threadsUsed: renderPool ? 4 : 1,
          itemsRendered: products.length,
          virtualizedItems: enableVirtualization ? Math.min(20, products.length) : products.length
        });
      } catch (error) {
        console.error('Multithreaded rendering failed:', error);
      } finally {
        setIsRendering(false);
      }
    };

    renderWithMultithreading();
  }, [products, enableMultithreading, createThreadPool, preloadImages, executeTask, enableVirtualization]);

  const handleAddToCart = useCallback(async (productId: string) => {
    if (enableMultithreading) {
      // Multithreaded cart operation
      try {
        await executeTask({
          id: `add-to-cart-${productId}`,
          operation: 'add_to_cart',
          data: { productId, quantity: 1 },
          priority: 'high',
          status: 'pending'
        });
      } catch (error) {
        console.error('Failed to add to cart:', error);
      }
    }
    onAddToCart?.(productId);
  }, [enableMultithreading, executeTask, onAddToCart]);

  const handleToggleFavorite = useCallback((productId: string) => {
    setFavorites(prev => {
      const newFavorites = new Set(prev);
      if (newFavorites.has(productId)) {
        newFavorites.delete(productId);
      } else {
        newFavorites.add(productId);
      }
      return newFavorites;
    });
    onToggleFavorite?.(productId);
  }, [onToggleFavorite]);

  // Product card component
  const ProductCard = ({ product }: { product: Product }) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3 }}
      whileHover={{ y: -4 }}
      className="h-full"
    >
      <Card className="h-full overflow-hidden group cursor-pointer hover:shadow-lg transition-all duration-300">
        <div className="relative">
          <img
            src={product.thumbnail || '/placeholder-product.jpg'}
            alt={product.title}
            className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
            onClick={() => onProductClick?.(product)}
          />

          {/* Overlay Actions */}
          <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center space-x-2">
            <Button size="sm" variant="secondary" onClick={() => onProductClick?.(product)}>
              <Eye className="w-4 h-4 mr-1" />
              View
            </Button>
            <Button size="sm" variant="secondary" onClick={() => handleAddToCart(product.id)}>
              <ShoppingCart className="w-4 h-4 mr-1" />
              Add
            </Button>
          </div>

          {/* Favorite Button */}
          <button
            onClick={() => handleToggleFavorite(product.id)}
            className="absolute top-2 right-2 p-2 rounded-full bg-white/80 hover:bg-white transition-colors"
          >
            <Heart 
              className={`w-4 h-4 ${favorites.has(product.id) ? 'fill-red-500 text-red-500' : 'text-gray-600'}`} 
            />
          </button>

          {/* Stock Badge */}
          {!product.inStock && (
            <Badge variant="destructive" className="absolute top-2 left-2">
              Out of Stock
            </Badge>
          )}

          {/* Multithreading Badge */}
          {enableMultithreading && showPerformanceMetrics && (
            <Badge variant="outline" className="absolute bottom-2 left-2 text-xs">
              <Zap className="w-3 h-3 mr-1" />
              MT
            </Badge>
          )}
        </div>

        <div className="p-4">
          <h3 className="font-semibold text-sm mb-2 line-clamp-2">{product.title}</h3>

          <div className="flex items-center justify-between mb-2">
            <span className="text-lg font-bold text-primary">
              {(product as any).priceDisplay || `$${product.price}`}
            </span>
            <div className="flex items-center space-x-1">
              <span className="text-yellow-500">★</span>
              <span className="text-sm text-muted-foreground">
                {product.rating?.toFixed(1) || 'N/A'}
              </span>
              <span className="text-xs text-muted-foreground">
                ({product.reviewCount || 0})
              </span>
            </div>
          </div>

          <p className="text-sm text-muted-foreground line-clamp-2 mb-3">
            {product.description}
          </p>

          <div className="flex flex-wrap gap-1 mb-3">
            {product.categories.slice(0, 2).map(category => (
              <Badge key={category} variant="outline" className="text-xs">
                {category}
              </Badge>
            ))}
          </div>

          <Button 
            className="w-full" 
            size="sm"
            disabled={!product.inStock}
            onClick={() => handleAddToCart(product.id)}
          >
            {product.inStock ? 'Add to Cart' : 'Out of Stock'}
          </Button>
        </div>
      </Card>
    </motion.div>
  );

  if (isRendering && products.length > 10) {
    return (
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="text-sm text-muted-foreground">
            Rendering {products.length} products with multithreading...
          </div>
          <Badge variant="secondary">
            <Zap className="w-3 h-3 mr-1" />
            Processing
          </Badge>
        </div>
        <div className={`grid grid-cols-1 md:grid-cols-2 lg:grid-cols-${gridCols} gap-6`}>
          {Array.from({ length: Math.min(12, products.length) }).map((_, i) => (
            <Card key={i} className="h-80">
              <Skeleton className="h-48 w-full" />
              <div className="p-4 space-y-2">
                <Skeleton className="h-4 w-3/4" />
                <Skeleton className="h-3 w-1/2" />
                <Skeleton className="h-6 w-full" />
              </div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Performance Metrics */}
      {showPerformanceMetrics && renderingMetrics && (
        <div className="bg-muted rounded-lg p-4">
          <div className="flex items-center justify-between mb-2">
            <h4 className="font-semibold text-sm">Rendering Performance</h4>
            <Badge variant="outline">
              <Zap className="w-3 h-3 mr-1" />
              Multithreaded
            </Badge>
          </div>
          <div className="grid grid-cols-4 gap-4 text-center">
            <div>
              <div className="text-lg font-bold text-green-600">
                {renderingMetrics.renderTime.toFixed(0)}ms
              </div>
              <div className="text-xs text-muted-foreground">Render Time</div>
            </div>
            <div>
              <div className="text-lg font-bold text-blue-600">
                {renderingMetrics.threadsUsed}
              </div>
              <div className="text-xs text-muted-foreground">Threads</div>
            </div>
            <div>
              <div className="text-lg font-bold text-purple-600">
                {renderingMetrics.itemsRendered}
              </div>
              <div className="text-xs text-muted-foreground">Items</div>
            </div>
            <div>
              <div className="text-lg font-bold text-orange-600">
                {(renderingMetrics.itemsRendered / (renderingMetrics.renderTime / 1000)).toFixed(1)}
              </div>
              <div className="text-xs text-muted-foreground">Items/sec</div>
            </div>
          </div>
        </div>
      )}

      {/* Product Grid */}
      {enableVirtualization && products.length > 50 ? (
        <VirtualGrid
          items={processedProducts}
          itemHeight={320}
          columns={gridCols}
          renderItem={({ item }) => <ProductCard product={item} />}
          className="h-[600px]"
        />
      ) : (
        <div className={`grid grid-cols-1 md:grid-cols-2 lg:grid-cols-${gridCols} gap-6`}>
          <AnimatePresence>
            {processedProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </AnimatePresence>
        </div>
      )}

      {processedProducts.length === 0 && (
        <div className="text-center py-12">
          <div className="text-muted-foreground">No products found</div>
        </div>
      )}
    </div>
  );
}

// Helper function to generate search keywords
function generateSearchKeywords(product: Product): string[] {
  const keywords = [
    ...product.title.split(' '),
    ...product.categories,
    ...product.description.split(' ').slice(0, 10)
  ];

  return [...new Set(keywords.map(k => k.toLowerCase().replace(/[^a-z0-9]/g, '')).filter(Boolean))];
}

