import { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';

// Katalyst Imports
import { useAI } from '@katalyst/hooks/use-ai';
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';

// Components
import { Card } from '@katalyst/design-system/ui/card';
import { Button } from '@katalyst/design-system/ui/button';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Skeleton } from '@katalyst/design-system/ui/skeleton';

// Icons
import { Brain, Sparkles, TrendingUp, User } from 'lucide-react';

interface Recommendation {
  id: string;
  title: string;
  description: string;
  confidence: number;
  category: 'trending' | 'personalized' | 'similar' | 'new';
  price?: number;
  image?: string;
  reason: string;
}

interface AIRecommendationsProps {
  userId?: string;
  context?: 'homepage' | 'product' | 'cart' | 'checkout';
  limit?: number;
}

export function AIRecommendations({ 
  userId = 'anonymous',
  context = 'homepage',
  limit = 6 
}: AIRecommendationsProps) {
  const [recommendations, setRecommendations] = useState<Recommendation[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [generationTime, setGenerationTime] = useState<number>(0);

  // Katalyst Hooks
  const { generateRecommendations, isAIEnabled, getModelInfo } = useAI();
  const { executeTask } = useMultithreadingStore();

  const generateAIRecommendations = useCallback(async () => {
    if (!isAIEnabled) {
      setError('AI recommendations are not available');
      return;
    }

    setIsLoading(true);
    setError(null);
    const startTime = performance.now();

    try {
      // Use multithreaded AI processing for recommendations
      const result = await executeTask({
        id: `ai-recommendations-${Date.now()}`,
        operation: 'generate_ai_recommendations',
        data: {
          userId,
          context,
          limit,
          includeReasons: true,
          useCollaborativeFiltering: true,
          useContentBased: true
        },
        priority: 'normal',
        status: 'pending'
      });

      if (result.result?.recommendations) {
        setRecommendations(result.result.recommendations);
      } else {
        // Fallback to direct AI hook
        const aiRecommendations = await generateRecommendations({
          userId,
          context,
          limit,
          preferences: getUserPreferences(),
          browsingHistory: getBrowsingHistory(),
          currentSession: getCurrentSessionData()
        });

        // Transform AI response to our format
        const formattedRecommendations = aiRecommendations.map((rec: any, index: number) => ({
          id: rec.id || `ai-rec-${index}`,
          title: rec.title || `AI Recommended Item ${index + 1}`,
          description: rec.description || 'Personalized recommendation based on your preferences',
          confidence: rec.confidence || Math.random() * 0.4 + 0.6, // 0.6-1.0 range
          category: rec.category || getRecommendationCategory(index),
          price: rec.price || Math.floor(Math.random() * 200) + 20,
          image: rec.image || `/placeholder-product-${(index % 4) + 1}.jpg`,
          reason: rec.reason || generateReasonText(rec.category)
        }));

        setRecommendations(formattedRecommendations);
      }

      const endTime = performance.now();
      setGenerationTime(endTime - startTime);
    } catch (error) {
      console.error('Failed to generate AI recommendations:', error);
      setError('Failed to generate recommendations');

      // Fallback to mock recommendations
      setRecommendations(generateMockRecommendations());
    } finally {
      setIsLoading(false);
    }
  }, [userId, context, limit, isAIEnabled, generateRecommendations, executeTask]);

  useEffect(() => {
    generateAIRecommendations();
  }, [generateAIRecommendations]);

  const getUserPreferences = () => {
    // Get user preferences from localStorage or context
    const saved = localStorage.getItem(`user-preferences-${userId}`);
    return saved ? JSON.parse(saved) : {
      categories: ['electronics', 'books', 'clothing'],
      priceRange: [20, 200],
      brands: []
    };
  };

  const getBrowsingHistory = () => {
    // Get browsing history for recommendations
    const saved = localStorage.getItem(`browsing-history-${userId}`);
    return saved ? JSON.parse(saved) : [];
  };

  const getCurrentSessionData = () => {
    // Get current session data for contextual recommendations
    return {
      currentPage: context,
      timeOnSite: Date.now() - (parseInt(sessionStorage.getItem('session-start') || '0')),
      pagesVisited: parseInt(sessionStorage.getItem('pages-visited') || '0'),
      searchQueries: JSON.parse(sessionStorage.getItem('search-queries') || '[]')
    };
  };

  const getRecommendationCategory = (index: number): Recommendation['category'] => {
    const categories: Recommendation['category'][] = ['trending', 'personalized', 'similar', 'new'];
    return categories[index % categories.length];
  };

  const generateReasonText = (category: string) => {
    const reasons = {
      trending: 'Popular with other users right now',
      personalized: 'Based on your preferences and history',
      similar: 'Similar to items you\'ve viewed',
      new: 'New arrivals you might like'
    };
    return reasons[category as keyof typeof reasons] || 'Recommended for you';
  };

  const generateMockRecommendations = (): Recommendation[] => {
    return Array.from({ length: limit }, (_, i) => ({
      id: `mock-rec-${i}`,
      title: `AI Recommended Product ${i + 1}`,
      description: `This is a personalized recommendation based on AI analysis of your preferences and behavior patterns.`,
      confidence: Math.random() * 0.4 + 0.6,
      category: getRecommendationCategory(i),
      price: Math.floor(Math.random() * 200) + 20,
      image: `/placeholder-product-${(i % 4) + 1}.jpg`,
      reason: generateReasonText(getRecommendationCategory(i))
    }));
  };

  const getCategoryIcon = (category: Recommendation['category']) => {
    switch (category) {
      case 'trending':
        return <TrendingUp className="w-4 h-4" />;
      case 'personalized':
        return <User className="w-4 h-4" />;
      case 'similar':
        return <Sparkles className="w-4 h-4" />;
      case 'new':
        return <Brain className="w-4 h-4" />;
      default:
        return <Brain className="w-4 h-4" />;
    }
  };

  const getCategoryColor = (category: Recommendation['category']) => {
    switch (category) {
      case 'trending':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'personalized':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'similar':
        return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'new':
        return 'bg-green-100 text-green-800 border-green-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const handleRecommendationClick = (recommendation: Recommendation) => {
    // Track recommendation click
    console.log('Recommendation clicked:', recommendation);

    // Add to session tracking
    const clicks = JSON.parse(sessionStorage.getItem('recommendation-clicks') || '[]');
    clicks.push({
      id: recommendation.id,
      category: recommendation.category,
      timestamp: Date.now()
    });
    sessionStorage.setItem('recommendation-clicks', JSON.stringify(clicks));
  };

  if (error && recommendations.length === 0) {
    return (
      <Card className="p-6">
        <div className="flex items-center justify-center text-muted-foreground">
          <Brain className="w-8 h-8 mr-2" />
          <div>
            <p className="font-medium">AI Recommendations Unavailable</p>
            <p className="text-sm">{error}</p>
          </div>
        </div>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Brain className="w-5 h-5 text-primary" />
          <h3 className="text-lg font-semibold">AI Recommendations</h3>
          {isAIEnabled && (
            <Badge variant="outline" className="text-xs">
              {getModelInfo()?.name || 'Cloudflare AI'}
            </Badge>
          )}
        </div>

        <div className="flex items-center space-x-2 text-sm text-muted-foreground">
          {generationTime > 0 && (
            <span>{generationTime.toFixed(0)}ms</span>
          )}
          <Button
            variant="ghost"
            size="sm"
            onClick={generateAIRecommendations}
            disabled={isLoading}
          >
            Refresh
          </Button>
        </div>
      </div>

      {/* Recommendations Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {isLoading ? (
          // Loading skeletons
          Array.from({ length: limit }).map((_, i) => (
            <Card key={`skeleton-${i}`} className="p-4">
              <Skeleton className="h-32 w-full mb-3" />
              <Skeleton className="h-4 w-3/4 mb-2" />
              <Skeleton className="h-3 w-1/2 mb-2" />
              <Skeleton className="h-6 w-full" />
            </Card>
          ))
        ) : (
          recommendations.map((rec, index) => (
            <motion.div
              key={rec.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: index * 0.1 }}
            >
              <Card className="overflow-hidden hover:shadow-lg transition-all duration-300 cursor-pointer group">
                <div className="relative">
                  <img
                    src={rec.image || '/placeholder-product.jpg'}
                    alt={rec.title}
                    className="w-full h-32 object-cover group-hover:scale-105 transition-transform duration-300"
                    onClick={() => handleRecommendationClick(rec)}
                  />

                  <div className="absolute top-2 left-2">
                    <Badge 
                      variant="secondary" 
                      className={`text-xs ${getCategoryColor(rec.category)}`}
                    >
                      {getCategoryIcon(rec.category)}
                      <span className="ml-1 capitalize">{rec.category}</span>
                    </Badge>
                  </div>

                  <div className="absolute top-2 right-2">
                    <Badge 
                      variant="outline" 
                      className="text-xs bg-white/90"
                    >
                      {Math.round(rec.confidence * 100)}% match
                    </Badge>
                  </div>
                </div>

                <div className="p-4">
                  <h4 className="font-medium text-sm mb-1 line-clamp-1">
                    {rec.title}
                  </h4>

                  <p className="text-xs text-muted-foreground mb-2 line-clamp-2">
                    {rec.description}
                  </p>

                  <div className="flex items-center justify-between mb-3">
                    {rec.price && (
                      <span className="font-semibold text-primary">
                        ${rec.price}
                      </span>
                    )}
                    <div className="flex items-center text-xs text-muted-foreground">
                      <Sparkles className="w-3 h-3 mr-1" />
                      AI
                    </div>
                  </div>

                  <div className="text-xs text-muted-foreground mb-3 italic">
                    "{rec.reason}"
                  </div>

                  <Button 
                    size="sm" 
                    className="w-full"
                    onClick={() => handleRecommendationClick(rec)}
                  >
                    View Details
                  </Button>
                </div>
              </Card>
            </motion.div>
          ))
        )}
      </div>

      {/* Performance Info */}
      {!isLoading && recommendations.length > 0 && (
        <div className="flex items-center justify-center space-x-4 text-xs text-muted-foreground">
          <span>Generated by AI in {generationTime.toFixed(0)}ms</span>
          <span>•</span>
          <span>{recommendations.length} recommendations</span>
          <span>•</span>
          <span>Avg confidence: {Math.round(recommendations.reduce((acc, r) => acc + r.confidence, 0) / recommendations.length * 100)}%</span>
        </div>
      )}
    </div>
  );
}

