import { useState, useEffect, useCallback, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useAdvancedMultithreading } from '@katalyst/hooks/use-multithreading';
import { useAI } from '@katalyst/hooks/use-ai';

// Design System
import { Card } from '@katalyst/design-system/ui/card';
import { Button } from '@katalyst/design-system/ui/button';
import { Input } from '@katalyst/design-system/ui/input';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Skeleton } from '@katalyst/design-system/ui/skeleton';
import { Slider } from '@katalyst/design-system/ui/slider';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@katalyst/design-system/ui/select';

// Components
import { ProductGrid } from '../components/ProductGrid';
import { LiveMetrics } from '../components/LiveMetrics';
import { AIRecommendations } from '../components/AIRecommendations';

// Store
import { useStorefrontStore } from '../store/storefront-store';

// Data
import { featuredProducts, performanceTestData } from '../data/homepage-data';

// Icons
import { Search, Filter, SlidersHorizontal, Zap, TrendingUp, Grid, List, ArrowUpDown } from 'lucide-react';

interface ProductFilters {
  category?: string;
  priceRange: [number, number];
  rating?: number;
  inStock?: boolean;
  brands?: string[];
  tags?: string[];
}

interface SortOption {
  value: string;
  label: string;
  field: keyof any;
  direction: 'asc' | 'desc';
}

export function ProductsPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [filters, setFilters] = useState<ProductFilters>({
    priceRange: [0, 2000]
  });
  const [sortOption, setSortOption] = useState<string>('relevance');
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const [performanceMode, setPerformanceMode] = useState(false);

  // Katalyst Hooks
  const { executeTask, metrics } = useMultithreadingStore();
  const { createThreadPool } = useAdvancedMultithreading();
  const { generateRecommendations, isAIEnabled } = useAI();

  // Store
  const {
    search,
    searchProducts,
    updateSearchFilters,
    clearSearch,
    loadProducts,
    products
  } = useStorefrontStore();

  // Sort options with multithreading optimization
  const sortOptions: SortOption[] = [
    { value: 'relevance', label: 'Relevance', field: 'score', direction: 'desc' },
    { value: 'price-low', label: 'Price: Low to High', field: 'price', direction: 'asc' },
    { value: 'price-high', label: 'Price: High to Low', field: 'price', direction: 'desc' },
    { value: 'rating', label: 'Highest Rated', field: 'rating', direction: 'desc' },
    { value: 'newest', label: 'Newest First', field: 'createdAt', direction: 'desc' },
    { value: 'popular', label: 'Most Popular', field: 'reviewCount', direction: 'desc' }
  ];

  // Categories extracted from products
  const categories = useMemo(() => {
    const allCategories = products.flatMap(p => p.categories);
    return [...new Set(allCategories)].sort();
  }, [products]);

  // Multithreaded search with advanced filtering
  const handleSearch = useCallback(async (query: string, newFilters?: Partial<ProductFilters>) => {
    const combinedFilters = { ...filters, ...newFilters };
    setIsSearching(true);

    try {
      if (performanceMode) {
        // Use large dataset for performance testing
        const testProducts = performanceTestData.largeProductSet;
        await searchWithLargeDataset(query, combinedFilters, testProducts);
      } else {
        // Standard search with multithreading
        await searchProducts(query, combinedFilters);
      }
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      setIsSearching(false);
    }
  }, [filters, performanceMode, searchProducts]);

  const searchWithLargeDataset = async (query: string, filters: ProductFilters, dataset: any[]) => {
    // Create dedicated search thread pool for heavy operations
    const searchPool = await createThreadPool({
      type: 'rayon',
      workerCount: Math.min(8, navigator.hardwareConcurrency || 4)
    });

    const startTime = performance.now();

    // Execute parallel search operations
    const searchTasks = [
      executeTask({
        id: `text-search-${Date.now()}`,
        operation: 'full_text_search',
        data: { query, dataset: dataset.slice(0, 250) },
        priority: 'high',
        status: 'pending'
      }),
      executeTask({
        id: `category-filter-${Date.now()}`,
        operation: 'category_filtering',
        data: { filters, dataset: dataset.slice(250, 500) },
        priority: 'high',
        status: 'pending'
      }),
      executeTask({
        id: `price-filter-${Date.now()}`,
        operation: 'price_range_filter',
        data: { priceRange: filters.priceRange, dataset: dataset.slice(500, 750) },
        priority: 'high',
        status: 'pending'
      }),
      executeTask({
        id: `availability-filter-${Date.now()}`,
        operation: 'availability_filter',
        data: { inStockOnly: filters.inStock, dataset: dataset.slice(750) },
        priority: 'normal',
        status: 'pending'
      })
    ];

    const results = await Promise.all(searchTasks);
    const endTime = performance.now();

    console.log(`Multithreaded search completed in ${endTime - startTime}ms`);
    console.log(`Processed ${dataset.length} products across ${searchTasks.length} threads`);

    // Merge and deduplicate results
    const allResults = results.flatMap(r => r.result?.products || []);
    const uniqueResults = allResults.filter((product, index, array) => 
      array.findIndex(p => p.id === product.id) === index
    );

    return uniqueResults;
  };

  // AI-powered smart filtering
  const handleSmartFilter = useCallback(async () => {
    if (!isAIEnabled) return;

    const userContext = {
      searchHistory: JSON.parse(localStorage.getItem('search-history') || '[]'),
      viewedProducts: JSON.parse(localStorage.getItem('viewed-products') || '[]'),
      currentQuery: searchQuery,
      currentFilters: filters
    };

    const smartFilters = await generateRecommendations({
      context: 'product-filtering',
      userContext,
      type: 'filter-suggestions'
    });

    console.log('AI Smart Filters:', smartFilters);
  }, [searchQuery, filters, generateRecommendations, isAIEnabled]);

  // Handle filter changes with debouncing
  useEffect(() => {
    const debounce = setTimeout(() => {
      if (searchQuery.trim()) {
        handleSearch(searchQuery, filters);
      }
    }, 300);

    return () => clearTimeout(debounce);
  }, [searchQuery, filters, handleSearch]);

  // Initialize products
  useEffect(() => {
    loadProducts();
  }, [loadProducts]);

  const currentProducts = search.query ? search.results : products;
  const totalResults = search.query ? search.totalResults : products.length;

  return (
    <div className="min-h-screen bg-background">
      {/* Header Section */}
      <div className="border-b bg-white/80 backdrop-blur-sm sticky top-0 z-40">
        <div className="container mx-auto px-4 py-4">
          <div className="flex flex-col lg:flex-row gap-4 items-start lg:items-center justify-between">
            {/* Search Bar */}
            <div className="flex-1 max-w-2xl">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  placeholder="Search products with AI-powered suggestions..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 pr-12"
                />
                {isSearching && (
                  <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                    <div className="animate-spin rounded-full h-4 w-4 border-2 border-primary border-t-transparent" />
                  </div>
                )}
              </div>
            </div>

            {/* Controls */}
            <div className="flex items-center space-x-2">
              {/* Performance Mode Toggle */}
              <Button
                variant={performanceMode ? "default" : "outline"}
                size="sm"
                onClick={() => setPerformanceMode(!performanceMode)}
                className="hidden lg:flex"
              >
                <Zap className="w-4 h-4 mr-1" />
                {performanceMode ? "1K Products" : "Performance"}
              </Button>

              {/* AI Smart Filter */}
              <Button
                variant="outline"
                size="sm"
                onClick={handleSmartFilter}
                disabled={!isAIEnabled}
                className="hidden lg:flex"
              >
                <TrendingUp className="w-4 h-4 mr-1" />
                Smart Filter
              </Button>

              {/* View Mode Toggle */}
              <div className="flex rounded-lg border">
                <Button
                  variant={viewMode === 'grid' ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode('grid')}
                  className="rounded-r-none"
                >
                  <Grid className="w-4 h-4" />
                </Button>
                <Button
                  variant={viewMode === 'list' ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode('list')}
                  className="rounded-l-none"
                >
                  <List className="w-4 h-4" />
                </Button>
              </div>

              {/* Filters Toggle */}
              <Button
                variant={showFilters ? "default" : "outline"}
                size="sm"
                onClick={() => setShowFilters(!showFilters)}
              >
                <SlidersHorizontal className="w-4 h-4 mr-1" />
                Filters
              </Button>
            </div>
          </div>

          {/* Results Summary */}
          <div className="flex items-center justify-between mt-4 text-sm text-muted-foreground">
            <div className="flex items-center space-x-4">
              <span>
                {totalResults} products
                {searchQuery && ` for "${searchQuery}"`}
              </span>
              {metrics && (
                <>
                  <span>•</span>
                  <span>Search time: {search.loading ? 'Processing...' : `${(metrics.searchTime || 0).toFixed(0)}ms`}</span>
                  <span>•</span>
                  <span>Threads: {metrics.activeThreads || 1}</span>
                </>
              )}
            </div>

            {/* Sort Dropdown */}
            <div className="flex items-center space-x-2">
              <ArrowUpDown className="w-4 h-4" />
              <Select value={sortOption} onValueChange={setSortOption}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                  {sortOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-6">
        <div className="flex gap-6">
          {/* Sidebar Filters */}
          <AnimatePresence>
            {showFilters && (
              <motion.aside
                initial={{ x: -300, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                exit={{ x: -300, opacity: 0 }}
                transition={{ duration: 0.3 }}
                className="w-80 shrink-0"
              >
                <Card className="p-6 space-y-6 sticky top-24">
                  <div className="flex items-center justify-between">
                    <h3 className="font-semibold">Filters</h3>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => {
                        setFilters({ priceRange: [0, 2000] });
                        clearSearch();
                      }}
                    >
                      Clear All
                    </Button>
                  </div>

                  {/* Categories */}
                  <div className="space-y-3">
                    <h4 className="font-medium text-sm">Category</h4>
                    <div className="space-y-2">
                      {categories.map((category) => (
                        <label key={category} className="flex items-center space-x-2 cursor-pointer">
                          <input
                            type="radio"
                            name="category"
                            value={category}
                            checked={filters.category === category}
                            onChange={(e) => setFilters({ ...filters, category: e.target.value })}
                            className="w-4 h-4 text-primary"
                          />
                          <span className="text-sm">{category}</span>
                        </label>
                      ))}
                    </div>
                  </div>

                  {/* Price Range */}
                  <div className="space-y-3">
                    <h4 className="font-medium text-sm">Price Range</h4>
                    <div className="px-3">
                      <Slider
                        value={filters.priceRange}
                        onValueChange={(value) => setFilters({ ...filters, priceRange: value as [number, number] })}
                        max={2000}
                        min={0}
                        step={10}
                        className="w-full"
                      />
                      <div className="flex justify-between text-xs text-muted-foreground mt-1">
                        <span>${filters.priceRange[0]}</span>
                        <span>${filters.priceRange[1]}</span>
                      </div>
                    </div>
                  </div>

                  {/* Rating Filter */}
                  <div className="space-y-3">
                    <h4 className="font-medium text-sm">Minimum Rating</h4>
                    <div className="space-y-2">
                      {[4.5, 4.0, 3.5, 3.0].map((rating) => (
                        <label key={rating} className="flex items-center space-x-2 cursor-pointer">
                          <input
                            type="radio"
                            name="rating"
                            value={rating}
                            checked={filters.rating === rating}
                            onChange={(e) => setFilters({ ...filters, rating: parseFloat(e.target.value) })}
                            className="w-4 h-4 text-primary"
                          />
                          <div className="flex items-center space-x-1">
                            <span className="text-sm">★</span>
                            <span className="text-sm">{rating} & up</span>
                          </div>
                        </label>
                      ))}
                    </div>
                  </div>

                  {/* Availability */}
                  <div className="space-y-3">
                    <h4 className="font-medium text-sm">Availability</h4>
                    <label className="flex items-center space-x-2 cursor-pointer">
                      <input
                        type="checkbox"
                        checked={filters.inStock === true}
                        onChange={(e) => setFilters({ 
                          ...filters, 
                          inStock: e.target.checked ? true : undefined 
                        })}
                        className="w-4 h-4 text-primary"
                      />
                      <span className="text-sm">In stock only</span>
                    </label>
                  </div>
                </Card>
              </motion.aside>
            )}
          </AnimatePresence>

          {/* Main Content */}
          <div className="flex-1 space-y-6">
            {/* Performance Metrics */}
            {performanceMode && (
              <Card className="p-4 bg-gradient-to-r from-blue-50 to-purple-50">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <Zap className="w-5 h-5 text-primary" />
                    <span className="font-medium">Performance Mode Active</span>
                    <Badge variant="secondary">{performanceTestData.largeProductSet.length} products</Badge>
                  </div>
                  <div className="text-sm text-muted-foreground">
                    Testing multithreading with large dataset
                  </div>
                </div>
              </Card>
            )}

            {/* AI Recommendations */}
            {searchQuery && isAIEnabled && (
              <div className="mb-6">
                <AIRecommendations 
                  context="product" 
                  limit={3}
                />
              </div>
            )}

            {/* Products Grid/List */}
            {search.loading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {Array.from({ length: 12 }).map((_, i) => (
                  <Card key={i} className="h-80">
                    <Skeleton className="h-48 w-full" />
                    <div className="p-4 space-y-2">
                      <Skeleton className="h-4 w-3/4" />
                      <Skeleton className="h-3 w-1/2" />
                      <Skeleton className="h-6 w-full" />
                    </div>
                  </Card>
                ))}
              </div>
            ) : (
              <ProductGrid
                products={currentProducts}
                enableMultithreading={true}
                showPerformanceMetrics={performanceMode}
                gridCols={viewMode === 'grid' ? 4 : 1}
                enableVirtualization={performanceMode}
              />
            )}

            {/* No Results */}
            {!search.loading && currentProducts.length === 0 && (
              <Card className="p-12 text-center">
                <div className="space-y-4">
                  <div className="text-4xl">🔍</div>
                  <h3 className="text-xl font-semibold">No products found</h3>
                  <p className="text-muted-foreground max-w-md mx-auto">
                    {searchQuery 
                      ? `No results for "${searchQuery}". Try adjusting your search terms or filters.`
                      : "Try adjusting your filters to see more products."
                    }
                  </p>
                  <div className="space-x-2">
                    <Button
                      onClick={() => {
                        setSearchQuery('');
                        setFilters({ priceRange: [0, 2000] });
                        clearSearch();
                      }}
                    >
                      Clear Search
                    </Button>
                    <Button variant="outline" onClick={() => setShowFilters(true)}>
                      Adjust Filters
                    </Button>
                  </div>
                </div>
              </Card>
            )}

            {/* Live Metrics for Performance Mode */}
            {performanceMode && (
              <div className="mt-8">
                <LiveMetrics />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

