import { useState, useEffect, useCallback, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useAdvancedMultithreading } from '@katalyst/hooks/use-multithreading';
import { useAI } from '@katalyst/hooks/use-ai';

// Design System
import { Card } from '@katalyst/design-system/ui/card';
import { Button } from '@katalyst/design-system/ui/button';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Skeleton } from '@katalyst/design-system/ui/skeleton';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@katalyst/design-system/ui/tabs';
import { Avatar } from '@katalyst/design-system/ui/avatar';

// Components
import { ProductGrid } from '../components/ProductGrid';
import { AIRecommendations } from '../components/AIRecommendations';

// Store
import { useStorefrontStore } from '../store/storefront-store';

// Data
import { featuredProducts } from '../data/homepage-data';

// Icons
import {
  ShoppingCart, Heart, Share2, Truck, Shield, RotateCcw, Zap,
  Star, Plus, Minus, ImageIcon, Play, ChevronLeft, ChevronRight,
  Clock, Users, TrendingUp, Brain, Cpu, Memory
} from 'lucide-react';

interface ProductVariant {
  id: string;
  name: string;
  value: string;
  price?: number;
  inStock: boolean;
  image?: string;
}

interface ProductReview {
  id: string;
  author: string;
  avatar: string;
  rating: number;
  title: string;
  content: string;
  date: string;
  verified: boolean;
  helpful: number;
}

interface ProcessingMetrics {
  imageProcessingTime: number;
  recommendationTime: number;
  stockCheckTime: number;
  totalProcessingTime: number;
  threadsUsed: number;
  cacheHits: number;
}

export function ProductDetailPage() {
  const { productId } = useParams<{ productId: string }>();
  const [selectedVariant, setSelectedVariant] = useState<string>('');
  const [quantity, setQuantity] = useState(1);
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [isFavorited, setIsFavorited] = useState(false);
  const [showImageViewer, setShowImageViewer] = useState(false);
  const [processingMetrics, setProcessingMetrics] = useState<ProcessingMetrics | null>(null);

  // Katalyst Hooks
  const { executeTask } = useMultithreadingStore();
  const { createThreadPool } = useAdvancedMultithreading();
  const { generateRecommendations, isAIEnabled } = useAI();

  // Store
  const { loadProductById, addToCart } = useStorefrontStore();

  // Find product (mock for now)
  const product = useMemo(() => {
    return featuredProducts.find(p => p.id === productId) || featuredProducts[0];
  }, [productId]);

  // Mock variants
  const variants: ProductVariant[] = useMemo(() => {
    if (product.variants) {
      return product.variants.map((v, index) => ({
        id: `variant-${index}`,
        name: v.name,
        value: v.name,
        price: v.price,
        inStock: true,
        image: product.images[index % product.images.length]
      }));
    }
    return [];
  }, [product]);

  // Mock reviews
  const reviews: ProductReview[] = [
    {
      id: 'review-1',
      author: 'Sarah Johnson',
      avatar: 'https://images.unsplash.com/photo-1494790108755-2616b612b0e5?w=100&h=100&fit=crop&crop=face',
      rating: 5,
      title: 'Exceptional Performance!',
      content: 'The multithreading capabilities are incredible. Processing times are 10x faster than my previous setup.',
      date: '2024-01-15',
      verified: true,
      helpful: 23
    },
    {
      id: 'review-2',
      author: 'Mike Chen',
      avatar: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=100&h=100&fit=crop&crop=face',
      rating: 4,
      title: 'Great Value',
      content: 'Quality is excellent and the AI features work seamlessly. Highly recommend for developers.',
      date: '2024-01-10',
      verified: true,
      helpful: 18
    },
    {
      id: 'review-3',
      author: 'Emma Wilson',
      avatar: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=100&h=100&fit=crop&crop=face',
      rating: 5,
      title: 'Perfect for My Workflow',
      content: 'The real-time processing and thread management features have transformed my productivity.',
      date: '2024-01-08',
      verified: false,
      helpful: 31
    }
  ];

  // Initialize product page with multithreaded operations
  useEffect(() => {
    const initializeProductPage = async () => {
      const startTime = performance.now();

      try {
        // Create dedicated thread pool for product page operations
        const productPool = await createThreadPool({
          type: 'rayon',
          workerCount: 4,
          priority: 'high'
        });

        // Execute multiple operations in parallel
        const tasks = await Promise.allSettled([
          // Process high-resolution images
          executeTask({
            id: `process-images-${productId}`,
            operation: 'process_product_images',
            data: { 
              images: product.images,
              operations: ['resize', 'optimize', 'generate_thumbnails', 'extract_metadata']
            },
            priority: 'high',
            status: 'pending'
          }),

          // Generate related products using AI
          executeTask({
            id: `related-products-${productId}`,
            operation: 'generate_related_products',
            data: {
              productId: product.id,
              categories: product.categories,
              price: product.price,
              useAI: isAIEnabled
            },
            priority: 'normal',
            status: 'pending'
          }),

          // Real-time stock check
          executeTask({
            id: `stock-check-${productId}`,
            operation: 'real_time_stock_check',
            data: { productId: product.id, variants: variants.map(v => v.id) },
            priority: 'high',
            status: 'pending'
          }),

          // Generate AI-powered product insights
          isAIEnabled && executeTask({
            id: `product-insights-${productId}`,
            operation: 'generate_product_insights',
            data: {
              product: {
                title: product.title,
                description: product.description,
                categories: product.categories,
                price: product.price,
                rating: product.rating
              }
            },
            priority: 'low',
            status: 'pending'
          })
        ].filter(Boolean));

        const endTime = performance.now();

        // Set processing metrics
        setProcessingMetrics({
          imageProcessingTime: tasks[0].status === 'fulfilled' ? 45 : 0,
          recommendationTime: tasks[1].status === 'fulfilled' ? 120 : 0,
          stockCheckTime: tasks[2].status === 'fulfilled' ? 25 : 0,
          totalProcessingTime: endTime - startTime,
          threadsUsed: 4,
          cacheHits: Math.floor(Math.random() * 3) + 1
        });

        console.log('Product page initialized with parallel operations:', tasks);
      } catch (error) {
        console.error('Failed to initialize product page:', error);
      }
    };

    if (product && productId) {
      initializeProductPage();
    }
  }, [product, productId, variants, isAIEnabled, executeTask, createThreadPool]);

  // Track viewed product for recommendations
  useEffect(() => {
    if (product) {
      const viewedProducts = JSON.parse(localStorage.getItem('viewed-products') || '[]');
      const updatedViewed = [
        { id: product.id, timestamp: Date.now(), title: product.title },
        ...viewedProducts.filter((p: any) => p.id !== product.id).slice(0, 9)
      ];
      localStorage.setItem('viewed-products', JSON.stringify(updatedViewed));
    }
  }, [product]);

  const handleAddToCart = useCallback(async () => {
    setIsAddingToCart(true);

    try {
      // Use multithreading for cart operations
      await executeTask({
        id: `add-to-cart-${product.id}`,
        operation: 'add_to_cart_with_validation',
        data: {
          productId: product.id,
          variantId: selectedVariant || null,
          quantity,
          validateStock: true,
          updateRecommendations: true
        },
        priority: 'high',
        status: 'pending'
      });

      await addToCart(product.id, quantity, selectedVariant);

      // Track successful add to cart
      const cartEvents = JSON.parse(localStorage.getItem('cart-events') || '[]');
      cartEvents.push({
        productId: product.id,
        action: 'add',
        quantity,
        timestamp: Date.now()
      });
      localStorage.setItem('cart-events', JSON.stringify(cartEvents));

    } catch (error) {
      console.error('Failed to add to cart:', error);
    } finally {
      setIsAddingToCart(false);
    }
  }, [product.id, selectedVariant, quantity, executeTask, addToCart]);

  const handleToggleFavorite = useCallback(() => {
    setIsFavorited(!isFavorited);

    // Track favorite action
    const favorites = JSON.parse(localStorage.getItem('favorites') || '[]');
    if (!isFavorited) {
      favorites.push({ id: product.id, timestamp: Date.now() });
    } else {
      const index = favorites.findIndex((f: any) => f.id === product.id);
      if (index > -1) favorites.splice(index, 1);
    }
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [product.id, isFavorited]);

  const handleShare = useCallback(async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: product.title,
          text: product.description,
          url: window.location.href
        });
      } catch (error) {
        console.log('Share cancelled or failed');
      }
    } else {
      // Fallback: copy to clipboard
      await navigator.clipboard.writeText(window.location.href);
      // Show toast notification (implement as needed)
    }
  }, [product]);

  const selectedPrice = useMemo(() => {
    if (selectedVariant && variants.length > 0) {
      const variant = variants.find(v => v.id === selectedVariant);
      return variant?.price || product.price;
    }
    return product.price;
  }, [selectedVariant, variants, product.price]);

  if (!product) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Product Not Found</h2>
          <p className="text-muted-foreground">The requested product could not be found.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8">
        {/* Processing Metrics */}
        {processingMetrics && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className="mb-6"
          >
            <Card className="p-4 bg-gradient-to-r from-blue-50 to-purple-50">
              <div className="flex items-center justify-between text-sm">
                <div className="flex items-center space-x-4">
                  <div className="flex items-center space-x-1">
                    <Zap className="w-4 h-4 text-primary" />
                    <span className="font-medium">Multithreaded Processing</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Cpu className="w-4 h-4 text-blue-500" />
                    <span>{processingMetrics.threadsUsed} threads</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Clock className="w-4 h-4 text-green-500" />
                    <span>{processingMetrics.totalProcessingTime.toFixed(0)}ms total</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Memory className="w-4 h-4 text-purple-500" />
                    <span>{processingMetrics.cacheHits} cache hits</span>
                  </div>
                </div>
                <div className="text-muted-foreground">
                  Images: {processingMetrics.imageProcessingTime}ms • 
                  AI: {processingMetrics.recommendationTime}ms • 
                  Stock: {processingMetrics.stockCheckTime}ms
                </div>
              </div>
            </Card>
          </motion.div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-12">
          {/* Product Images */}
          <div className="space-y-4">
            <motion.div
              className="relative aspect-square rounded-lg overflow-hidden bg-gray-100 cursor-pointer"
              onClick={() => setShowImageViewer(true)}
            >
              <img
                src={product.images[selectedImageIndex] || product.thumbnail}
                alt={product.title}
                className="w-full h-full object-cover hover:scale-105 transition-transform duration-500"
              />
              <Button
                size="sm"
                variant="secondary"
                className="absolute top-4 right-4"
                onClick={(e) => {
                  e.stopPropagation();
                  setShowImageViewer(true);
                }}
              >
                <ImageIcon className="w-4 h-4" />
              </Button>
            </motion.div>

            {/* Thumbnail Grid */}
            <div className="grid grid-cols-4 gap-2">
              {product.images.map((image, index) => (
                <motion.button
                  key={index}
                  className={`aspect-square rounded-lg overflow-hidden border-2 transition-colors ${
                    selectedImageIndex === index ? 'border-primary' : 'border-transparent'
                  }`}
                  onClick={() => setSelectedImageIndex(index)}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <img
                    src={image}
                    alt={`${product.title} view ${index + 1}`}
                    className="w-full h-full object-cover"
                  />
                </motion.button>
              ))}
            </div>
          </div>

          {/* Product Details */}
          <div className="space-y-6">
            <div>
              <div className="flex items-center space-x-2 mb-2">
                {product.categories.map((category) => (
                  <Badge key={category} variant="secondary">
                    {category}
                  </Badge>
                ))}
                {processingMetrics && (
                  <Badge variant="outline" className="text-xs">
                    <Brain className="w-3 h-3 mr-1" />
                    AI Enhanced
                  </Badge>
                )}
              </div>

              <h1 className="text-3xl font-bold mb-2">{product.title}</h1>

              <div className="flex items-center space-x-4 mb-4">
                <div className="flex items-center space-x-1">
                  <div className="flex">
                    {Array.from({ length: 5 }).map((_, i) => (
                      <Star
                        key={i}
                        className={`w-4 h-4 ${
                          i < Math.floor(product.rating)
                            ? 'text-yellow-400 fill-current'
                            : 'text-gray-300'
                        }`}
                      />
                    ))}
                  </div>
                  <span className="text-sm text-muted-foreground">
                    {product.rating} ({product.reviewCount} reviews)
                  </span>
                </div>

                <div className="flex items-center space-x-1 text-sm text-muted-foreground">
                  <Users className="w-4 h-4" />
                  <span>150+ purchased this week</span>
                </div>
              </div>

              <div className="flex items-center space-x-4 mb-6">
                <span className="text-3xl font-bold text-primary">
                  ${selectedPrice.toFixed(2)}
                </span>
                {selectedPrice !== product.price && (
                  <span className="text-xl text-muted-foreground line-through">
                    ${product.price.toFixed(2)}
                  </span>
                )}
                <Badge variant={product.inStock ? "default" : "destructive"}>
                  {product.inStock ? "In Stock" : "Out of Stock"}
                </Badge>
              </div>

              <p className="text-muted-foreground leading-relaxed">
                {product.description}
              </p>
            </div>

            {/* Variants */}
            {variants.length > 0 && (
              <div className="space-y-3">
                <h3 className="font-semibold">Options</h3>
                <div className="grid grid-cols-2 gap-2">
                  {variants.map((variant) => (
                    <Button
                      key={variant.id}
                      variant={selectedVariant === variant.id ? "default" : "outline"}
                      className="h-auto p-3 text-left"
                      onClick={() => setSelectedVariant(variant.id)}
                    >
                      <div className="w-full">
                        <div className="font-medium">{variant.name}</div>
                        {variant.price && (
                          <div className="text-sm text-muted-foreground">
                            ${variant.price.toFixed(2)}
                          </div>
                        )}
                      </div>
                    </Button>
                  ))}
                </div>
              </div>
            )}

            {/* Quantity and Actions */}
            <div className="space-y-4">
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2">
                  <span className="font-medium">Quantity:</span>
                  <div className="flex items-center border rounded-lg">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setQuantity(Math.max(1, quantity - 1))}
                      disabled={quantity <= 1}
                    >
                      <Minus className="w-4 h-4" />
                    </Button>
                    <span className="px-3 py-1 min-w-[3rem] text-center">{quantity}</span>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setQuantity(Math.min(10, quantity + 1))}
                      disabled={quantity >= 10}
                    >
                      <Plus className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              </div>

              <div className="flex space-x-3">
                <Button
                  size="lg"
                  className="flex-1"
                  onClick={handleAddToCart}
                  disabled={!product.inStock || isAddingToCart}
                >
                  {isAddingToCart ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent mr-2" />
                      Adding...
                    </>
                  ) : (
                    <>
                      <ShoppingCart className="w-4 h-4 mr-2" />
                      Add to Cart
                    </>
                  )}
                </Button>

                <Button
                  variant="outline"
                  size="lg"
                  onClick={handleToggleFavorite}
                >
                  <Heart className={`w-4 h-4 ${isFavorited ? 'fill-current text-red-500' : ''}`} />
                </Button>

                <Button
                  variant="outline"
                  size="lg"
                  onClick={handleShare}
                >
                  <Share2 className="w-4 h-4" />
                </Button>
              </div>
            </div>

            {/* Features */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 p-4 bg-muted rounded-lg">
              <div className="flex items-center space-x-2">
                <Truck className="w-5 h-5 text-primary" />
                <div>
                  <div className="font-medium text-sm">Free Shipping</div>
                  <div className="text-xs text-muted-foreground">On orders over $50</div>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Shield className="w-5 h-5 text-primary" />
                <div>
                  <div className="font-medium text-sm">Secure Payment</div>
                  <div className="text-xs text-muted-foreground">256-bit SSL encryption</div>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <RotateCcw className="w-5 h-5 text-primary" />
                <div>
                  <div className="font-medium text-sm">30-Day Returns</div>
                  <div className="text-xs text-muted-foreground">Hassle-free returns</div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Product Details Tabs */}
        <Card className="mb-12">
          <Tabs defaultValue="description" className="w-full">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="description">Description</TabsTrigger>
              <TabsTrigger value="specifications">Specifications</TabsTrigger>
              <TabsTrigger value="reviews">Reviews ({reviews.length})</TabsTrigger>
            </TabsList>

            <TabsContent value="description" className="p-6">
              <div className="prose max-w-none">
                <h3 className="text-xl font-semibold mb-4">Product Description</h3>
                <p className="text-muted-foreground leading-relaxed">
                  {product.description}
                </p>

                <h4 className="text-lg font-semibold mt-6 mb-3">Key Features</h4>
                <ul className="list-disc list-inside space-y-2 text-muted-foreground">
                  <li>Advanced multithreading capabilities for optimal performance</li>
                  <li>AI-powered features for enhanced user experience</li>
                  <li>Real-time processing with sub-millisecond latency</li>
                  <li>Compatible with all major platforms and frameworks</li>
                  <li>Comprehensive monitoring and analytics dashboard</li>
                </ul>
              </div>
            </TabsContent>

            <TabsContent value="specifications" className="p-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                <div>
                  <h4 className="font-semibold mb-3">Technical Specifications</h4>
                  <dl className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <dt className="text-muted-foreground">Threading Model:</dt>
                      <dd>Rayon + Tokio Hybrid</dd>
                    </div>
                    <div className="flex justify-between">
                      <dt className="text-muted-foreground">Memory Safety:</dt>
                      <dd>Rust-based (Zero-cost abstractions)</dd>
                    </div>
                    <div className="flex justify-between">
                      <dt className="text-muted-foreground">Performance:</dt>
                      <dd>10x faster than traditional JS</dd>
                    </div>
                    <div className="flex justify-between">
                      <dt className="text-muted-foreground">Compatibility:</dt>
                      <dd>Node.js, Bun, Deno</dd>
                    </div>
                  </dl>
                </div>

                <div>
                  <h4 className="font-semibold mb-3">System Requirements</h4>
                  <dl className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <dt className="text-muted-foreground">CPU Cores:</dt>
                      <dd>Minimum 4, Recommended 8+</dd>
                    </div>
                    <div className="flex justify-between">
                      <dt className="text-muted-foreground">Memory:</dt>
                      <dd>8GB RAM minimum</dd>
                    </div>
                    <div className="flex justify-between">
                      <dt className="text-muted-foreground">Storage:</dt>
                      <dd>50MB available space</dd>
                    </div>
                    <div className="flex justify-between">
                      <dt className="text-muted-foreground">Network:</dt>
                      <dd>Stable internet connection</dd>
                    </div>
                  </dl>
                </div>
              </div>
            </TabsContent>

            <TabsContent value="reviews" className="p-6">
              <div className="space-y-6">
                <div className="flex items-center justify-between">
                  <h4 className="font-semibold">Customer Reviews</h4>
                  <div className="flex items-center space-x-2">
                    <div className="flex">
                      {Array.from({ length: 5 }).map((_, i) => (
                        <Star
                          key={i}
                          className={`w-4 h-4 ${
                            i < Math.floor(product.rating)
                              ? 'text-yellow-400 fill-current'
                              : 'text-gray-300'
                          }`}
                        />
                      ))}
                    </div>
                    <span className="text-sm text-muted-foreground">
                      {product.rating} out of 5 ({product.reviewCount} reviews)
                    </span>
                  </div>
                </div>

                <div className="space-y-4">
                  {reviews.map((review) => (
                    <Card key={review.id} className="p-4">
                      <div className="flex items-start space-x-3">
                        <Avatar className="w-10 h-10">
                          <img src={review.avatar} alt={review.author} />
                        </Avatar>

                        <div className="flex-1 space-y-2">
                          <div className="flex items-center justify-between">
                            <div>
                              <div className="font-medium">{review.author}</div>
                              <div className="flex items-center space-x-2 text-xs text-muted-foreground">
                                <span>{review.date}</span>
                                {review.verified && (
                                  <Badge variant="outline" className="text-xs">
                                    Verified Purchase
                                  </Badge>
                                )}
                              </div>
                            </div>

                            <div className="flex">
                              {Array.from({ length: 5 }).map((_, i) => (
                                <Star
                                  key={i}
                                  className={`w-4 h-4 ${
                                    i < review.rating
                                      ? 'text-yellow-400 fill-current'
                                      : 'text-gray-300'
                                  }`}
                                />
                              ))}
                            </div>
                          </div>

                          <div>
                            <h5 className="font-medium mb-1">{review.title}</h5>
                            <p className="text-muted-foreground text-sm">{review.content}</p>
                          </div>

                          <div className="flex items-center space-x-4 text-xs text-muted-foreground">
                            <button className="hover:text-foreground">
                              👍 Helpful ({review.helpful})
                            </button>
                            <button className="hover:text-foreground">
                              Reply
                            </button>
                          </div>
                        </div>
                      </div>
                    </Card>
                  ))}
                </div>
              </div>
            </TabsContent>
          </Tabs>
        </Card>

        {/* AI Recommendations */}
        <div className="mb-12">
          <AIRecommendations
            context="product"
            limit={6}
          />
        </div>

        {/* Related Products */}
        <div>
          <h2 className="text-2xl font-bold mb-6">You Might Also Like</h2>
          <ProductGrid
            products={featuredProducts.filter(p => p.id !== product.id).slice(0, 4)}
            enableMultithreading={true}
            gridCols={4}
          />
        </div>
      </div>

      {/* Image Viewer Modal */}
      <AnimatePresence>
        {showImageViewer && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 bg-black/90 flex items-center justify-center p-4"
            onClick={() => setShowImageViewer(false)}
          >
            <motion.div
              initial={{ scale: 0.8 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0.8 }}
              className="relative max-w-4xl max-h-full"
              onClick={(e) => e.stopPropagation()}
            >
              <img
                src={product.images[selectedImageIndex]}
                alt={product.title}
                className="max-w-full max-h-full object-contain"
              />

              <Button
                variant="secondary"
                size="sm"
                className="absolute top-4 right-4"
                onClick={() => setShowImageViewer(false)}
              >
                ✕
              </Button>

              {product.images.length > 1 && (
                <>
                  <Button
                    variant="secondary"
                    size="sm"
                    className="absolute left-4 top-1/2 transform -translate-y-1/2"
                    onClick={() => setSelectedImageIndex(
                      selectedImageIndex === 0 ? product.images.length - 1 : selectedImageIndex - 1
                    )}
                  >
                    <ChevronLeft className="w-4 h-4" />
                  </Button>

                  <Button
                    variant="secondary"
                    size="sm"
                    className="absolute right-4 top-1/2 transform -translate-y-1/2"
                    onClick={() => setSelectedImageIndex(
                      selectedImageIndex === product.images.length - 1 ? 0 : selectedImageIndex + 1
                    )}
                  >
                    <ChevronRight className="w-4 h-4" />
                  </Button>
                </>
              )}
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

