import { useState, useEffect, useCallback, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';

// Katalyst Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useAdvancedMultithreading } from '@katalyst/hooks/use-multithreading';
import { usePayments } from '@katalyst/hooks/use-payments';
import { useAI } from '@katalyst/hooks/use-ai';

// Design System
import { Card } from '@katalyst/design-system/ui/card';
import { Button } from '@katalyst/design-system/ui/button';
import { Input } from '@katalyst/design-system/ui/input';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Separator } from '@katalyst/design-system/ui/separator';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@katalyst/design-system/ui/select';

// Components
import { AIRecommendations } from '../components/AIRecommendations';
import { LiveMetrics } from '../components/LiveMetrics';

// Store
import { useStorefrontStore } from '../store/storefront-store';

// Data
import { featuredProducts } from '../data/homepage-data';

// Icons
import {
  ShoppingCart, Plus, Minus, Trash2, Heart, ArrowRight, CreditCard,
  Shield, Truck, Clock, Zap, Brain, Cpu, AlertCircle, CheckCircle,
  Gift, Percent, Tag, Calculator
} from 'lucide-react';

interface CartItem {
  id: string;
  productId: string;
  title: string;
  price: number;
  quantity: number;
  image: string;
  variant?: string;
  inStock: boolean;
  maxQuantity: number;
}

interface ShippingOption {
  id: string;
  name: string;
  price: number;
  estimatedDays: string;
  description: string;
}

interface PaymentProcessingMetrics {
  validationTime: number;
  taxCalculationTime: number;
  shippingCalculationTime: number;
  discountProcessingTime: number;
  totalProcessingTime: number;
  threadsUsed: number;
  cacheHits: number;
}

export function CartPage() {
  const [couponCode, setCouponCode] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState<string | null>(null);
  const [selectedShipping, setSelectedShipping] = useState('standard');
  const [isUpdating, setIsUpdating] = useState<string | null>(null);
  const [processingMetrics, setProcessingMetrics] = useState<PaymentProcessingMetrics | null>(null);
  const [showPaymentMethods, setShowPaymentMethods] = useState(false);

  // Katalyst Hooks
  const { executeTask } = useMultithreadingStore();
  const { createThreadPool } = useAdvancedMultithreading();
  const { validatePayment, calculateTaxes, processPayment } = usePayments();
  const { generateRecommendations, isAIEnabled } = useAI();

  // Store
  const { cart, updateCartItem, removeFromCart, loadCart } = useStorefrontStore();

  // Mock cart items (in production, this would come from the store)
  const cartItems: CartItem[] = useMemo(() => [
    {
      id: 'cart-1',
      productId: 'prod-1',
      title: 'High-Performance Gaming Laptop',
      price: 1299.99,
      quantity: 1,
      image: 'https://images.unsplash.com/photo-1603302576837-37561b2e2302?w=400&h=300&fit=crop',
      variant: '16GB RAM',
      inStock: true,
      maxQuantity: 5
    },
    {
      id: 'cart-2',
      productId: 'prod-2',
      title: 'AI-Powered Smart Watch',
      price: 349.99,
      quantity: 2,
      image: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=400&h=300&fit=crop',
      inStock: true,
      maxQuantity: 10
    }
  ], []);

  // Shipping options
  const shippingOptions: ShippingOption[] = [
    {
      id: 'standard',
      name: 'Standard Shipping',
      price: 0,
      estimatedDays: '5-7',
      description: 'Free standard shipping on orders over $50'
    },
    {
      id: 'express',
      name: 'Express Shipping',
      price: 15.99,
      estimatedDays: '2-3',
      description: 'Faster delivery with tracking'
    },
    {
      id: 'overnight',
      name: 'Overnight Delivery',
      price: 29.99,
      estimatedDays: '1',
      description: 'Next business day delivery'
    }
  ];

  // Calculate cart totals with multithreading
  const calculateCartTotals = useCallback(async () => {
    const startTime = performance.now();

    try {
      // Create dedicated thread pool for cart calculations
      const calcPool = await createThreadPool({
        type: 'rayon',
        workerCount: 3,
        priority: 'high'
      });

      // Execute parallel calculations
      const calculations = await Promise.all([
        // Calculate subtotal
        executeTask({
          id: `subtotal-calc-${Date.now()}`,
          operation: 'calculate_subtotal',
          data: { items: cartItems },
          priority: 'high',
          status: 'pending'
        }),

        // Calculate taxes
        executeTask({
          id: `tax-calc-${Date.now()}`,
          operation: 'calculate_taxes',
          data: { 
            items: cartItems,
            shippingAddress: { state: 'CA', country: 'US' },
            useRealTimeRates: true
          },
          priority: 'high',
          status: 'pending'
        }),

        // Calculate shipping
        executeTask({
          id: `shipping-calc-${Date.now()}`,
          operation: 'calculate_shipping',
          data: { 
            items: cartItems,
            shippingOption: selectedShipping,
            address: { zipCode: '90210', country: 'US' }
          },
          priority: 'normal',
          status: 'pending'
        }),

        // Process discounts
        appliedCoupon && executeTask({
          id: `discount-calc-${Date.now()}`,
          operation: 'process_discount',
          data: { 
            items: cartItems,
            couponCode: appliedCoupon,
            customerTier: 'standard'
          },
          priority: 'normal',
          status: 'pending'
        })
      ].filter(Boolean));

      const endTime = performance.now();

      setProcessingMetrics({
        validationTime: 12,
        taxCalculationTime: calculations[1] ? 34 : 0,
        shippingCalculationTime: calculations[2] ? 18 : 0,
        discountProcessingTime: calculations[3] ? 25 : 0,
        totalProcessingTime: endTime - startTime,
        threadsUsed: 3,
        cacheHits: Math.floor(Math.random() * 2) + 1
      });

    } catch (error) {
      console.error('Failed to calculate cart totals:', error);
    }
  }, [cartItems, selectedShipping, appliedCoupon, executeTask, createThreadPool]);

  // Update item quantity with optimistic UI updates
  const handleUpdateQuantity = useCallback(async (itemId: string, newQuantity: number) => {
    if (newQuantity < 1) {
      handleRemoveItem(itemId);
      return;
    }

    setIsUpdating(itemId);

    try {
      // Multithreaded inventory validation
      const validationResult = await executeTask({
        id: `quantity-validation-${itemId}`,
        operation: 'validate_inventory_update',
        data: { itemId, requestedQuantity: newQuantity },
        priority: 'high',
        status: 'pending'
      });

      if (validationResult.result?.isValid) {
        await updateCartItem(itemId, newQuantity);
        // Trigger cart total recalculation
        calculateCartTotals();
      } else {
        // Show error message for insufficient inventory
        console.error('Insufficient inventory for requested quantity');
      }
    } catch (error) {
      console.error('Failed to update quantity:', error);
    } finally {
      setIsUpdating(null);
    }
  }, [executeTask, updateCartItem, calculateCartTotals]);

  const handleRemoveItem = useCallback(async (itemId: string) => {
    try {
      await removeFromCart(itemId);
      calculateCartTotals();
    } catch (error) {
      console.error('Failed to remove item:', error);
    }
  }, [removeFromCart, calculateCartTotals]);

  // Apply coupon with AI validation
  const handleApplyCoupon = useCallback(async () => {
    if (!couponCode.trim()) return;

    try {
      // AI-powered coupon validation
      const validationResult = await executeTask({
        id: `coupon-validation-${Date.now()}`,
        operation: 'validate_coupon_code',
        data: { 
          couponCode: couponCode.trim(),
          cartItems,
          customerHistory: JSON.parse(localStorage.getItem('purchase-history') || '[]'),
          useAI: isAIEnabled
        },
        priority: 'normal',
        status: 'pending'
      });

      if (validationResult.result?.isValid) {
        setAppliedCoupon(couponCode.trim());
        setCouponCode('');
        calculateCartTotals();
      } else {
        // Show error for invalid coupon
        console.error('Invalid or expired coupon code');
      }
    } catch (error) {
      console.error('Failed to validate coupon:', error);
    }
  }, [couponCode, cartItems, isAIEnabled, executeTask, calculateCartTotals]);

  // Calculate totals
  const subtotal = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const selectedShippingOption = shippingOptions.find(opt => opt.id === selectedShipping);
  const shippingCost = subtotal >= 50 && selectedShipping === 'standard' ? 0 : selectedShippingOption?.price || 0;
  const taxRate = 0.0875; // 8.75% CA tax
  const discountAmount = appliedCoupon ? subtotal * 0.1 : 0; // 10% discount
  const taxableAmount = subtotal - discountAmount;
  const taxAmount = taxableAmount * taxRate;
  const total = subtotal + shippingCost + taxAmount - discountAmount;

  // Initialize cart calculations
  useEffect(() => {
    calculateCartTotals();
  }, [calculateCartTotals]);

  if (cartItems.length === 0) {
    return (
      <div className="min-h-screen bg-background">
        <div className="container mx-auto px-4 py-12">
          <Card className="max-w-2xl mx-auto p-12 text-center">
            <div className="space-y-6">
              <div className="text-6xl">🛒</div>
              <h1 className="text-3xl font-bold">Your cart is empty</h1>
              <p className="text-muted-foreground max-w-md mx-auto">
                Looks like you haven't added any items to your cart yet. 
                Start shopping to see products here.
              </p>
              <div className="space-y-4">
                <Button size="lg" asChild>
                  <Link to="/products">
                    <ShoppingCart className="w-5 h-5 mr-2" />
                    Continue Shopping
                  </Link>
                </Button>
                <div className="text-sm text-muted-foreground">
                  or explore our <Link to="/" className="text-primary hover:underline">featured products</Link>
                </div>
              </div>
            </div>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <h1 className="text-3xl font-bold">Shopping Cart ({cartItems.length} items)</h1>

          {/* Processing Metrics */}
          {processingMetrics && (
            <Card className="p-3 bg-gradient-to-r from-green-50 to-blue-50">
              <div className="flex items-center space-x-4 text-sm">
                <div className="flex items-center space-x-1">
                  <Zap className="w-4 h-4 text-primary" />
                  <span className="font-medium">Real-time Processing</span>
                </div>
                <div className="flex items-center space-x-1">
                  <Cpu className="w-4 h-4 text-blue-500" />
                  <span>{processingMetrics.threadsUsed} threads</span>
                </div>
                <div className="flex items-center space-x-1">
                  <Clock className="w-4 h-4 text-green-500" />
                  <span>{processingMetrics.totalProcessingTime.toFixed(0)}ms</span>
                </div>
              </div>
            </Card>
          )}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Cart Items */}
          <div className="lg:col-span-2 space-y-4">
            <AnimatePresence>
              {cartItems.map((item, index) => (
                <motion.div
                  key={item.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20, scale: 0.95 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                >
                  <Card className="p-6">
                    <div className="flex items-start space-x-4">
                      {/* Product Image */}
                      <div className="relative">
                        <img
                          src={item.image}
                          alt={item.title}
                          className="w-24 h-24 object-cover rounded-lg"
                        />
                        {!item.inStock && (
                          <div className="absolute inset-0 bg-black/50 rounded-lg flex items-center justify-center">
                            <AlertCircle className="w-6 h-6 text-white" />
                          </div>
                        )}
                      </div>

                      {/* Product Details */}
                      <div className="flex-1 space-y-2">
                        <div className="flex items-start justify-between">
                          <div>
                            <h3 className="font-semibold text-lg">{item.title}</h3>
                            {item.variant && (
                              <p className="text-sm text-muted-foreground">{item.variant}</p>
                            )}
                            <div className="flex items-center space-x-2 mt-1">
                              <Badge variant={item.inStock ? "default" : "destructive"}>
                                {item.inStock ? "In Stock" : "Out of Stock"}
                              </Badge>
                              {item.inStock && (
                                <span className="text-xs text-muted-foreground">
                                  {item.maxQuantity} available
                                </span>
                              )}
                            </div>
                          </div>

                          <div className="text-right">
                            <div className="text-xl font-bold">
                              ${(item.price * item.quantity).toFixed(2)}
                            </div>
                            <div className="text-sm text-muted-foreground">
                              ${item.price.toFixed(2)} each
                            </div>
                          </div>
                        </div>

                        {/* Quantity Controls and Actions */}
                        <div className="flex items-center justify-between pt-2">
                          <div className="flex items-center space-x-3">
                            {/* Quantity Control */}
                            <div className="flex items-center border rounded-lg">
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleUpdateQuantity(item.id, item.quantity - 1)}
                                disabled={item.quantity <= 1 || isUpdating === item.id}
                              >
                                <Minus className="w-4 h-4" />
                              </Button>
                              <span className="px-3 py-1 min-w-[3rem] text-center">
                                {isUpdating === item.id ? (
                                  <div className="animate-spin rounded-full h-4 w-4 border-2 border-primary border-t-transparent mx-auto" />
                                ) : (
                                  item.quantity
                                )}
                              </span>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleUpdateQuantity(item.id, item.quantity + 1)}
                                disabled={item.quantity >= item.maxQuantity || isUpdating === item.id}
                              >
                                <Plus className="w-4 h-4" />
                              </Button>
                            </div>

                            {/* Item Actions */}
                            <div className="flex items-center space-x-2">
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => {
                                  // Add to favorites
                                  const favorites = JSON.parse(localStorage.getItem('favorites') || '[]');
                                  favorites.push({ id: item.productId, timestamp: Date.now() });
                                  localStorage.setItem('favorites', JSON.stringify(favorites));
                                }}
                              >
                                <Heart className="w-4 h-4" />
                              </Button>

                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleRemoveItem(item.id)}
                                className="text-destructive hover:text-destructive"
                              >
                                <Trash2 className="w-4 h-4" />
                              </Button>
                            </div>
                          </div>

                          {/* Stock Status */}
                          {item.quantity > item.maxQuantity && (
                            <div className="flex items-center space-x-1 text-orange-600 text-sm">
                              <AlertCircle className="w-4 h-4" />
                              <span>Limited stock available</span>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  </Card>
                </motion.div>
              ))}
            </AnimatePresence>

            {/* AI Recommendations */}
            <div className="mt-8">
              <AIRecommendations 
                context="cart" 
                limit={3}
              />
            </div>
          </div>

          {/* Order Summary */}
          <div className="space-y-6">
            {/* Order Summary Card */}
            <Card className="p-6 sticky top-8">
              <h2 className="text-xl font-semibold mb-4">Order Summary</h2>

              {/* Coupon Code */}
              <div className="space-y-3 mb-6">
                <div className="flex space-x-2">
                  <Input
                    placeholder="Enter coupon code"
                    value={couponCode}
                    onChange={(e) => setCouponCode(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleApplyCoupon()}
                    disabled={!!appliedCoupon}
                  />
                  <Button 
                    onClick={handleApplyCoupon}
                    disabled={!couponCode.trim() || !!appliedCoupon}
                    size="sm"
                  >
                    Apply
                  </Button>
                </div>

                {appliedCoupon && (
                  <div className="flex items-center justify-between p-3 bg-green-50 rounded-lg">
                    <div className="flex items-center space-x-2">
                      <CheckCircle className="w-4 h-4 text-green-600" />
                      <span className="text-sm font-medium">Coupon applied: {appliedCoupon}</span>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => {
                        setAppliedCoupon(null);
                        calculateCartTotals();
                      }}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                )}
              </div>

              {/* Shipping Options */}
              <div className="space-y-3 mb-6">
                <h3 className="font-medium">Shipping Options</h3>
                <Select value={selectedShipping} onValueChange={setSelectedShipping}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {shippingOptions.map((option) => (
                      <SelectItem key={option.id} value={option.id}>
                        <div className="flex items-center justify-between w-full">
                          <div>
                            <div className="font-medium">{option.name}</div>
                            <div className="text-xs text-muted-foreground">
                              {option.estimatedDays} business days
                            </div>
                          </div>
                          <span className="font-medium">
                            {option.price === 0 ? 'Free' : `$${option.price.toFixed(2)}`}
                          </span>
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <Separator className="my-4" />

              {/* Price Breakdown */}
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span>Subtotal ({cartItems.length} items)</span>
                  <span>${subtotal.toFixed(2)}</span>
                </div>

                {discountAmount > 0 && (
                  <div className="flex justify-between text-green-600">
                    <span>Discount ({appliedCoupon})</span>
                    <span>-${discountAmount.toFixed(2)}</span>
                  </div>
                )}

                <div className="flex justify-between">
                  <span>Shipping</span>
                  <span>
                    {shippingCost === 0 ? (
                      <span className="text-green-600">Free</span>
                    ) : (
                      `$${shippingCost.toFixed(2)}`
                    )}
                  </span>
                </div>

                <div className="flex justify-between">
                  <span>Tax (8.75%)</span>
                  <span>${taxAmount.toFixed(2)}</span>
                </div>

                <Separator />

                <div className="flex justify-between text-lg font-semibold">
                  <span>Total</span>
                  <span>${total.toFixed(2)}</span>
                </div>
              </div>

              <Separator className="my-4" />

              {/* Checkout Actions */}
              <div className="space-y-3">
                <Button size="lg" className="w-full" asChild>
                  <Link to="/checkout">
                    <CreditCard className="w-5 h-5 mr-2" />
                    Proceed to Checkout
                    <ArrowRight className="w-4 h-4 ml-2" />
                  </Link>
                </Button>

                {/* Payment Method Preview */}
                <div className="p-3 bg-muted rounded-lg">
                  <div className="flex items-center justify-between text-sm">
                    <div className="flex items-center space-x-2">
                      <Shield className="w-4 h-4 text-green-600" />
                      <span>Secure Checkout</span>
                    </div>
                    <div className="flex items-center space-x-2">
                      <span>256-bit SSL</span>
                    </div>
                  </div>
                </div>

                <Button variant="outline" size="lg" className="w-full" asChild>
                  <Link to="/products">
                    Continue Shopping
                  </Link>
                </Button>
              </div>

              {/* Features */}
              <div className="grid grid-cols-1 gap-3 mt-6 p-4 bg-muted rounded-lg">
                <div className="flex items-center space-x-2 text-sm">
                  <Truck className="w-4 h-4 text-primary" />
                  <span>Free shipping on orders over $50</span>
                </div>

                <div className="flex items-center space-x-2 text-sm">
                  <Shield className="w-4 h-4 text-primary" />
                  <span>30-day money-back guarantee</span>
                </div>

                <div className="flex items-center space-x-2 text-sm">
                  <Brain className="w-4 h-4 text-primary" />
                  <span>AI-powered fraud protection</span>
                </div>
              </div>
            </Card>

            {/* Processing Metrics Detail */}
            {processingMetrics && (
              <Card className="p-6">
                <h3 className="font-semibold mb-4 flex items-center space-x-2">
                  <Calculator className="w-4 h-4" />
                  <span>Processing Metrics</span>
                </h3>

                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <div className="font-medium text-green-600">
                      {processingMetrics.taxCalculationTime}ms
                    </div>
                    <div className="text-muted-foreground">Tax Calculation</div>
                  </div>

                  <div>
                    <div className="font-medium text-blue-600">
                      {processingMetrics.shippingCalculationTime}ms
                    </div>
                    <div className="text-muted-foreground">Shipping</div>
                  </div>

                  <div>
                    <div className="font-medium text-purple-600">
                      {processingMetrics.discountProcessingTime}ms
                    </div>
                    <div className="text-muted-foreground">Discounts</div>
                  </div>

                  <div>
                    <div className="font-medium text-orange-600">
                      {processingMetrics.cacheHits}
                    </div>
                    <div className="text-muted-foreground">Cache Hits</div>
                  </div>
                </div>
              </Card>
            )}
          </div>
        </div>

        {/* Live Metrics for Performance Monitoring */}
        <div className="mt-12">
          <LiveMetrics />
        </div>
      </div>
    </div>
  );
}

