import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { Suspense, lazy } from 'react';

// Layout Components
import { MainLayout } from '../components/layout/MainLayout';
import { LoadingScreen } from '../components/ui/LoadingScreen';

// Lazy load pages for better performance with code splitting
const HomePage = lazy(() => import('../pages/HomePage').then(module => ({ default: module.HomePage })));
const ProductsPage = lazy(() => import('../pages/ProductsPage').then(module => ({ default: module.ProductsPage })));
const ProductDetailPage = lazy(() => import('../pages/ProductDetailPage').then(module => ({ default: module.ProductDetailPage })));
const CartPage = lazy(() => import('../pages/CartPage').then(module => ({ default: module.CartPage })));
const CheckoutPage = lazy(() => import('../pages/CheckoutPage').then(module => ({ default: module.CheckoutPage })));

// Error Boundary for route-level error handling
import { ErrorBoundary } from '../components/ui/ErrorBoundary';

export function AppRouter() {
  return (
    <BrowserRouter>
      <ErrorBoundary>
        <MainLayout>
          <Suspense fallback={<LoadingScreen />}>
            <Routes>
              {/* Main Routes */}
              <Route path="/" element={<HomePage />} />
              <Route path="/products" element={<ProductsPage />} />
              <Route path="/products/:productId" element={<ProductDetailPage />} />
              <Route path="/cart" element={<CartPage />} />
              <Route path="/checkout" element={<CheckoutPage />} />

              {/* User Routes */}
              <Route path="/account" element={<Navigate to="/account/profile" replace />} />
              <Route path="/account/*" element={<AccountRoutes />} />

              {/* Legal & Info Pages */}
              <Route path="/about" element={<AboutPage />} />
              <Route path="/contact" element={<ContactPage />} />
              <Route path="/privacy" element={<PrivacyPage />} />
              <Route path="/terms" element={<TermsPage />} />
              <Route path="/help" element={<HelpPage />} />

              {/* Search */}
              <Route path="/search" element={<SearchResultsPage />} />

              {/* Category Pages */}
              <Route path="/categories/:categorySlug" element={<CategoryPage />} />

              {/* Admin Routes (protected) */}
              <Route path="/admin/*" element={<AdminRoutes />} />

              {/* 404 Page */}
              <Route path="*" element={<NotFoundPage />} />
            </Routes>
          </Suspense>
        </MainLayout>
      </ErrorBoundary>
    </BrowserRouter>
  );
}

// Nested route components for better organization
function AccountRoutes() {
  const ProfilePage = lazy(() => import('../pages/account/ProfilePage').then(module => ({ default: module.ProfilePage })));
  const OrdersPage = lazy(() => import('../pages/account/OrdersPage').then(module => ({ default: module.OrdersPage })));
  const FavoritesPage = lazy(() => import('../pages/account/FavoritesPage').then(module => ({ default: module.FavoritesPage })));
  const SettingsPage = lazy(() => import('../pages/account/SettingsPage').then(module => ({ default: module.SettingsPage })));

  return (
    <Suspense fallback={<LoadingScreen />}>
      <Routes>
        <Route path="profile" element={<ProfilePage />} />
        <Route path="orders" element={<OrdersPage />} />
        <Route path="orders/:orderId" element={<OrderDetailPage />} />
        <Route path="favorites" element={<FavoritesPage />} />
        <Route path="settings" element={<SettingsPage />} />
        <Route path="*" element={<Navigate to="/account/profile" replace />} />
      </Routes>
    </Suspense>
  );
}

function AdminRoutes() {
  const AdminDashboard = lazy(() => import('../pages/admin/AdminDashboard').then(module => ({ default: module.AdminDashboard })));
  const ProductsAdmin = lazy(() => import('../pages/admin/ProductsAdmin').then(module => ({ default: module.ProductsAdmin })));
  const OrdersAdmin = lazy(() => import('../pages/admin/OrdersAdmin').then(module => ({ default: module.OrdersAdmin })));
  const AnalyticsAdmin = lazy(() => import('../pages/admin/AnalyticsAdmin').then(module => ({ default: module.AnalyticsAdmin })));

  return (
    <Suspense fallback={<LoadingScreen />}>
      <Routes>
        <Route path="/" element={<AdminDashboard />} />
        <Route path="products" element={<ProductsAdmin />} />
        <Route path="orders" element={<OrdersAdmin />} />
        <Route path="analytics" element={<AnalyticsAdmin />} />
        <Route path="*" element={<Navigate to="/admin" replace />} />
      </Routes>
    </Suspense>
  );
}

// Simple placeholder components (to be implemented later)
function AboutPage() {
  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-3xl font-bold mb-6">About Katalyst E-Commerce</h1>
      <div className="prose max-w-none">
        <p className="text-lg text-muted-foreground leading-relaxed">
          Experience the future of online shopping with our multithreaded, AI-powered e-commerce platform 
          built on the Katalyst framework.
        </p>
      </div>
    </div>
  );
}

function ContactPage() {
  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-3xl font-bold mb-6">Contact Us</h1>
      <p className="text-muted-foreground">Get in touch with our team for support or questions.</p>
    </div>
  );
}

function PrivacyPage() {
  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-3xl font-bold mb-6">Privacy Policy</h1>
      <p className="text-muted-foreground">Your privacy is important to us.</p>
    </div>
  );
}

function TermsPage() {
  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-3xl font-bold mb-6">Terms of Service</h1>
      <p className="text-muted-foreground">Terms and conditions for using our platform.</p>
    </div>
  );
}

function HelpPage() {
  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-3xl font-bold mb-6">Help Center</h1>
      <p className="text-muted-foreground">Find answers to common questions and get support.</p>
    </div>
  );
}

function SearchResultsPage() {
  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-3xl font-bold mb-6">Search Results</h1>
      <p className="text-muted-foreground">Search functionality powered by multithreaded processing.</p>
    </div>
  );
}

function CategoryPage() {
  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-3xl font-bold mb-6">Category</h1>
      <p className="text-muted-foreground">Browse products in this category.</p>
    </div>
  );
}

function OrderDetailPage() {
  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-3xl font-bold mb-6">Order Details</h1>
      <p className="text-muted-foreground">Detailed order information and tracking.</p>
    </div>
  );
}

function NotFoundPage() {
  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="text-center space-y-6">
        <div className="text-6xl">404</div>
        <h1 className="text-3xl font-bold">Page Not Found</h1>
        <p className="text-muted-foreground max-w-md">
          The page you're looking for doesn't exist or has been moved.
        </p>
        <div className="space-x-4">
          <a href="/" className="inline-flex items-center justify-center px-4 py-2 bg-primary text-primary-foreground rounded-lg hover:bg-primary/90">
            Go Home
          </a>
          <a href="/products" className="inline-flex items-center justify-center px-4 py-2 border border-input rounded-lg hover:bg-accent">
            Browse Products
          </a>
        </div>
      </div>
    </div>
  );
}

