import { Component, ErrorInfo, ReactNode } from 'react';
import { AlertTriangle, RefreshCw, Home, Bug } from 'lucide-react';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
}

interface State {
  hasError: boolean;
  error?: Error;
  errorInfo?: ErrorInfo;
}

export class ErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error: Error): State {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    this.setState({ error, errorInfo });

    // Log error for monitoring (in production, send to error tracking service)
    console.error('ErrorBoundary caught an error:', error, errorInfo);

    // Report to analytics/monitoring service
    if (typeof window !== 'undefined' && (window as any).gtag) {
      (window as any).gtag('event', 'exception', {
        description: error.toString(),
        fatal: false,
      });
    }
  }

  handleReload = () => {
    window.location.reload();
  };

  handleGoHome = () => {
    window.location.href = '/';
  };

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <div className="min-h-screen bg-background flex items-center justify-center p-4">
          <div className="max-w-2xl w-full text-center space-y-8">
            {/* Error Icon */}
            <div className="flex justify-center">
              <div className="w-24 h-24 rounded-full bg-destructive/10 flex items-center justify-center">
                <AlertTriangle className="w-12 h-12 text-destructive" />
              </div>
            </div>

            {/* Error Message */}
            <div className="space-y-4">
              <h1 className="text-3xl font-bold">Something went wrong</h1>
              <p className="text-muted-foreground text-lg">
                We encountered an unexpected error in the application. 
                Our multithreaded system is working to resolve this issue.
              </p>
            </div>

            {/* Error Details (Development Only) */}
            {process.env.NODE_ENV === 'development' && this.state.error && (
              <div className="text-left bg-muted rounded-lg p-6 space-y-4">
                <h3 className="font-semibold flex items-center space-x-2">
                  <Bug className="w-4 h-4" />
                  <span>Error Details (Development)</span>
                </h3>

                <div className="space-y-2">
                  <div>
                    <h4 className="font-medium text-sm text-destructive">Error Message:</h4>
                    <p className="text-sm font-mono bg-destructive/10 p-2 rounded">
                      {this.state.error.message}
                    </p>
                  </div>

                  <div>
                    <h4 className="font-medium text-sm text-destructive">Stack Trace:</h4>
                    <pre className="text-xs font-mono bg-destructive/10 p-2 rounded overflow-auto max-h-48">
                      {this.state.error.stack}
                    </pre>
                  </div>

                  {this.state.errorInfo && (
                    <div>
                      <h4 className="font-medium text-sm text-destructive">Component Stack:</h4>
                      <pre className="text-xs font-mono bg-destructive/10 p-2 rounded overflow-auto max-h-48">
                        {this.state.errorInfo.componentStack}
                      </pre>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Recovery Actions */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button
                onClick={this.handleReload}
                className="inline-flex items-center justify-center px-6 py-3 bg-primary text-primary-foreground rounded-lg hover:bg-primary/90 transition-colors"
              >
                <RefreshCw className="w-4 h-4 mr-2" />
                Reload Page
              </button>

              <button
                onClick={this.handleGoHome}
                className="inline-flex items-center justify-center px-6 py-3 border border-input rounded-lg hover:bg-accent transition-colors"
              >
                <Home className="w-4 h-4 mr-2" />
                Go to Homepage
              </button>
            </div>

            {/* Help Information */}
            <div className="text-center space-y-2">
              <p className="text-sm text-muted-foreground">
                If this error persists, please contact our support team.
              </p>
              <div className="flex justify-center space-x-4 text-xs text-muted-foreground">
                <span>Error ID: {Date.now()}</span>
                <span>•</span>
                <span>Katalyst Framework v1.0.0</span>
                <span>•</span>
                <span>Multithreaded Processing: Active</span>
              </div>
            </div>

            {/* Framework Features Still Working */}
            <div className="bg-muted/50 rounded-lg p-4">
              <h3 className="font-medium text-sm mb-3">System Status</h3>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-xs">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span>Threading Engine</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span>AI Services</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span>Security Layer</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-yellow-500 rounded-full"></div>
                  <span>UI Components</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

