import { useState } from 'react';
import { Link } from 'react-router-dom';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@katalyst/design-system/ui/avatar';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@katalyst/design-system/ui/dropdown-menu';
import { Badge } from '@katalyst/design-system/ui/badge';

// Store
import { useStorefrontStore } from '../../store/storefront-store';

// Icons
import { 
  User, Settings, Heart, Package, CreditCard, 
  LogOut, Shield, Bell, ChevronDown 
} from 'lucide-react';

export function UserMenu() {
  const [isOpen, setIsOpen] = useState(false);

  // Store
  const { user } = useStorefrontStore();

  // Mock user data (in production this would come from auth)
  const mockUser = {
    isAuthenticated: false,
    name: 'John Doe',
    email: 'john@example.com',
    avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=100&h=100&fit=crop&crop=face',
    orders: 12,
    favorites: 8
  };

  const isAuthenticated = user?.isAuthenticated || mockUser.isAuthenticated;

  if (!isAuthenticated) {
    return (
      <div className="flex items-center space-x-2">
        <Button variant="ghost" size="sm" asChild>
          <Link to="/auth/login">Sign In</Link>
        </Button>
        <Button size="sm" asChild>
          <Link to="/auth/register">Sign Up</Link>
        </Button>
      </div>
    );
  }

  return (
    <DropdownMenu open={isOpen} onOpenChange={setIsOpen}>
      <DropdownMenuTrigger asChild>
        <Button variant="ghost" className="relative h-8 w-8 rounded-full">
          <Avatar className="h-8 w-8">
            <AvatarImage src={mockUser.avatar} alt={mockUser.name} />
            <AvatarFallback>
              {mockUser.name.split(' ').map(n => n[0]).join('')}
            </AvatarFallback>
          </Avatar>
        </Button>
      </DropdownMenuTrigger>

      <DropdownMenuContent className="w-80" align="end" forceMount>
        {/* User Info Header */}
        <DropdownMenuLabel className="font-normal">
          <div className="flex flex-col space-y-2">
            <div className="flex items-center space-x-3">
              <Avatar className="h-10 w-10">
                <AvatarImage src={mockUser.avatar} alt={mockUser.name} />
                <AvatarFallback>
                  {mockUser.name.split(' ').map(n => n[0]).join('')}
                </AvatarFallback>
              </Avatar>
              <div className="flex-1">
                <p className="text-sm font-medium leading-none">{mockUser.name}</p>
                <p className="text-xs leading-none text-muted-foreground mt-1">
                  {mockUser.email}
                </p>
              </div>
            </div>

            {/* Quick Stats */}
            <div className="flex space-x-4 text-xs text-muted-foreground">
              <div className="flex items-center space-x-1">
                <Package className="w-3 h-3" />
                <span>{mockUser.orders} orders</span>
              </div>
              <div className="flex items-center space-x-1">
                <Heart className="w-3 h-3" />
                <span>{mockUser.favorites} favorites</span>
              </div>
            </div>
          </div>
        </DropdownMenuLabel>

        <DropdownMenuSeparator />

        {/* Account Navigation */}
        <DropdownMenuItem asChild>
          <Link to="/account/profile" className="w-full flex items-center">
            <User className="mr-3 h-4 w-4" />
            <span>Profile</span>
          </Link>
        </DropdownMenuItem>

        <DropdownMenuItem asChild>
