import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Separator } from '@katalyst/design-system/ui/separator';
import { Badge } from '@katalyst/design-system/ui/badge';

// Icons
import { X, Plus, Minus, Trash2, ShoppingBag, ArrowRight } from 'lucide-react';

interface CartDrawerProps {
  isOpen: boolean;
  onClose: () => void;
}

export function CartDrawer({ isOpen, onClose }: CartDrawerProps) {
  // Mock cart items
  const cartItems = [
    {
      id: '1',
      title: 'High-Performance Gaming Laptop',
      price: 1299.99,
      quantity: 1,
      image: 'https://images.unsplash.com/photo-1603302576837-37561b2e2302?w=100&h=100&fit=crop'
    },
    {
      id: '2',  
      title: 'AI-Powered Smart Watch',
      price: 349.99,
      quantity: 2,
      image: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=100&h=100&fit=crop'
    }
  ];

  const subtotal = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const shipping = 15.99;
  const total = subtotal + shipping;

  return (
    <AnimatePresence>
      {isOpen && (
        <>
          {/* Backdrop */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/50 z-50"
            onClick={onClose}
          />

          {/* Cart Drawer */}
          <motion.div
            initial={{ x: '100%' }}
            animate={{ x: 0 }}
            exit={{ x: '100%' }}
            transition={{ type: 'tween', duration: 0.3 }}
            className="fixed right-0 top-0 h-full w-96 bg-background border-l shadow-lg z-50 flex flex-col"
          >
            {/* Header */}
            <div className="flex items-center justify-between p-4 border-b">
              <div className="flex items-center space-x-2">
                <ShoppingBag className="w-5 h-5" />
                <h2 className="font-semibold">Shopping Cart</h2>
                <Badge>{cartItems.length}</Badge>
              </div>
              <Button variant="ghost" size="sm" onClick={onClose}>
                <X className="w-5 h-5" />
              </Button>
            </div>

            {/* Cart Items */}
            <div className="flex-1 overflow-y-auto p-4">
              {cartItems.length === 0 ? (
                <div className="text-center py-12 space-y-4">
                  <ShoppingBag className="w-12 h-12 text-muted-foreground mx-auto" />
                  <p className="text-muted-foreground">Your cart is empty</p>
                  <Button onClick={onClose} asChild>
                    <Link to="/products">Start Shopping</Link>
                  </Button>
                </div>
              ) : (
                <div className="space-y-4">
                  {cartItems.map((item) => (
                    <div key={item.id} className="flex space-x-4 p-4 border rounded-lg">
                      <img
                        src={item.image}
                        alt={item.title}
                        className="w-16 h-16 object-cover rounded"
                      />

                      <div className="flex-1 space-y-2">
                        <h4 className="font-medium text-sm leading-tight">
                          {item.title}
                        </h4>

                        <div className="flex items-center justify-between">
                          <span className="font-semibold">
                            ${item.price.toFixed(2)}
                          </span>

                          <div className="flex items-center space-x-2">
