  const trendingSearches: SearchSuggestion[] = [
    { query: 'AI development tools', type: 'trending', count: 1240 },
    { query: 'multithreaded software', type: 'trending', count: 890 },
    { query: 'performance monitoring', type: 'trending', count: 567 }
  ];

  const aiSuggestions: SearchSuggestion[] = [
    { query: 'products similar to your recent views', type: 'ai' },
    { query: 'trending in your favorite categories', type: 'ai' },
    { query: 'recommended for developers', type: 'ai' }
  ];

  // Multithreaded search
  const performSearch = useCallback(async (query: string) => {
    if (!query.trim()) {
      setSearchResults([]);
      return;
    }

    setIsSearching(true);

    try {
      // Use multithreading for search
      const result = await executeTask({
        id: `search-modal-${Date.now()}`,
        operation: 'search_products_modal',
        data: {
          query: query.trim(),
          limit: 6,
          includeAISuggestions: isAIEnabled,
          includePersonalization: true
        },
        priority: 'high',
        status: 'pending'
      });

      // Mock search results for now
      const mockResults: SearchResult[] = [
        {
          id: '1',
          title: 'High-Performance Gaming Laptop',
          description: 'Advanced multithreading capabilities for gaming and development',
          price: 1299.99,
          image: 'https://images.unsplash.com/photo-1603302576837-37561b2e2302?w=100&h=100&fit=crop',
          category: 'Electronics',
          rating: 4.8
        },
        {
          id: '2',
          title: 'AI-Powered Smart Watch',
          description: 'Real-time health monitoring with edge AI processing',
          price: 349.99,
          image: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=100&h=100&fit=crop',
          category: 'Wearables',
          rating: 4.6
        },
        {
          id: '3',
          title: 'Professional Development Course',
          description: 'Learn modern web development with multithreading',
          price: 199.99,
          image: 'https://images.unsplash.com/photo-1516321318423-f06f85e504b3?w=100&h=100&fit=crop',
          category: 'Education',
          rating: 4.9
        }
      ].filter(item => 
        item.title.toLowerCase().includes(query.toLowerCase()) ||
        item.description.toLowerCase().includes(query.toLowerCase())
      );

      setSearchResults(mockResults);
    } catch (error) {
      console.error('Search failed:', error);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  }, [executeTask, isAIEnabled]);

  // Debounced search
  useEffect(() => {
    const debounce = setTimeout(() => {
      performSearch(searchQuery);
    }, 300);

    return () => clearTimeout(debounce);
  }, [searchQuery, performSearch]);

  // Focus input when modal opens
  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  // Keyboard navigation
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'ArrowDown') {
      e.preventDefault();
      setSelectedIndex(prev => Math.min(prev + 1, searchResults.length - 1));
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      setSelectedIndex(prev => Math.max(prev - 1, 0));
    } else if (e.key === 'Enter') {
      e.preventDefault();
      if (searchResults[selectedIndex]) {
        handleResultClick(searchResults[selectedIndex]);
      } else if (searchQuery.trim()) {
        handleSearchSubmit();
      }
    } else if (e.key === 'Escape') {
      onClose();
    }
  };

  const handleResultClick = (result: SearchResult) => {
    // Track search click
    const searchClicks = JSON.parse(localStorage.getItem('search-clicks') || '[]');
    searchClicks.push({
      query: searchQuery,
      resultId: result.id,
      timestamp: Date.now()
    });
    localStorage.setItem('search-clicks', JSON.stringify(searchClicks));

    onClose();
  };

  const handleSearchSubmit = () => {
    if (searchQuery.trim()) {
      // Track search
      const searches = JSON.parse(localStorage.getItem('search-history') || '[]');
      searches.unshift({
        query: searchQuery.trim(),
        timestamp: Date.now()
      });
      localStorage.setItem('search-history', JSON.stringify(searches.slice(0, 10)));

      onClose();
      // Navigate to search results
      window.location.href = `/products?q=${encodeURIComponent(searchQuery)}`;
    }
  };

  const handleSuggestionClick = (suggestion: SearchSuggestion) => {
    setSearchQuery(suggestion.query);
    performSearch(suggestion.query);
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <>
          {/* Backdrop */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/50 z-50"
            onClick={onClose}
          />

          {/* Search Modal */}
          <motion.div
            initial={{ opacity: 0, y: -100, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: -100, scale: 0.95 }}
            transition={{ type: 'tween', duration: 0.2 }}
            className="fixed top-20 left-1/2 transform -translate-x-1/2 w-full max-w-2xl bg-background border shadow-2xl rounded-lg z-50 max-h-[80vh] overflow-hidden"
          >
            {/* Search Header */}
            <div className="flex items-center space-x-3 p-4 border-b">
              <Search className="w-5 h-5 text-muted-foreground" />
              <Input
                ref={inputRef}
                placeholder="Search products with AI assistance..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyDown={handleKeyDown}
                className="border-0 focus-visible:ring-0 text-lg"
              />

              <div className="flex items-center space-x-2">
                {isAIEnabled && (
                  <Badge variant="outline" className="text-xs">
                    <Brain className="w-3 h-3 mr-1" />
                    AI
                  </Badge>
                )}
                <Button variant="ghost" size="sm" onClick={onClose}>
                  <X className="w-5 h-5" />
                </Button>
              </div>
            </div>

            {/* Search Content */}
            <div className="max-h-96 overflow-y-auto">
              {/* Loading State */}
              {isSearching && (
                <div className="flex items-center justify-center py-12">
                  <div className="flex items-center space-x-3">
                    <div className="animate-spin rounded-full h-5 w-5 border-2 border-primary border-t-transparent" />
                    <span className="text-muted-foreground">Searching with AI...</span>
                  </div>
                </div>
              )}

              {/* Search Results */}
              {!isSearching && searchResults.length > 0 && (
                <div className="p-2">
                  <div className="text-sm text-muted-foreground px-3 py-2 mb-2">
                    Search Results ({searchResults.length})
                  </div>
                  {searchResults.map((result, index) => (
                    <Link
                      key={result.id}
                      to={`/products/${result.id}`}
                      onClick={() => handleResultClick(result)}
                      className={`flex items-center space-x-3 p-3 rounded-lg hover:bg-accent transition-colors ${
                        index === selectedIndex ? 'bg-accent' : ''
                      }`}
                    >
                      <img
                        src={result.image}
                        alt={result.title}
                        className="w-12 h-12 object-cover rounded"
                      />
                      <div className="flex-1 min-w-0">
                        <h4 className="font-medium truncate">{result.title}</h4>
                        <p className="text-sm text-muted-foreground truncate">
                          {result.description}
                        </p>
                        <div className="flex items-center space-x-2 mt-1">
                          <Badge variant="outline" className="text-xs">
                            {result.category}
                          </Badge>
                          {result.rating && (
                            <div className="flex items-center space-x-1 text-xs">
                              <Star className="w-3 h-3 fill-current text-yellow-400" />
                              <span>{result.rating}</span>
                            </div>
                          )}
                        </div>
                      </div>
                      {result.price && (
                        <div className="text-right">
                          <div className="font-semibold">${result.price.toFixed(2)}</div>
                        </div>
                      )}
                    </Link>
                  ))}

                  {/* View All Results */}
                  <div className="p-3">
                    <Button
                      variant="outline"
                      className="w-full"
                      onClick={handleSearchSubmit}
                    >
                      View all results for "{searchQuery}"
                      <ArrowRight className="w-4 h-4 ml-2" />
                    </Button>
                  </div>
                </div>
              )}

              {/* No Results */}
              {!isSearching && searchQuery && searchResults.length === 0 && (
                <div className="text-center py-12 space-y-4">
                  <Search className="w-12 h-12 text-muted-foreground mx-auto" />
                  <div>
                    <h3 className="font-medium mb-2">No results found</h3>
                    <p className="text-sm text-muted-foreground">
                      Try searching for something else or browse our categories
                    </p>
                  </div>
                  <Button onClick={() => window.location.href = '/products'}>
                    Browse All Products
                  </Button>
                </div>
              )}

              {/* Suggestions (when no search query) */}
              {!searchQuery && (
                <div className="p-4 space-y-6">
                  {/* Recent Searches */}
                  {recentSearches.length > 0 && (
                    <div>
                      <div className="flex items-center space-x-2 mb-3">
                        <Clock className="w-4 h-4 text-muted-foreground" />
                        <span className="text-sm font-medium">Recent Searches</span>
                      </div>
                      <div className="space-y-1">
                        {recentSearches.map((search, index) => (
                          <button
                            key={index}
                            onClick={() => handleSuggestionClick(search)}
                            className="flex items-center space-x-3 p-2 rounded-lg hover:bg-accent transition-colors w-full text-left"
                          >
                            <Search className="w-4 h-4 text-muted-foreground" />
                            <span className="text-sm">{search.query}</span>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Trending Searches */}
                  <div>
                    <div className="flex items-center space-x-2 mb-3">
                      <TrendingUp className="w-4 h-4 text-muted-foreground" />
                      <span className="text-sm font-medium">Trending Searches</span>
                    </div>
                    <div className="space-y-1">
                      {trendingSearches.map((search, index) => (
                        <button
                          key={index}
                          onClick={() => handleSuggestionClick(search)}
                          className="flex items-center justify-between p-2 rounded-lg hover:bg-accent transition-colors w-full text-left"
                        >
                          <div className="flex items-center space-x-3">
                            <TrendingUp className="w-4 h-4 text-orange-500" />
                            <span className="text-sm">{search.query}</span>
                          </div>
                          {search.count && (
                            <Badge variant="secondary" className="text-xs">
                              {search.count}
                            </Badge>
                          )}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* AI Suggestions */}
                  {isAIEnabled && (
                    <div>
                      <div className="flex items-center space-x-2 mb-3">
                        <Brain className="w-4 h-4 text-muted-foreground" />
                        <span className="text-sm font-medium">AI Suggestions</span>
                        <Badge variant="outline" className="text-xs">
                          <Zap className="w-3 h-3 mr-1" />
                          Smart
                        </Badge>
                      </div>
                      <div className="space-y-1">
                        {aiSuggestions.map((search, index) => (
                          <button
                            key={index}
                            onClick={() => handleSuggestionClick(search)}
                            className="flex items-center space-x-3 p-2 rounded-lg hover:bg-accent transition-colors w-full text-left"
                          >
                            <Brain className="w-4 h-4 text-purple-500" />
                            <span className="text-sm">{search.query}</span>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Search Footer */}
            <div className="border-t p-3">
              <div className="flex items-center justify-between text-xs text-muted-foreground">
                <div className="flex items-center space-x-4">
                  <div className="flex items-center space-x-1">
                    <Command className="w-3 h-3" />
                    <span>↵ to search</span>
                  </div>
                  <div>↑↓ to navigate</div>
                  <div>esc to close</div>
                </div>

                <div className="flex items-center space-x-1">
                  <Zap className="w-3 h-3 text-primary" />
                  <span>Powered by Katalyst AI</span>
                </div>
              </div>
            </div>
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
}

