import './App.css'

// Katalyst Providers
import { KatalystProvider } from '@katalyst/core/providers/KatalystProvider'
import { MultithreadingProvider } from '@katalyst/core/providers/MultithreadingProvider'
import { UnifiedRuntimeProvider } from '@katalyst/core/providers/UnifiedRuntimeProvider'
import { AIProvider } from '@katalyst/ai/providers/AIProvider'
import { PaymentsProvider } from '@katalyst/payments/providers/PaymentsProvider'
import { SecurityAIProvider } from '@katalyst/security-ai/providers/SecurityAIProvider'
import { PWAProvider } from '@katalyst/pwa/providers/PWAProvider'

// Router
import { AppRouter } from './router/AppRouter'

// Error Boundary
import { ErrorBoundary } from './components/ui/ErrorBoundary'

function App() {
  return (
    <ErrorBoundary>
      <KatalystProvider config={{
        multithreading: {
          enabled: true,
          defaultPoolType: 'rayon',
          maxWorkers: navigator.hardwareConcurrency || 4,
          enablePerformanceMonitoring: true
        },
        ai: {
          provider: 'cloudflare',
          models: ['@cf/meta/llama-3.1-8b-instruct'],
          enableRecommendations: true,
          contextWindowSize: 8192
        },
        payments: {
          providers: ['stripe', 'paypal'],
          enableFraudDetection: true,
          securityLevel: 'enhanced'
        },
        security: {
          enableAISecurity: true,
          realTimeMonitoring: true,
          threatDetection: 'advanced'
        },
        pwa: {
          enableOffline: true,
          cacheStrategy: 'networkFirst',
          updateStrategy: 'automatic'
        },
        performance: {
          enableMetrics: true,
          enableTracing: true,
          sampleRate: 1.0
        }
      }}>
        <MultithreadingProvider>
          <UnifiedRuntimeProvider>
            <AIProvider>
              <PaymentsProvider>
                <SecurityAIProvider>
                  <PWAProvider>
                    <AppRouter />
                  </PWAProvider>
                </SecurityAIProvider>
              </PaymentsProvider>
            </AIProvider>
          </UnifiedRuntimeProvider>
        </MultithreadingProvider>
      </KatalystProvider>
    </ErrorBoundary>
  )
}

export default App

