import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { ReactQueryDevtools } from '@tanstack/react-query-devtools';

// Katalyst Core Imports
import { KatalystProvider } from '@katalyst/core/components/KatalystProvider';
import { MultithreadingProvider } from '@katalyst/core/components/MultithreadingProvider';
import { UnifiedRuntimeProvider } from '@katalyst/core/components/UnifiedRuntimeProvider';

// Katalyst Feature Imports
import { PaymentProvider } from '@katalyst/payments/payments/providers/PaymentProvider';
import { PWAProvider } from '@katalyst/pwa/core/PWAProvider';
import { SecurityAIProvider } from '@katalyst/security-ai/components/SecurityAIProvider';

// Design System
import { KatalystDesignSystem } from '@katalyst/design-system/themes/KatalystDesignSystem';
import { ThemeProvider } from '@katalyst/design-system/themes/theme-provider';

// Storefront Pages
import { HomePage } from './pages/HomePage';
import { ProductsPage } from './pages/ProductsPage';
import { ProductDetailPage } from './pages/ProductDetailPage';
import { CartPage } from './pages/CartPage';
import { CheckoutPage } from './pages/CheckoutPage';
import { AccountPage } from './pages/AccountPage';
import { SearchPage } from './pages/SearchPage';

// Shared Components
import { Navigation } from './components/Navigation';
import { Footer } from './components/Footer';
import { AIAssistant } from './components/AIAssistant';
import { PerformanceMonitor } from './components/PerformanceMonitor';

// Hooks
import { useKatalyst } from '@katalyst/hooks/use-katalyst';
import { useMultithreading } from '@katalyst/hooks/use-multithreading';
import { usePWA } from '@katalyst/hooks/use-pwa';

// Store
import { useStorefrontStore } from './store/storefront-store';

// Create QueryClient with optimized config for multithreading
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      staleTime: 1000 * 60 * 5, // 5 minutes
      cacheTime: 1000 * 60 * 30, // 30 minutes
      retry: 3,
      refetchOnWindowFocus: false,
    },
    mutations: {
      retry: 1,
    },
  },
});

function AppContent() {
  const { isInitialized, metrics } = useKatalyst();
  const { threadPools, executingTasks } = useMultithreading();
  const { isInstalled, promptInstall } = usePWA();
  const { initializeStore } = useStorefrontStore();

  // Initialize store with Katalyst capabilities
  React.useEffect(() => {
    if (isInitialized) {
      initializeStore({
        multithreading: {
          enabled: true,
          preferredPool: 'rayon'
        },
        ai: {
          enabled: true,
          provider: 'cloudflare'
        },
        payments: {
          providers: ['stripe', 'paypal', 'crypto']
        }
      });
    }
  }, [isInitialized, initializeStore]);

  return (
    <div className="min-h-screen bg-background">
      {/* PWA Install Prompt */}
      {!isInstalled && (
        <div className="bg-primary text-primary-foreground p-2 text-center">
          <button onClick={promptInstall} className="text-sm underline">
            Install Katalyst Storefront for the best experience
          </button>
        </div>
      )}

      {/* Performance Monitoring */}
      <PerformanceMonitor />

      {/* Main Application */}
      <Navigation />

      <main className="container mx-auto px-4 py-8">
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/products" element={<ProductsPage />} />
          <Route path="/products/:id" element={<ProductDetailPage />} />
          <Route path="/search" element={<SearchPage />} />
          <Route path="/cart" element={<CartPage />} />
          <Route path="/checkout" element={<CheckoutPage />} />
          <Route path="/account" element={<AccountPage />} />
        </Routes>
      </main>

      <Footer />

      {/* AI Assistant - Available on all pages */}
      <AIAssistant />

      {/* Development Tools */}
      {import.meta.env.DEV && (
        <div className="fixed bottom-4 right-4 z-50">
          <div className="bg-black/80 text-white p-2 rounded text-xs">
            <div>Threads: {threadPools.length}</div>
            <div>Tasks: {executingTasks}</div>
            <div>Performance: {metrics?.averageResponseTime}ms</div>
          </div>
        </div>
      )}
    </div>
  );
}

export default function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <KatalystProvider
        config={{
          multithreading: {
            enabled: true,
            defaultPoolType: 'rayon',
            maxWorkers: navigator.hardwareConcurrency || 4
          },
          ai: {
            provider: 'cloudflare',
            models: ['@cf/meta/llama-3.1-8b-instruct'],
            features: ['recommendations', 'search', 'support']
          },
          performance: {
            monitoring: true,
            optimization: 'balanced'
          }
        }}
      >
        <MultithreadingProvider>
          <UnifiedRuntimeProvider>
            <ThemeProvider defaultTheme="system" storageKey="katalyst-storefront-theme">
              <KatalystDesignSystem>
                <PaymentProvider
                  providers={{
                    stripe: { publishableKey: import.meta.env.VITE_STRIPE_PUBLISHABLE_KEY },
                    paypal: { clientId: import.meta.env.VITE_PAYPAL_CLIENT_ID },
                    crypto: { networks: ['ethereum', 'polygon'] }
                  }}
                >
                  <PWAProvider>
                    <SecurityAIProvider>
                      <Router>
                        <AppContent />
                      </Router>
                    </SecurityAIProvider>
                  </PWAProvider>
                </PaymentProvider>
              </KatalystDesignSystem>
            </ThemeProvider>
          </UnifiedRuntimeProvider>
        </MultithreadingProvider>
      </KatalystProvider>
      <ReactQueryDevtools initialIsOpen={false} />
    </QueryClientProvider>
  );
}

