import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';

// Katalyst Core Imports
import { useMultithreading } from '@katalyst/hooks/use-multithreading';
import { usePWA } from '@katalyst/hooks/use-pwa';
import { useAI } from '@katalyst/hooks/use-ai';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Input } from '@katalyst/design-system/ui/input';
import { Badge } from '@katalyst/design-system/ui/badge';

// Icons
import { 
  Zap, Search, ShoppingCart, Menu, User, Bell,
  Cpu, Brain, Shield, Smartphone, Globe,
  TrendingUp, Star, Heart
} from 'lucide-react';

// Layout Components
import { UserMenu } from './UserMenu';
import { SearchModal } from './SearchModal';
import { CartDrawer } from './CartDrawer';
import { MobileNav } from './MobileNav';

interface NavigationItem {
  title: string;
  href: string;
  items: Array<{
    title: string;
    href: string;
    description: string;
  }>;
}

interface HeaderProps {
  onMobileMenuToggle: () => void;
}

export function Header({ onMobileMenuToggle }: HeaderProps) {
  const [searchModalOpen, setSearchModalOpen] = useState(false);
  const [cartDrawerOpen, setCartDrawerOpen] = useState(false);
  const [mobileNavOpen, setMobileNavOpen] = useState(false);
  const [notificationsCount, setNotificationsCount] = useState(3);

  const location = useLocation();

  // Katalyst Hooks
  const { metrics, threadPools, executingTasks } = useMultithreading();
  const { isInstallable, isInstalled, promptInstall } = usePWA();
  const { isAIEnabled } = useAI();

  // Navigation configuration leveraging Katalyst features
  const navigation: NavigationItem[] = [
    {
      title: 'Products',
      href: '/products',
      items: [
        {
          title: 'AI Development Tools',
          href: '/products/category/ai-tools',
          description: 'AI-powered development environments and tools'
        },
        {
          title: 'Performance Software',
          href: '/products/category/performance',
          description: 'Multithreaded applications and performance tools'
        },
        {
          title: 'Security Solutions',
          href: '/products/category/security',
          description: 'AI-enhanced security and monitoring tools'
        },
        {
          title: 'PWA Applications',
          href: '/products/category/pwa',
          description: 'Progressive web applications and utilities'
        }
      ]
    },
    {
      title: 'Solutions',
      href: '/solutions',
      items: [
        {
          title: 'Enterprise AI',
          href: '/solutions/enterprise-ai',
          description: 'AI-powered solutions for enterprise development'
        },
        {
          title: 'Performance Optimization',
          href: '/solutions/performance',
          description: 'Multithreaded performance optimization services'
        },
        {
          title: 'Security Consulting',
          href: '/solutions/security',
          description: 'AI-enhanced security audit and consulting'
        },
        {
          title: 'Custom Development',
          href: '/solutions/custom',
          description: 'Custom Katalyst-powered application development'
        }
      ]
    },
    {
      title: 'Resources',
      href: '/resources',
      items: [
        {
          title: 'Documentation',
          href: '/docs',
          description: 'Complete Katalyst framework documentation'
        },
        {
          title: 'API Reference',
          href: '/api',
          description: 'Comprehensive API documentation'
        },
        {
          title: 'Tutorials',
          href: '/tutorials',
          description: 'Step-by-step guides and tutorials'
        },
        {
          title: 'Community',
          href: '/community',
          description: 'Join our developer community'
        }
      ]
    }
  ];

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.metaKey || e.ctrlKey) {
        switch (e.key) {
          case 'k':
            e.preventDefault();
            setSearchModalOpen(true);
            break;
          case 'b':
            e.preventDefault();
            setCartDrawerOpen(true);
            break;
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Update notifications count with multithreading
  useEffect(() => {
    const interval = setInterval(() => {
      // Simulate real-time notifications
      if (executingTasks > 5) {
        setNotificationsCount(prev => prev + 1);
      }
    }, 30000);

    return () => clearInterval(interval);
  }, [executingTasks]);

  return (
    <>
      <motion.header 
        className="sticky top-0 z-40 w-full border-b bg-background/80 backdrop-blur-lg"
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: 'spring', stiffness: 300, damping: 30 }}
      >
        <div className="container mx-auto px-4">
          <div className="flex h-16 items-center justify-between">
            {/* Logo and Brand */}
            <div className="flex items-center space-x-4">
              <Link to="/" className="flex items-center space-x-2">
                <div className="relative">
                  <Zap className="w-8 h-8 text-primary" />
                  <div className="absolute -top-1 -right-1 w-3 h-3 bg-gradient-to-r from-primary to-secondary rounded-full animate-pulse" />
                </div>
                <div className="hidden sm:block">
                  <h1 className="font-bold text-xl bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
                    Katalyst
                  </h1>
                  <p className="text-xs text-muted-foreground -mt-1">Storefront</p>
                </div>
              </Link>

              {/* Performance Indicators */}
              <div className="hidden lg:flex items-center space-x-2">
                <Badge variant="outline" className="text-xs">
                  <Cpu className="w-3 h-3 mr-1" />
                  {threadPools.length} Pools
                </Badge>
                {isAIEnabled && (
                  <Badge variant="outline" className="text-xs">
                    <Brain className="w-3 h-3 mr-1" />
                    AI Ready
                  </Badge>
                )}
                {metrics && (
                  <Badge variant="outline" className="text-xs">
                    <TrendingUp className="w-3 h-3 mr-1" />
                    {metrics.averageResponseTime || 0}ms
                  </Badge>
                )}
              </div>
            </div>

            {/* Desktop Navigation */}
            <nav className="hidden md:flex items-center space-x-6">
              {navigation.map((item) => (
                <div key={item.href} className="relative group">
                  <Link
                    to={item.href}
                    className={`text-sm font-medium transition-colors hover:text-primary ${
                      location.pathname.startsWith(item.href) 
                        ? 'text-primary' 
                        : 'text-muted-foreground'
                    }`}
                  >
                    {item.title}
                  </Link>

                  {/* Dropdown Menu */}
                  <div className="absolute left-0 top-full mt-2 w-80 bg-background border shadow-lg rounded-lg opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200 z-50">
                    <div className="p-4 space-y-3">
                      {item.items.map((subItem) => (
                        <Link
                          key={subItem.href}
                          to={subItem.href}
                          className="block p-3 rounded-lg hover:bg-accent transition-colors"
                        >
                          <div className="font-medium text-sm">{subItem.title}</div>
                          <div className="text-xs text-muted-foreground mt-1">
                            {subItem.description}
                          </div>
                        </Link>
                      ))}
                    </div>
                  </div>
                </div>
              ))}
            </nav>

            {/* Search Bar */}
            <div className="hidden lg:flex flex-1 max-w-md mx-8">
              <div className="relative w-full">
                <Input
                  placeholder="Search with AI assistance... (⌘K)"
                  className="pr-10 cursor-pointer"
                  onClick={() => setSearchModalOpen(true)}
                  readOnly
                />
                <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              </div>
            </div>

            {/* Actions */}
            <div className="flex items-center space-x-3">
              {/* Search (Mobile) */}
              <Button
                variant="ghost"
                size="sm"
                className="lg:hidden"
                onClick={() => setSearchModalOpen(true)}
              >
                <Search className="w-5 h-5" />
              </Button>

              {/* PWA Install */}
              {isInstallable && !isInstalled && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={promptInstall}
                  className="hidden sm:flex items-center space-x-1"
                >
                  <Smartphone className="w-4 h-4" />
                  <span className="text-xs">Install</span>
                </Button>
              )}

              {/* Notifications */}
              <Button variant="ghost" size="sm" className="relative">
                <Bell className="w-5 h-5" />
                {notificationsCount > 0 && (
                  <Badge className="absolute -top-1 -right-1 px-1 min-w-[1.25rem] h-5 text-xs">
                    {notificationsCount}
                  </Badge>
                )}
              </Button>

              {/* Favorites */}
              <Button variant="ghost" size="sm" className="hidden sm:flex">
                <Heart className="w-5 h-5" />
              </Button>

              {/* Cart */}
              <Button 
                variant="ghost" 
                size="sm" 
                className="relative"
                onClick={() => setCartDrawerOpen(true)}
              >
                <ShoppingCart className="w-5 h-5" />
                <Badge className="absolute -top-1 -right-1 px-1 min-w-[1.25rem] h-5 text-xs">
                  3
                </Badge>
              </Button>

              {/* User Menu */}
              <UserMenu />

              {/* Mobile Menu */}
              <Button
                variant="ghost"
                size="sm"
                className="md:hidden"
                onClick={() => setMobileNavOpen(true)}
              >
                <Menu className="w-5 h-5" />
              </Button>
            </div>
          </div>

          {/* Secondary Navigation Bar */}
          <div className="hidden xl:flex items-center justify-between py-2 border-t border-border/40">
            <div className="flex items-center space-x-6 text-xs text-muted-foreground">
              <div className="flex items-center space-x-1">
                <Shield className="w-3 h-3 text-green-500" />
                <span>Security: Enhanced</span>
              </div>
              <div className="flex items-center space-x-1">
                <Globe className="w-3 h-3 text-blue-500" />
                <span>CDN: Active</span>
              </div>
              <div className="flex items-center space-x-1">
                <Cpu className="w-3 h-3 text-orange-500" />
                <span>Tasks: {executingTasks}</span>
              </div>
              {isAIEnabled && (
                <div className="flex items-center space-x-1">
                  <Brain className="w-3 h-3 text-purple-500" />
                  <span>AI: Online</span>
                </div>
              )}
            </div>

            <div className="flex items-center space-x-4 text-xs">
              <Link to="/help" className="hover:text-primary transition-colors">
                Help Center
              </Link>
              <Link to="/contact" className="hover:text-primary transition-colors">
                Support
              </Link>
              <Link to="/status" className="flex items-center space-x-1 hover:text-primary transition-colors">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
                <span>Status</span>
              </Link>
            </div>
          </div>
        </div>
      </motion.header>

      {/* Modals and Drawers */}
      <SearchModal 
        isOpen={searchModalOpen} 
        onClose={() => setSearchModalOpen(false)} 
      />

      <CartDrawer 
        isOpen={cartDrawerOpen} 
        onClose={() => setCartDrawerOpen(false)} 
      />

      <MobileNav 
        isOpen={mobileNavOpen}
        onClose={() => setMobileNavOpen(false)}
        navigation={navigation}
      />
    </>
  );
}

