import { useMemo } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';

// Katalyst Core Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useAI } from '@katalyst/hooks/use-ai';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Badge } from '@katalyst/design-system/ui/badge';

// Icons
import { 
  ChevronRight, Home, Package, Search, User, 
  ShoppingCart, Settings, HelpCircle, Brain,
  Cpu, TrendingUp
} from 'lucide-react';

interface BreadcrumbItem {
  label: string;
  href: string;
  icon?: React.ComponentType<any>;
  isActive?: boolean;
  metadata?: {
    category?: string;
    id?: string;
    performance?: boolean;
  };
}

export function Breadcrumbs() {
  const location = useLocation();
  const { executeTask } = useMultithreadingStore();
  const { generateRecommendations, isAIEnabled } = useAI();

  // Enhanced breadcrumb generation using Katalyst's multithreading
  const breadcrumbs = useMemo(() => {
    const pathSegments = location.pathname.split('/').filter(Boolean);
    const crumbs: BreadcrumbItem[] = [];

    // Always start with home
    crumbs.push({
      label: 'Home',
      href: '/',
      icon: Home
    });

    // Process each path segment with enhanced context
    pathSegments.forEach((segment, index) => {
      const href = '/' + pathSegments.slice(0, index + 1).join('/');
      const isLast = index === pathSegments.length - 1;

      let crumb: BreadcrumbItem = {
        label: formatSegment(segment),
        href,
        isActive: isLast
      };

      // Enhanced segment processing with Katalyst context
      switch (segment) {
        case 'products':
          crumb = {
            ...crumb,
            label: 'Products',
            icon: Package,
            metadata: { category: 'catalog', performance: true }
          };
          break;

        case 'cart':
          crumb = {
            ...crumb,
            label: 'Shopping Cart',
            icon: ShoppingCart,
            metadata: { category: 'commerce' }
          };
          break;

        case 'search':
          crumb = {
            ...crumb,
            label: 'Search Results',
            icon: Search,
            metadata: { category: 'discovery', performance: true }
          };
          break;

        case 'account':
          crumb = {
            ...crumb,
            label: 'My Account',
            icon: User,
            metadata: { category: 'profile' }
          };
          break;

        case 'admin':
          crumb = {
            ...crumb,
            label: 'Admin Dashboard',
            icon: Settings,
            metadata: { category: 'administration', performance: true }
          };
          break;

        case 'help':
          crumb = {
            ...crumb,
            label: 'Help Center',
            icon: HelpCircle,
            metadata: { category: 'support' }
          };
          break;

        case 'category':
          // Handle dynamic category segments
          if (index < pathSegments.length - 1) {
            const categoryName = pathSegments[index + 1];
            crumb = {
              ...crumb,
              label: 'Category',
              metadata: { category: 'navigation' }
            };
          }
          break;

        default:
          // Handle dynamic segments (IDs, etc.)
          if (segment.length > 10 && /^[a-f0-9-]+$/i.test(segment)) {
            // Looks like a UUID or ID
            crumb = {
              ...crumb,
              label: `Item ${segment.slice(0, 8)}...`,
              metadata: { category: 'detail', id: segment }
            };
          } else if (/^\d+$/.test(segment)) {
            // Numeric ID
            crumb = {
              ...crumb,
              label: `#${segment}`,
              metadata: { category: 'detail', id: segment }
            };
          } else {
            // Regular segment - capitalize and clean
            crumb = {
              ...crumb,
              label: formatSegment(segment),
              metadata: { category: 'navigation' }
            };
          }
      }

      crumbs.push(crumb);
    });

    return crumbs;
  }, [location.pathname]);

  // Enhanced breadcrumb analytics using multithreading
  const logBreadcrumbNavigation = async (crumb: BreadcrumbItem) => {
    if (!crumb.isActive) {
      await executeTask({
        id: `breadcrumb-nav-${Date.now()}`,
        operation: 'log_breadcrumb_navigation',
        data: {
          from: location.pathname,
          to: crumb.href,
          label: crumb.label,
          category: crumb.metadata?.category,
          timestamp: Date.now(),
          performanceMode: crumb.metadata?.performance || false
        },
        priority: 'low',
        status: 'pending'
      });
    }
  };

  // AI-powered contextual suggestions
  const getContextualSuggestions = async () => {
    if (!isAIEnabled) return [];

    try {
      const currentContext = {
        path: location.pathname,
        breadcrumbs: breadcrumbs.map(b => ({ label: b.label, category: b.metadata?.category })),
        timestamp: Date.now()
      };

      return await generateRecommendations(currentContext, {
        type: 'navigation_suggestions',
        limit: 3
      });
    } catch (error) {
      console.error('Failed to generate contextual suggestions:', error);
      return [];
    }
  };

  const formatSegment = (segment: string): string => {
    return segment
      .split('-')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  if (breadcrumbs.length <= 1) {
    return null; // Don't show breadcrumbs on home page
  }

  return (
    <motion.div 
      className="border-b bg-muted/20"
      initial={{ opacity: 0, y: -10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.2 }}
    >
      <div className="container mx-auto px-4 py-3">
        <div className="flex items-center justify-between">
          {/* Breadcrumb Navigation */}
          <nav className="flex items-center space-x-1" aria-label="Breadcrumb">
            <ol className="flex items-center space-x-1">
              {breadcrumbs.map((crumb, index) => (
                <li key={crumb.href} className="flex items-center">
                  {index > 0 && (
                    <ChevronRight className="w-4 h-4 text-muted-foreground mx-2" />
                  )}

                  {crumb.isActive ? (
                    <div className="flex items-center space-x-2">
                      {crumb.icon && (
                        <crumb.icon className="w-4 h-4 text-primary" />
                      )}
                      <span className="font-medium text-foreground">
                        {crumb.label}
                      </span>

                      {/* Performance indicators for active items */}
                      {crumb.metadata?.performance && (
                        <div className="flex items-center space-x-1">
                          <Badge variant="outline" className="text-xs">
                            <Cpu className="w-3 h-3 mr-1" />
                            Optimized
                          </Badge>
                          {isAIEnabled && (
                            <Badge variant="outline" className="text-xs">
                              <Brain className="w-3 h-3 mr-1" />
                              AI
                            </Badge>
                          )}
                        </div>
                      )}
                    </div>
                  ) : (
                    <Link
                      to={crumb.href}
                      onClick={() => logBreadcrumbNavigation(crumb)}
                      className="flex items-center space-x-1 text-muted-foreground hover:text-primary transition-colors group"
                    >
                      {crumb.icon && (
                        <crumb.icon className="w-4 h-4 group-hover:text-primary transition-colors" />
                      )}
                      <span className="text-sm group-hover:underline">
                        {crumb.label}
                      </span>
                    </Link>
                  )}
                </li>
              ))}
            </ol>
          </nav>

          {/* Smart Actions */}
          <div className="flex items-center space-x-2">
            {/* Performance Mode Indicator */}
            {breadcrumbs.some(b => b.metadata?.performance) && (
              <div className="hidden sm:flex items-center space-x-1 text-xs text-muted-foreground">
                <TrendingUp className="w-3 h-3 text-green-500" />
                <span>Performance Mode</span>
              </div>
            )}

            {/* AI Context Button */}
            {isAIEnabled && (
              <Button 
                variant="ghost" 
                size="sm" 
                className="text-xs"
                onClick={getContextualSuggestions}
              >
                <Brain className="w-4 h-4 mr-1" />
                <span className="hidden sm:inline">Smart Navigate</span>
              </Button>
            )}

            {/* Quick Actions based on current context */}
            {location.pathname.includes('/products') && (
              <Button variant="outline" size="sm" asChild>
                <Link to="/search" className="text-xs">
                  <Search className="w-4 h-4 mr-1" />
                  Search
                </Link>
              </Button>
            )}
          </div>
        </div>

        {/* Contextual Information Bar */}
        {breadcrumbs.length > 2 && (
          <div className="mt-2 pt-2 border-t border-border/20">
            <div className="flex items-center justify-between text-xs text-muted-foreground">
              <div className="flex items-center space-x-4">
                <span>Path: {location.pathname}</span>
                {breadcrumbs.some(b => b.metadata?.category) && (
                  <span>
                    Context: {breadcrumbs
                      .filter(b => b.metadata?.category)
                      .map(b => b.metadata?.category)
                      .filter((value, index, self) => self.indexOf(value) === index)
                      .join(', ')
                    }
                  </span>
                )}
              </div>

              <div className="flex items-center space-x-3">
                <span>Powered by Katalyst Context Engine</span>
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
              </div>
            </div>
          </div>
        )}
      </div>
    </motion.div>
  );
}

