import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Imports
import { usePWA } from '@katalyst/hooks/use-pwa';

// Design System
import { Button } from '@katalyst/design-system/ui/button';

// Icons
import { Smartphone } from 'lucide-react';

// Layout Components
import { Header } from './Header';
import { Breadcrumbs } from './Breadcrumbs';
import { Footer } from './Footer';

interface MainLayoutProps {
  children: React.ReactNode;
}

export function MainLayout({ children }: MainLayoutProps) {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [showInstallPrompt, setShowInstallPrompt] = useState(false);

  const location = useLocation();

  // Katalyst Hooks
  const { metrics, threadPools } = useMultithreading();
  const { isInstallable, isInstalled, promptInstall } = usePWA();

  // Store
  const { cart, user } = useStorefrontStore();

  // Mock cart item count
  const cartItemCount = 3; // This would come from the cart store

  // Show install prompt for PWA
  useEffect(() => {
    if (isInstallable && !isInstalled) {
      const hasSeenPrompt = localStorage.getItem('pwa-install-prompted');
      if (!hasSeenPrompt) {
        setTimeout(() => setShowInstallPrompt(true), 3000);
      }
    }
  }, [isInstallable, isInstalled]);

  const handleInstallApp = async () => {
    await promptInstall();
    localStorage.setItem('pwa-install-prompted', 'true');
    setShowInstallPrompt(false);
  };

  const navigation = [
    {
      title: 'Products',
      href: '/products',
      items: [
        { title: 'All Products', href: '/products', description: 'Browse our full catalog' },
        { title: 'Electronics', href: '/categories/electronics', description: 'Laptops, phones, gadgets' },
        { title: 'Clothing', href: '/categories/clothing', description: 'Fashion and apparel' },
        { title: 'Books', href: '/categories/books', description: 'Digital and physical books' },
        { title: 'Home & Garden', href: '/categories/home', description: 'Home improvement and decor' }
      ]
    },
    {
      title: 'Features',
      href: '#',
      items: [
        { title: 'Multithreaded Search', href: '/products?demo=search', description: 'Lightning-fast product search' },
        { title: 'AI Recommendations', href: '/#ai-features', description: 'Personalized suggestions' },
        { title: 'Real-time Analytics', href: '/#metrics', description: 'Live performance monitoring' },
        { title: 'Secure Payments', href: '/checkout', description: 'Multi-provider payment processing' }
      ]
    },
    {
      title: 'About',
      href: '/about',
      items: [
        { title: 'About Katalyst', href: '/about', description: 'Learn about our technology' },
        { title: 'Contact Us', href: '/contact', description: 'Get in touch with our team' },
        { title: 'Help Center', href: '/help', description: 'Support and documentation' },
        { title: 'Privacy Policy', href: '/privacy', description: 'How we protect your data' }
      ]
    }
  ];

  return (
    <div className="min-h-screen bg-background flex flex-col">
      {/* PWA Install Prompt */}
      <AnimatePresence>
        {showInstallPrompt && (
          <motion.div
            initial={{ y: -100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            exit={{ y: -100, opacity: 0 }}
            className="bg-gradient-to-r from-primary to-secondary text-white p-4 shadow-lg"
          >
            <div className="container mx-auto flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <Smartphone className="w-5 h-5" />
                <div>
                  <p className="font-medium">Install Katalyst App</p>
                  <p className="text-sm opacity-90">Get native performance and offline access</p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={handleInstallApp}
                >
                  Install
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => {
                    setShowInstallPrompt(false);
                    localStorage.setItem('pwa-install-prompted', 'true');
                  }}
                  className="text-white hover:bg-white/10"
                >
                  ✕
                </Button>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Header */}
      <header className="sticky top-0 z-50 w-full border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
        <div className="container mx-auto">
          <div className="flex h-16 items-center justify-between">
            {/* Logo and Brand */}
            <div className="flex items-center space-x-4">
              <Link to="/" className="flex items-center space-x-2">
                <div className="relative">
                  <Zap className="w-8 h-8 text-primary" />
                  <div className="absolute -top-1 -right-1">
                    <div className="w-3 h-3 bg-gradient-to-r from-primary to-secondary rounded-full animate-pulse" />
                  </div>
                </div>
                <div className="hidden md:block">
                  <h1 className="font-bold text-xl bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
                    Katalyst
                  </h1>
                  <p className="text-xs text-muted-foreground -mt-1">E-Commerce</p>
                </div>
              </Link>

              {/* Performance Indicator */}
              <div className="hidden lg:flex items-center space-x-2 px-3 py-1 bg-muted rounded-full">
                <Cpu className="w-3 h-3 text-primary" />
                <span className="text-xs font-medium">{threadPools.length} pools</span>
                <span className="text-xs text-muted-foreground">
                  {metrics?.totalTasks || 0} tasks
                </span>
              </div>
            </div>

            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center space-x-6">
              <NavigationMenu>
                <NavigationMenuList>
                  {navigation.map((item) => (
                    <NavigationMenuItem key={item.title}>
                      <NavigationMenuTrigger>{item.title}</NavigationMenuTrigger>
                      <NavigationMenuContent>
                        <div className="grid w-[400px] gap-3 p-4 md:w-[500px] md:grid-cols-2 lg:w-[600px]">
                          {item.items.map((subItem) => (
                            <NavigationMenuLink
                              key={subItem.title}
                              asChild
                            >
                              <Link
                                to={subItem.href}
                                className="block select-none space-y-1 rounded-md p-3 leading-none no-underline outline-none transition-colors hover:bg-accent hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground"
                              >
                                <div className="text-sm font-medium leading-none">
                                  {subItem.title}
                                </div>
                                <p className="line-clamp-2 text-sm leading-snug text-muted-foreground">
                                  {subItem.description}
                                </p>
                              </Link>
                            </NavigationMenuLink>
                          ))}
                        </div>
                      </NavigationMenuContent>
                    </NavigationMenuItem>
                  ))}
                </NavigationMenuList>
              </NavigationMenu>
            </div>

            {/* Search Bar (Desktop) */}
            <div className="hidden lg:flex items-center space-x-2 flex-1 max-w-sm mx-8">
              <div className="relative w-full">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  placeholder="Search with AI..."
                  className="pl-10 pr-4 cursor-pointer"
                  onClick={() => setIsSearchOpen(true)}
                  readOnly
                />
                <Badge variant="secondary" className="absolute right-2 top-1/2 transform -translate-y-1/2 text-xs">
                  <Brain className="w-3 h-3 mr-1" />
                  AI
                </Badge>
              </div>
            </div>

            {/* Actions */}
            <div className="flex items-center space-x-3">
              {/* Search Button (Mobile) */}
              <Button
                variant="ghost"
                size="sm"
                className="lg:hidden"
                onClick={() => setIsSearchOpen(true)}
              >
                <Search className="w-5 h-5" />
              </Button>

              {/* Cart Button */}
              <Button
                variant="ghost"
                size="sm"
                className="relative"
                onClick={() => setIsCartOpen(true)}
              >
                <ShoppingCart className="w-5 h-5" />
                {cartItemCount > 0 && (
                  <Badge
                    variant="destructive"
                    className="absolute -top-2 -right-2 h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs"
                  >
                    {cartItemCount > 9 ? '9+' : cartItemCount}
                  </Badge>
                )}
              </Button>

              {/* User Menu */}
              <UserMenu />

              {/* Mobile Menu Button */}
              <Button
                variant="ghost"
                size="sm"
                className="md:hidden"
                onClick={() => setIsMobileMenuOpen(true)}
              >
                <Menu className="w-5 h-5" />
              </Button>
            </div>
          </div>
        </div>

        {/* Secondary Navigation Bar */}
        <div className="border-t bg-muted/30">
          <div className="container mx-auto">
            <div className="flex h-10 items-center justify-between px-4">
              <div className="flex items-center space-x-4 text-sm">
                <Link 
                  to="/" 
                  className={`hover:text-primary transition-colors ${
                    location.pathname === '/' ? 'text-primary font-medium' : 'text-muted-foreground'
                  }`}
                >
                  <Home className="w-4 h-4 inline mr-1" />
                  Home
                </Link>
                <Link 
                  to="/products" 
                  className={`hover:text-primary transition-colors ${
                    location.pathname === '/products' ? 'text-primary font-medium' : 'text-muted-foreground'
                  }`}
                >
                  <Package className="w-4 h-4 inline mr-1" />
                  Products
                </Link>
                <Link 
                  to="/account/favorites" 
                  className={`hover:text-primary transition-colors ${
                    location.pathname.includes('/favorites') ? 'text-primary font-medium' : 'text-muted-foreground'
                  }`}
                >
                  <Heart className="w-4 h-4 inline mr-1" />
                  Favorites
                </Link>
              </div>

              <div className="hidden md:flex items-center space-x-4 text-xs text-muted-foreground">
                <div className="flex items-center space-x-1">
                  <Shield className="w-3 h-3 text-green-600" />
                  <span>Secure</span>
                </div>
                <div className="flex items-center space-x-1">
                  <Globe className="w-3 h-3 text-blue-600" />
                  <span>Global Shipping</span>
                </div>
                <div className="flex items-center space-x-1">
                  <Brain className="w-3 h-3 text-purple-600" />
                  <span>AI Powered</span>
                </div>
                {isInstalled && (
                  <div className="flex items-center space-x-1">
                    <Smartphone className="w-3 h-3 text-primary" />
                    <span>PWA Active</span>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1">
        {children}
      </main>

      {/* Footer */}
      <Footer />

      {/* Modals and Drawers */}
      <SearchModal 
        isOpen={isSearchOpen} 
        onClose={() => setIsSearchOpen(false)} 
      />

      <CartDrawer 
        isOpen={isCartOpen} 
        onClose={() => setIsCartOpen(false)} 
      />

      <MobileNav 
        isOpen={isMobileMenuOpen} 
        onClose={() => setIsMobileMenuOpen(false)}
        navigation={navigation}
      />
    </div>
  );
}

