import { useState, useRef, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Core Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useAI } from '@katalyst/hooks/use-ai';
import { useMultithreading } from '@katalyst/hooks/use-multithreading';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@katalyst/design-system/ui/card';
import { Input } from '@katalyst/design-system/ui/input';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Avatar, AvatarFallback } from '@katalyst/design-system/ui/avatar';
import { ScrollArea } from '@katalyst/design-system/ui/scroll-area';

// Icons
import {
  MessageCircle, Send, Bot, User, Zap, Brain, Cpu, Loader2,
  X, Minimize2, Maximize2, RotateCcw, Copy, ThumbsUp, ThumbsDown,
  Sparkles, Code, Search, ShoppingBag, Settings, HelpCircle
} from 'lucide-react';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant' | 'system';
  content: string;
  timestamp: Date;
  status?: 'sending' | 'sent' | 'error';
  metadata?: {
    processingTime?: number;
    threadPoolId?: string;
    model?: string;
    confidence?: number;
    suggestions?: string[];
  };
}

interface ChatContext {
  sessionId: string;
  userPreferences: {
    theme: 'light' | 'dark';
    language: string;
    aiPersonality: 'professional' | 'friendly' | 'technical';
  };
  conversationHistory: ChatMessage[];
  currentPage?: string;
  userIntent?: string;
}

interface KatalystAIChatbotProps {
  isOpen: boolean;
  onClose: () => void;
  initialMessage?: string;
  context?: Partial<ChatContext>;
}

export function KatalystAIChatbot({ 
  isOpen, 
  onClose, 
  initialMessage,
  context = {}
}: KatalystAIChatbotProps) {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [isMinimized, setIsMinimized] = useState(false);
  const [chatContext, setChatContext] = useState<ChatContext>({
    sessionId: `chat-${Date.now()}`,
    userPreferences: {
      theme: 'light',
      language: 'en',
      aiPersonality: 'friendly'
    },
    conversationHistory: [],
    ...context
  });

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Katalyst Hooks
  const { executeTask, createThreadPool } = useMultithreadingStore();
  const { generateRecommendations, isAIEnabled, processWithAI } = useAI();
  const { threadPools, createWorkerPool } = useMultithreading();

  // Predefined quick actions with Katalyst integration
  const quickActions = [
    {
      icon: Search,
      label: 'Find Products',
      action: 'product_search',
      prompt: 'Help me find products in your catalog'
    },
    {
      icon: ShoppingBag,
      label: 'Order Status',
      action: 'order_inquiry',
      prompt: 'Check the status of my recent orders'
    },
    {
      icon: Code,
      label: 'Technical Support',
      action: 'tech_support',
      prompt: 'I need help with technical integration'
    },
    {
      icon: Settings,
      label: 'Account Help',
      action: 'account_support',
      prompt: 'Help me with my account settings'
    }
  ];

  // Initialize chatbot with welcome message
  useEffect(() => {
    if (isOpen && messages.length === 0) {
      const welcomeMessage: ChatMessage = {
        id: `msg-${Date.now()}`,
        type: 'assistant',
        content: `Hello! I'm your Katalyst AI assistant, powered by advanced multithreading and AI technology. I can help you with:

• **Product Search** - Find exactly what you're looking for
• **Technical Support** - Get help with integrations and setup
• **Order Management** - Check status, track shipments, handle returns
• **Performance Insights** - Real-time system analytics and optimization

How can I assist you today?`,
        timestamp: new Date(),
        metadata: {
          model: '@cf/meta/llama-3.1-8b-instruct',
          confidence: 0.95,
          suggestions: [
            'Show me your latest AI development tools',
            'Help me optimize my application performance',
            'What multithreading capabilities do you offer?'
          ]
        }
      };

      setMessages([welcomeMessage]);
    }

    if (initialMessage && isOpen) {
      handleSendMessage(initialMessage);
    }
  }, [isOpen, initialMessage]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Focus input when opened
  useEffect(() => {
    if (isOpen && !isMinimized) {
      setTimeout(() => inputRef.current?.focus(), 100);
    }
  }, [isOpen, isMinimized]);

  // AI-powered message processing with multithreading
  const processAIMessage = useCallback(async (userMessage: string, messageId: string): Promise<string> => {
    try {
      setIsTyping(true);

      // Create dedicated AI processing thread pool for complex queries
      const aiProcessingPool = await createThreadPool({
        type: 'tokio',
        workerCount: 2,
        priority: 'high'
      });

      // Execute AI processing with context awareness
      const aiResponse = await executeTask({
        id: `ai-chat-${messageId}`,
        operation: 'process_ai_chat_message',
        data: {
          userMessage,
          context: chatContext,
          conversationHistory: messages.slice(-10), // Last 10 messages for context
          systemPrompt: `You are Katalyst AI, an advanced assistant for a multithreaded, AI-powered e-commerce platform.

Key Capabilities:
- Real-time multithreaded processing
- AI-powered product recommendations
- Performance optimization insights
- Technical support for developers
- Cloudflare AI model integration

Personality: ${chatContext.userPreferences.aiPersonality}
Current Page: ${chatContext.currentPage || 'unknown'}

Provide helpful, accurate responses that showcase Katalyst's advanced capabilities.`,
          modelConfig: {
            model: '@cf/meta/llama-3.1-8b-instruct',
            maxTokens: 1024,
            temperature: 0.7,
            topP: 0.9
          },
          processingOptions: {
            useMultithreading: true,
            enableContextAnalysis: true,
            includeRecommendations: true,
            threadPoolId: aiProcessingPool.id
          }
        },
        priority: 'high',
        status: 'pending'
      });

      // Process AI response and generate suggestions
      const response = aiResponse?.content || `I apologize, but I'm having trouble processing your request right now. As your Katalyst AI assistant, I'm designed to help with:

• Product searches and recommendations
• Technical support and troubleshooting  
• Order management and tracking
• Performance optimization insights

Please try rephrasing your question, or select one of the quick actions below to get started.`;

      // Generate contextual suggestions using AI
      let suggestions: string[] = [];
      if (isAIEnabled) {
        try {
          const suggestionContext = {
            userMessage,
            aiResponse: response,
            conversationHistory: messages.slice(-5)
          };

          const aiSuggestions = await generateRecommendations(suggestionContext, {
            type: 'conversation_suggestions',
            limit: 3
          });

          suggestions = aiSuggestions || [];
        } catch (error) {
          console.warn('Failed to generate suggestions:', error);
        }
      }

      // Update message with metadata
      setMessages(prev => prev.map(msg => 
        msg.id === messageId 
          ? {
              ...msg,
              content: response,
              status: 'sent',
              metadata: {
                ...msg.metadata,
                processingTime: Date.now() - msg.timestamp.getTime(),
                threadPoolId: aiProcessingPool.id,
                model: '@cf/meta/llama-3.1-8b-instruct',
                confidence: 0.85,
                suggestions: suggestions.length > 0 ? suggestions : [
                  'Tell me more about this',
                  'Show me related products',
                  'How can I get started?'
                ]
              }
            }
          : msg
      ));

      return response;

    } catch (error) {
      console.error('AI message processing failed:', error);

      // Update message with error state
      setMessages(prev => prev.map(msg => 
        msg.id === messageId 
          ? { ...msg, status: 'error', content: 'I apologize, but I encountered an error processing your message. Please try again.' }
          : msg
      ));

      return 'I apologize, but I encountered an error processing your message. Please try again.';
    } finally {
      setIsTyping(false);
    }
  }, [executeTask, createThreadPool, chatContext, messages, isAIEnabled, generateRecommendations]);

  // Handle sending messages
  const handleSendMessage = useCallback(async (message?: string) => {
    const messageText = message || inputValue.trim();
    if (!messageText) return;

    // Clear input
    setInputValue('');

    // Add user message
    const userMessage: ChatMessage = {
      id: `user-msg-${Date.now()}`,
      type: 'user',
      content: messageText,
      timestamp: new Date(),
      status: 'sent'
    };

    // Add AI response placeholder
    const aiMessageId = `ai-msg-${Date.now()}`;
    const aiMessage: ChatMessage = {
      id: aiMessageId,
      type: 'assistant',
      content: '',
      timestamp: new Date(),
      status: 'sending'
    };

    setMessages(prev => [...prev, userMessage, aiMessage]);

    // Process AI response
    await processAIMessage(messageText, aiMessageId);

    // Update conversation context
    setChatContext(prev => ({
      ...prev,
      conversationHistory: [...prev.conversationHistory, userMessage]
    }));

  }, [inputValue, processAIMessage]);

  // Handle quick action clicks
  const handleQuickAction = useCallback((action: typeof quickActions[0]) => {
    handleSendMessage(action.prompt);
  }, [handleSendMessage]);

  // Handle suggestion clicks
  const handleSuggestionClick = useCallback((suggestion: string) => {
    handleSendMessage(suggestion);
  }, [handleSendMessage]);

  // Copy message content
  const copyMessage = useCallback((content: string) => {
    navigator.clipboard.writeText(content);
  }, []);

  // Clear conversation
  const clearConversation = useCallback(() => {
    setMessages([]);
    setChatContext(prev => ({ ...prev, conversationHistory: [] }));
  }, []);

  if (!isOpen) return null;

  return (
    <motion.div
      initial={{ opacity: 0, y: 100, scale: 0.9 }}
      animate={{ 
        opacity: 1, 
        y: 0, 
        scale: 1,
        height: isMinimized ? 'auto' : '600px'
      }}
      exit={{ opacity: 0, y: 100, scale: 0.9 }}
      transition={{ type: 'spring', stiffness: 300, damping: 30 }}
      className="fixed bottom-4 right-4 z-50 w-96 max-w-[calc(100vw-2rem)]"
    >
      <Card className="shadow-2xl border-primary/20">
        {/* Chat Header */}
