import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Core Imports
import { useAI } from '@katalyst/hooks/use-ai';
import { useMultithreading } from '@katalyst/hooks/use-multithreading';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Badge } from '@katalyst/design-system/ui/badge';

// Icons
import { MessageCircle, Bot, Zap, Brain, Sparkles } from 'lucide-react';

// Components
import { KatalystAIChatbot } from './KatalystAIChatbot';

interface FloatingChatButtonProps {
  className?: string;
}

export function FloatingChatButton({ className = '' }: FloatingChatButtonProps) {
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [hasNewMessage, setHasNewMessage] = useState(false);
  const [isVisible, setIsVisible] = useState(false);

  // Katalyst Hooks
  const { isAIEnabled } = useAI();
  const { executingTasks } = useMultithreading();

  // Show floating button after a delay
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  // Simulate periodic AI activity indicators
  useEffect(() => {
    if (isAIEnabled && !isChatOpen) {
      const interval = setInterval(() => {
        // Show activity when there are many executing tasks
        if (executingTasks > 5 && Math.random() > 0.7) {
          setHasNewMessage(true);
          setTimeout(() => setHasNewMessage(false), 3000);
        }
      }, 15000);

      return () => clearInterval(interval);
    }
  }, [isAIEnabled, executingTasks, isChatOpen]);

  // Auto-hide new message indicator when chat opens
  useEffect(() => {
    if (isChatOpen) {
      setHasNewMessage(false);
    }
  }, [isChatOpen]);

  const handleToggleChat = () => {
    setIsChatOpen(!isChatOpen);
    setHasNewMessage(false);
  };

  if (!isVisible) return null;

  return (
    <>
      <AnimatePresence>
        {!isChatOpen && (
          <motion.div
            initial={{ opacity: 0, scale: 0, y: 100 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0, y: 100 }}
            transition={{ 
              type: 'spring', 
              stiffness: 300, 
              damping: 25,
              delay: 0.2 
            }}
            className={`fixed bottom-6 right-6 z-40 ${className}`}
          >
            {/* Main Chat Button */}
            <Button
              onClick={handleToggleChat}
              className="relative h-14 w-14 rounded-full bg-gradient-to-r from-primary to-secondary shadow-2xl hover:shadow-3xl transition-all duration-300 group"
              size="sm"
            >
              <motion.div
                animate={hasNewMessage ? { scale: [1, 1.1, 1] } : {}}
                transition={{ repeat: hasNewMessage ? Infinity : 0, duration: 1.5 }}
              >
                <Bot className="h-6 w-6 text-white group-hover:scale-110 transition-transform" />
              </motion.div>

              {/* Activity Pulse */}
              {isAIEnabled && (
                <motion.div
                  className="absolute inset-0 rounded-full bg-gradient-to-r from-primary to-secondary"
                  animate={{ scale: [1, 1.2, 1], opacity: [0.5, 0, 0.5] }}
                  transition={{ repeat: Infinity, duration: 2, ease: 'easeInOut' }}
                />
              )}

              {/* New Message Indicator */}
              <AnimatePresence>
                {hasNewMessage && (
                  <motion.div
                    initial={{ scale: 0 }}
                    animate={{ scale: 1 }}
                    exit={{ scale: 0 }}
                    className="absolute -top-1 -right-1"
                  >
                    <Badge className="h-5 w-5 p-0 bg-red-500 border-2 border-white flex items-center justify-center animate-pulse">
                      <span className="text-xs text-white">!</span>
                    </Badge>
                  </motion.div>
                )}
              </AnimatePresence>
            </Button>

            {/* Tooltip/Preview */}
            <AnimatePresence>
              {!isChatOpen && isVisible && (
                <motion.div
                  initial={{ opacity: 0, x: 20, scale: 0.9 }}
                  animate={{ 
                    opacity: 1, 
                    x: 0, 
                    scale: 1,
                    transition: { delay: 1.5 }
                  }}
                  exit={{ opacity: 0, x: 20, scale: 0.9 }}
                  className="absolute right-16 bottom-0 whitespace-nowrap"
                >
                  <div className="bg-background border border-border rounded-lg shadow-lg p-3 max-w-xs">
                    <div className="flex items-center space-x-2 mb-1">
                      <Bot className="h-4 w-4 text-primary" />
                      <span className="font-medium text-sm">Katalyst AI Assistant</span>
                      {isAIEnabled && (
                        <Badge variant="outline" className="text-xs">
                          <Brain className="w-3 h-3 mr-1" />
                          AI
                        </Badge>
                      )}
                    </div>
                    <p className="text-xs text-muted-foreground">
                      {hasNewMessage 
                        ? "I have insights about your current activity!"
                        : "Need help? I'm powered by advanced AI and multithreading!"
                      }
                    </p>

                    {/* Features preview */}
                    <div className="flex items-center space-x-2 mt-2 text-xs text-muted-foreground">
                      <div className="flex items-center space-x-1">
                        <Sparkles className="w-3 h-3" />
                        <span>Smart</span>
                      </div>
                      <div className="flex items-center space-x-1">
                        <Zap className="w-3 h-3" />
                        <span>Fast</span>
                      </div>
                      <div className="flex items-center space-x-1">
                        <MessageCircle className="w-3 h-3" />
                        <span>24/7</span>
                      </div>
                    </div>
                  </div>

                  {/* Arrow pointing to button */}
                  <div className="absolute right-0 top-1/2 transform translate-x-1 -translate-y-1/2">
                    <div className="w-2 h-2 bg-background border-r border-b border-border rotate-45" />
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Chatbot Component */}
      <AnimatePresence>
        {isChatOpen && (
          <KatalystAIChatbot
            isOpen={isChatOpen}
            onClose={() => setIsChatOpen(false)}
            context={{
              sessionId: `float-chat-${Date.now()}`,
              currentPage: window.location.pathname,
              userPreferences: {
                theme: 'light',
                language: 'en',
                aiPersonality: 'friendly'
              }
            }}
          />
        )}
      </AnimatePresence>
    </>
  );
}

