import { useState, useEffect } from 'react';
import { Outlet } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Imports
import { usePWA } from '@katalyst/hooks/use-pwa';

// Design System
import { Button } from '@katalyst/design-system/ui/button';

// Icons
import { Smartphone } from 'lucide-react';

// Layout Components
import { Header } from './Header';
import { Breadcrumbs } from './Breadcrumbs';
import { Footer } from './Footer';

export function MainLayout() {
  const [showInstallPrompt, setShowInstallPrompt] = useState(false);

  // Katalyst Hooks
  const { isInstallable, isInstalled, promptInstall } = usePWA();

  // Show install prompt for PWA
  useEffect(() => {
    if (isInstallable && !isInstalled) {
      const hasSeenPrompt = localStorage.getItem('pwa-install-prompted');
      if (!hasSeenPrompt) {
        setTimeout(() => setShowInstallPrompt(true), 3000);
      }
    }
  }, [isInstallable, isInstalled]);

  const handleInstallApp = async () => {
    await promptInstall();
    localStorage.setItem('pwa-install-prompted', 'true');
    setShowInstallPrompt(false);
  };

  return (
    <div className="min-h-screen bg-background flex flex-col">
      {/* PWA Install Prompt */}
      <AnimatePresence>
        {showInstallPrompt && (
          <motion.div
            initial={{ y: -100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            exit={{ y: -100, opacity: 0 }}
            className="bg-gradient-to-r from-primary to-secondary text-white p-4 shadow-lg z-60"
          >
            <div className="container mx-auto flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <Smartphone className="w-5 h-5" />
                <div>
                  <p className="font-medium">Install Katalyst App</p>
                  <p className="text-sm opacity-90">Get native performance and offline access</p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={handleInstallApp}
                >
                  Install
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => {
                    setShowInstallPrompt(false);
                    localStorage.setItem('pwa-install-prompted', 'true');
                  }}
                  className="text-white hover:bg-white/10"
                >
                  ✕
                </Button>
              </div>
            </div>
