import { useState, useEffect, useCallback, useRef } from 'react';

// Types from the worker
interface WorkerMessage {
  id: string;
  type: 'computation' | 'image_processing' | 'ml_inference' | 'signal_processing' | 'benchmark';
  operation: string;
  data: any;
  priority: 'low' | 'normal' | 'high' | 'critical';
  options?: {
    useSimd?: boolean;
    threadCount?: number;
    chunkSize?: number;
    precision?: 'f32' | 'f64';
  };
}

interface WorkerResponse {
  id: string;
  type: 'success' | 'error' | 'progress';
  result?: any;
  error?: string;
  metrics?: {
    executionTime: number;
    memoryUsed: number;
    operationsPerSecond?: number;
    simdAcceleration?: boolean;
  };
  progress?: number;
}

interface YogiTask {
  id: string;
  operation: string;
  status: 'pending' | 'running' | 'completed' | 'error';
  progress: number;
  result?: any;
  error?: string;
  metrics?: WorkerResponse['metrics'];
  startTime: number;
}

interface YogiCapabilities {
  wasmSupported: boolean;
  simdSupported: boolean;
  threadsSupported: boolean;
  memorySize: number;
  availableFunctions: string[];
}

interface UseYogiWASMReturn {
  // State
  isInitialized: boolean;
  isInitializing: boolean;
  capabilities: YogiCapabilities | null;
  activeTasks: Map<string, YogiTask>;
  error: string | null;

  // Operations
  runMatrixMultiplication: (matrixA: number[][], matrixB: number[][], options?: any) => Promise<number[][]>;
  processImage: (imageData: ImageData, operation: 'blur' | 'sharpen' | 'edge_detect' | 'grayscale', options?: any) => Promise<ImageData>;
  runNeuralNetworkInference: (input: number[], weights: number[], layerCount: number, options?: any) => Promise<{ predictions: number[], confidence: number, topClass: number }>;
  calculateFibonacci: (n: number, threadCount?: number) => Promise<number>;
  runBenchmarkSuite: (onProgress?: (progress: number, currentTest: string) => void) => Promise<any>;

  // Generic operation runner
  runOperation: (operation: string, data: any, options?: any) => Promise<any>;

  // Utils
  cancelTask: (taskId: string) => void;
  clearTasks: () => void;
  getPerformanceStats: () => any;
}

export function useYogiWASM(): UseYogiWASMReturn {
  const [isInitialized, setIsInitialized] = useState(false);
  const [isInitializing, setIsInitializing] = useState(false);
  const [capabilities, setCapabilities] = useState<YogiCapabilities | null>(null);
  const [activeTasks, setActiveTasks] = useState(new Map<string, YogiTask>());
  const [error, setError] = useState<string | null>(null);

  const workerRef = useRef<Worker | null>(null);
  const pendingCallbacksRef = useRef(new Map<string, { resolve: (value: any) => void, reject: (error: Error) => void }>());
  const progressCallbacksRef = useRef(new Map<string, (progress: number, data?: any) => void>());

  // Initialize the WASM worker
  useEffect(() => {
    let mounted = true;

    const initializeWorker = async () => {
      if (isInitializing || isInitialized) return;

      setIsInitializing(true);
      setError(null);

      try {
        // Create the worker
        workerRef.current = new Worker(
          new URL('../workers/yogi-wasm-worker.ts', import.meta.url),
          { type: 'module' }
        );

        // Handle worker messages
        workerRef.current.onmessage = (event: MessageEvent<WorkerResponse>) => {
          const response = event.data;

          if (response.id === 'init') {
            if (response.type === 'success' && mounted) {
              setCapabilities({
                wasmSupported: true,
                simdSupported: true,
                threadsSupported: typeof SharedArrayBuffer !== 'undefined',
                memorySize: response.result?.memorySize || 0,
                availableFunctions: response.result?.availableFunctions || []
              });
              setIsInitialized(true);
              setIsInitializing(false);
            } else if (response.type === 'error' && mounted) {
              setError(response.error || 'Worker initialization failed');
              setIsInitializing(false);
            }
            return;
          }

          // Handle task responses
          const callbacks = pendingCallbacksRef.current.get(response.id);
          const progressCallback = progressCallbacksRef.current.get(response.id);

          if (response.type === 'progress' && progressCallback) {
            progressCallback(response.progress || 0, response.result);

            // Update task progress
            setActiveTasks(prev => {
              const newTasks = new Map(prev);
              const task = newTasks.get(response.id);
              if (task) {
                newTasks.set(response.id, {
                  ...task,
                  progress: response.progress || 0,
                  status: 'running'
                });
              }
              return newTasks;
            });
          } else if (response.type === 'success' && callbacks) {
            // Complete the task
            setActiveTasks(prev => {
              const newTasks = new Map(prev);
              const task = newTasks.get(response.id);
              if (task) {
                newTasks.set(response.id, {
                  ...task,
                  status: 'completed',
                  progress: 100,
                  result: response.result,
                  metrics: response.metrics
                });
              }
              return newTasks;
            });

            callbacks.resolve(response.result);
            pendingCallbacksRef.current.delete(response.id);
            progressCallbacksRef.current.delete(response.id);

          } else if (response.type === 'error' && callbacks) {
            // Error in task
            setActiveTasks(prev => {
              const newTasks = new Map(prev);
              const task = newTasks.get(response.id);
              if (task) {
                newTasks.set(response.id, {
                  ...task,
                  status: 'error',
                  error: response.error
                });
              }
              return newTasks;
            });

            callbacks.reject(new Error(response.error || 'Worker operation failed'));
            pendingCallbacksRef.current.delete(response.id);
            progressCallbacksRef.current.delete(response.id);
          }
        };

        // Handle worker errors
        workerRef.current.onerror = (error) => {
          console.error('Yogi WASM Worker error:', error);
          if (mounted) {
            setError(`Worker error: ${error.message || 'Unknown error'}`);
            setIsInitializing(false);
          }
        };

      } catch (error) {
        console.error('Failed to initialize Yogi WASM worker:', error);
        if (mounted) {
          setError(`Failed to initialize worker: ${error}`);
          setIsInitializing(false);
        }
      }
    };

    initializeWorker();

    return () => {
      mounted = false;
      if (workerRef.current) {
        workerRef.current.terminate();
        workerRef.current = null;
      }
    };
  }, []);

  // Generic operation runner
  const runOperation = useCallback(async (
    operation: string,
    data: any,
    options: any = {},
    onProgress?: (progress: number, data?: any) => void
  ): Promise<any> => {
    if (!isInitialized || !workerRef.current) {
      throw new Error('Yogi WASM worker not initialized');
    }

    const taskId = `task-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    // Create task tracking
    const task: YogiTask = {
      id: taskId,
      operation,
      status: 'pending',
      progress: 0,
      startTime: Date.now()
    };

    setActiveTasks(prev => new Map(prev).set(taskId, task));

    // Set up progress callback if provided
    if (onProgress) {
      progressCallbacksRef.current.set(taskId, onProgress);
    }

    return new Promise((resolve, reject) => {
      // Store callbacks for response handling
      pendingCallbacksRef.current.set(taskId, { resolve, reject });

      // Send message to worker
      const message: WorkerMessage = {
        id: taskId,
        type: 'computation',
        operation,
        data,
        priority: options.priority || 'normal',
        options
      };

      workerRef.current!.postMessage(message);

      // Update task status
      setActiveTasks(prev => {
        const newTasks = new Map(prev);
        const currentTask = newTasks.get(taskId);
        if (currentTask) {
          newTasks.set(taskId, { ...currentTask, status: 'running' });
        }
        return newTasks;
      });
    });
  }, [isInitialized]);

  // Specific operation methods
  const runMatrixMultiplication = useCallback(async (
    matrixA: number[][],
    matrixB: number[][],
    options: any = {}
  ): Promise<number[][]> => {
    const size = matrixA.length;
    return runOperation('matrix_multiply', { matrixA, matrixB, size }, options);
  }, [runOperation]);

  const processImage = useCallback(async (
    imageData: ImageData,
    operation: 'blur' | 'sharpen' | 'edge_detect' | 'grayscale',
    options: any = {}
  ): Promise<ImageData> => {
    const operationMap = {
      'blur': 0,
      'sharpen': 1,
      'edge_detect': 2,
      'grayscale': 3
    };

    const result = await runOperation('image_processing', {
      imageData: imageData.data,
      width: imageData.width,
      height: imageData.height,
      operation: operationMap[operation]
    }, options);

    return new ImageData(result, imageData.width, imageData.height);
  }, [runOperation]);

  const runNeuralNetworkInference = useCallback(async (
    input: number[],
    weights: number[],
    layerCount: number,
    options: any = {}
  ): Promise<{ predictions: number[], confidence: number, topClass: number }> => {
    return runOperation('neural_network_inference', { input, weights, layerCount }, options);
  }, [runOperation]);

  const calculateFibonacci = useCallback(async (
    n: number,
    threadCount: number = 4
  ): Promise<number> => {
    return runOperation('fibonacci_parallel', { n }, { threadCount });
  }, [runOperation]);

  const runBenchmarkSuite = useCallback(async (
    onProgress?: (progress: number, currentTest: string) => void
  ): Promise<any> => {
    return runOperation('benchmark_suite', {}, {}, onProgress);
  }, [runOperation]);

  // Utility methods
  const cancelTask = useCallback((taskId: string) => {
    // Remove from active tasks and cleanup callbacks
    setActiveTasks(prev => {
      const newTasks = new Map(prev);
      newTasks.delete(taskId);
      return newTasks;
    });

    const callbacks = pendingCallbacksRef.current.get(taskId);
    if (callbacks) {
      callbacks.reject(new Error('Task cancelled'));
      pendingCallbacksRef.current.delete(taskId);
    }

    progressCallbacksRef.current.delete(taskId);
  }, []);

  const clearTasks = useCallback(() => {
    setActiveTasks(new Map());
    pendingCallbacksRef.current.clear();
    progressCallbacksRef.current.clear();
  }, []);

  const getPerformanceStats = useCallback(() => {
    const tasks = Array.from(activeTasks.values());
    const completedTasks = tasks.filter(t => t.status === 'completed');

    if (completedTasks.length === 0) return null;

    const totalExecutionTime = completedTasks.reduce((sum, task) => 
      sum + (task.metrics?.executionTime || 0), 0);

    const averageExecutionTime = totalExecutionTime / completedTasks.length;

    const totalMemoryUsed = completedTasks.reduce((sum, task) => 
      sum + (task.metrics?.memoryUsed || 0), 0);

    const operationsCount = completedTasks.length;
    const successRate = (completedTasks.length / tasks.length) * 100;

    return {
      operationsCount,
      averageExecutionTime,
      totalMemoryUsed,
      successRate,
      simdUtilization: completedTasks.filter(t => t.metrics?.simdAcceleration).length / completedTasks.length * 100
    };
  }, [activeTasks]);

  return {
    // State
    isInitialized,
    isInitializing,
    capabilities,
    activeTasks,
    error,

    // Operations
    runMatrixMultiplication,
    processImage,
    runNeuralNetworkInference,
    calculateFibonacci,
    runBenchmarkSuite,
    runOperation,

    // Utils
    cancelTask,
    clearTasks,
    getPerformanceStats
  };
}

