import { useState, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Core Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useMultithreading } from '@katalyst/hooks/use-multithreading';

// Custom Hook
import { useYogiWASM } from '../../hooks/use-yogi-wasm';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@katalyst/design-system/ui/card';
import { Progress } from '@katalyst/design-system/ui/progress';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@katalyst/design-system/ui/tabs';
import { Input } from '@katalyst/design-system/ui/input';
import { Label } from '@katalyst/design-system/ui/label';

// Icons
import {
  Zap, Cpu, Brain, Activity, Play, Square, RotateCcw,
  BarChart3, TrendingUp, Settings, Clock, Memory,
  HardDrive, Gauge, CheckCircle, AlertTriangle,
  Loader2, Sparkles, Code2, Image, Calculator,
  Target, Award
} from 'lucide-react';

interface BenchmarkResult {
  name: string;
  score: number;
  executionTime: number;
  operations: number;
}

export function WASMPerformanceDashboard() {
  const [activeTab, setActiveTab] = useState('overview');
  const [benchmarkProgress, setBenchmarkProgress] = useState(0);
  const [currentBenchmark, setCurrentBenchmark] = useState('');
  const [benchmarkResults, setBenchmarkResults] = useState<BenchmarkResult[]>([]);
  const [isBenchmarkRunning, setIsBenchmarkRunning] = useState(false);
  const [matrixSize, setMatrixSize] = useState(512);
  const [fibonacciN, setFibonacciN] = useState(35);
  const [threadCount, setThreadCount] = useState(4);

  // Katalyst Hooks
  const { executeTask } = useMultithreadingStore();
  const { threadPools, executingTasks, createWorkerPool } = useMultithreading();

  // WASM Hook
  const {
    isInitialized,
    isInitializing,
    capabilities,
    activeTasks,
    error,
    runMatrixMultiplication,
    processImage,
    runNeuralNetworkInference,
    calculateFibonacci,
    runBenchmarkSuite,
    runOperation,
    clearTasks,
    getPerformanceStats
  } = useYogiWASM();

  const performanceStats = getPerformanceStats();

  // Run comprehensive benchmark suite
  const runComprehensiveBenchmarks = useCallback(async () => {
    if (!isInitialized || isBenchmarkRunning) return;

    setIsBenchmarkRunning(true);
    setBenchmarkProgress(0);
    setBenchmarkResults([]);
    clearTasks();

    try {
      const results = await runBenchmarkSuite((progress, data) => {
        setBenchmarkProgress(progress);
        if (data?.currentBenchmark) {
          setCurrentBenchmark(data.currentBenchmark);
        }
      });

      setBenchmarkResults(results.benchmarks || []);
    } catch (error) {
      console.error('Benchmark suite failed:', error);
    } finally {
      setIsBenchmarkRunning(false);
      setCurrentBenchmark('');
    }
  }, [isInitialized, isBenchmarkRunning, runBenchmarkSuite, clearTasks]);

  // Run individual performance tests
  const runMatrixTest = useCallback(async () => {
    if (!isInitialized) return;

    try {
      // Create random matrices
      const matrixA = Array.from({ length: matrixSize }, () =>
        Array.from({ length: matrixSize }, () => Math.random())
      );
      const matrixB = Array.from({ length: matrixSize }, () =>
        Array.from({ length: matrixSize }, () => Math.random())
      );

      await runMatrixMultiplication(matrixA, matrixB, {
        useSimd: true,
        threadCount,
        priority: 'high'
      });
    } catch (error) {
      console.error('Matrix test failed:', error);
    }
  }, [isInitialized, matrixSize, threadCount, runMatrixMultiplication]);

  const runFibonacciTest = useCallback(async () => {
    if (!isInitialized) return;

    try {
      await calculateFibonacci(fibonacciN, threadCount);
    } catch (error) {
      console.error('Fibonacci test failed:', error);
    }
  }, [isInitialized, fibonacciN, threadCount, calculateFibonacci]);

  const runImageProcessingTest = useCallback(async () => {
    if (!isInitialized) return;

    try {
      // Create test image data
      const width = 1920;
      const height = 1080;
      const imageData = new ImageData(width, height);

      // Fill with test pattern
      for (let i = 0; i < imageData.data.length; i += 4) {
        imageData.data[i] = Math.floor(Math.random() * 256);     // R
        imageData.data[i + 1] = Math.floor(Math.random() * 256); // G
        imageData.data[i + 2] = Math.floor(Math.random() * 256); // B
        imageData.data[i + 3] = 255; // A
      }

      await processImage(imageData, 'edge_detect', {
        useSimd: true,
        threadCount,
        priority: 'high'
      });
    } catch (error) {
      console.error('Image processing test failed:', error);
    }
  }, [isInitialized, threadCount, processImage]);

  const runMLInferenceTest = useCallback(async () => {
    if (!isInitialized) return;

    try {
      // Mock neural network input and weights
      const inputSize = 784; // 28x28 image
      const weightsSize = inputSize * 128 + 128 * 64 + 64 * 10; // Simple 3-layer network

      const input = Array.from({ length: inputSize }, () => Math.random());
      const weights = Array.from({ length: weightsSize }, () => Math.random() - 0.5);

      await runNeuralNetworkInference(input, weights, 3, {
        useSimd: true,
        precision: 'f32',
        priority: 'high'
      });
    } catch (error) {
      console.error('ML inference test failed:', error);
    }
  }, [isInitialized, runNeuralNetworkInference]);

  // Get status color for tasks
  const getTaskStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'text-green-600';
      case 'running': return 'text-blue-600';
      case 'error': return 'text-red-600';
      default: return 'text-yellow-600';
    }
  };

  const getTaskStatusIcon = (status: string) => {
    switch (status) {
      case 'completed': return CheckCircle;
      case 'running': return Loader2;
      case 'error': return AlertTriangle;
      default: return Clock;
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
            WASM Performance Dashboard
          </h1>
          <p className="text-muted-foreground mt-1">
            High-performance computing with WebAssembly and multithreading
          </p>
        </div>

        <div className="flex items-center space-x-3">
          {/* WASM Status */}
          <div className={`flex items-center space-x-2 px-3 py-1 rounded-full ${
            isInitialized 
              ? 'bg-green-100 text-green-800' 
              : isInitializing 
                ? 'bg-yellow-100 text-yellow-800'
                : 'bg-red-100 text-red-800'
          }`}>
            {isInitializing ? (
              <Loader2 className="w-4 h-4 animate-spin" />
            ) : isInitialized ? (
              <Zap className="w-4 h-4" />
            ) : (
              <AlertTriangle className="w-4 h-4" />
            )}
            <span className="text-sm font-medium">
              {isInitializing ? 'Initializing...' : isInitialized ? 'WASM Ready' : 'WASM Error'}
            </span>
          </div>

          {/* Clear Tasks */}
          <Button
            variant="outline"
            size="sm"
            onClick={clearTasks}
            disabled={activeTasks.size === 0}
          >
            <RotateCcw className="w-4 h-4 mr-1" />
            Clear
          </Button>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <Card className="border-red-200 bg-red-50">
          <CardContent className="pt-4">
            <div className="flex items-center space-x-2 text-red-600">
              <AlertTriangle className="w-5 h-5" />
              <span className="font-medium">WASM Error:</span>
            </div>
            <p className="text-red-700 mt-1">{error}</p>
          </CardContent>
        </Card>
      )}

      {/* Capabilities Overview */}
      {capabilities && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">WASM Support</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2">
                <CheckCircle className="w-5 h-5 text-green-600" />
                <span className="font-medium">Enabled</span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                {capabilities.availableFunctions.length} functions available
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">SIMD Acceleration</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2">
                <Sparkles className="w-5 h-5 text-purple-600" />
                <span className="font-medium">
                  {capabilities.simdSupported ? 'Supported' : 'Not Available'}
                </span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                Vector processing enabled
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">Memory</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2">
                <Memory className="w-5 h-5 text-blue-600" />
                <span className="font-medium">
                  {Math.round(capabilities.memorySize / 1024 / 1024)} MB
                </span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                Linear memory allocated
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">Threading</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2">
                <Cpu className="w-5 h-5 text-orange-600" />
                <span className="font-medium">
                  {capabilities.threadsSupported ? 'Supported' : 'Single Thread'}
                </span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                {threadPools.length} Katalyst pools active
              </p>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Main Dashboard */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="benchmarks">Benchmarks</TabsTrigger>
          <TabsTrigger value="operations">Operations</TabsTrigger>
          <TabsTrigger value="monitoring">Monitoring</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Performance Stats */}
            <Card>
              <CardHeader>
                <CardTitle>Performance Statistics</CardTitle>
                <CardDescription>Real-time WASM operation metrics</CardDescription>
              </CardHeader>
              <CardContent>
                {performanceStats ? (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-muted-foreground">Operations Completed</span>
                        <div className="font-medium text-2xl">{performanceStats.operationsCount}</div>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Success Rate</span>
                        <div className="font-medium text-2xl">{performanceStats.successRate.toFixed(1)}%</div>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Avg Execution Time</span>
                        <div className="font-medium text-2xl">{performanceStats.averageExecutionTime.toFixed(0)}ms</div>
                      </div>
                      <div>
                        <span className="text-muted-foreground">SIMD Utilization</span>
                        <div className="font-medium text-2xl">{performanceStats.simdUtilization.toFixed(0)}%</div>
                      </div>
                    </div>

                    <div className="space-y-2">
                      <div className="flex justify-between text-sm">
                        <span>Memory Efficiency</span>
                        <span>{(performanceStats.totalMemoryUsed / 1024 / 1024).toFixed(1)} MB used</span>
                      </div>
                      <Progress value={Math.min((performanceStats.totalMemoryUsed / (16 * 1024 * 1024)) * 100, 100)} />
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <Activity className="mx-auto h-8 w-8 text-muted-foreground mb-2" />
                    <p className="text-muted-foreground">No performance data yet</p>
                    <p className="text-sm text-muted-foreground mt-1">Run some operations to see statistics</p>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Quick Operations */}
            <Card>
              <CardHeader>
                <CardTitle>Quick Performance Tests</CardTitle>
                <CardDescription>Run individual WASM operations</CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="space-y-3">
                  <div className="space-y-2">
                    <Label htmlFor="matrixSize">Matrix Size: {matrixSize}x{matrixSize}</Label>
                    <Input
                      id="matrixSize"
                      type="range"
                      min="128"
                      max="1024"
                      step="128"
                      value={matrixSize}
                      onChange={(e) => setMatrixSize(parseInt(e.target.value))}
                    />
                  </div>

                  <Button 
                    className="w-full justify-start" 
                    variant="outline"
                    onClick={runMatrixTest}
                    disabled={!isInitialized}
                  >
                    <Calculator className="w-4 h-4 mr-2" />
                    Matrix Multiplication ({matrixSize}x{matrixSize})
                  </Button>
                </div>

                <div className="space-y-3">
                  <div className="space-y-2">
                    <Label htmlFor="fibonacciN">Fibonacci N: {fibonacciN}</Label>
                    <Input
                      id="fibonacciN"
                      type="range"
                      min="30"
                      max="45"
                      value={fibonacciN}
                      onChange={(e) => setFibonacciN(parseInt(e.target.value))}
                    />
                  </div>

                  <Button 
                    className="w-full justify-start" 
                    variant="outline"
                    onClick={runFibonacciTest}
                    disabled={!isInitialized}
                  >
                    <Target className="w-4 h-4 mr-2" />
                    Parallel Fibonacci ({fibonacciN})
                  </Button>
                </div>

                <Button 
                  className="w-full justify-start" 
                  variant="outline"
                  onClick={runImageProcessingTest}
                  disabled={!isInitialized}
                >
                  <Image className="w-4 h-4 mr-2" />
                  Image Processing (1920x1080)
                </Button>

                <Button 
                  className="w-full justify-start" 
                  variant="outline"
                  onClick={runMLInferenceTest}
                  disabled={!isInitialized}
                >
                  <Brain className="w-4 h-4 mr-2" />
                  Neural Network Inference
                </Button>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="benchmarks" className="space-y-6">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between">
              <div>
                <CardTitle>Comprehensive Benchmark Suite</CardTitle>
                <CardDescription>Full system performance evaluation</CardDescription>
              </div>
              <Button
                onClick={runComprehensiveBenchmarks}
                disabled={!isInitialized || isBenchmarkRunning}
                className="flex items-center space-x-2"
              >
                {isBenchmarkRunning ? (
                  <Loader2 className="w-4 h-4 animate-spin" />
                ) : (
                  <Play className="w-4 h-4" />
                )}
                <span>{isBenchmarkRunning ? 'Running...' : 'Start Benchmarks'}</span>
              </Button>
            </CardHeader>
            <CardContent>
              {isBenchmarkRunning && (
                <div className="space-y-4 mb-6">
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">
                      {currentBenchmark || 'Preparing benchmarks...'}
                    </span>
                    <span className="text-sm text-muted-foreground">
                      {benchmarkProgress.toFixed(0)}%
                    </span>
                  </div>
                  <Progress value={benchmarkProgress} />
                </div>
              )}

              {benchmarkResults.length > 0 && (
                <div className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {benchmarkResults.map((result, index) => (
                      <motion.div
                        key={index}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: index * 0.1 }}
                        className="p-4 border rounded-lg"
                      >
                        <div className="flex items-center justify-between mb-2">
                          <h4 className="font-medium text-sm">{result.name}</h4>
                          <Badge variant={result.score > 500 ? 'default' : 'secondary'}>
                            {result.score} pts
                          </Badge>
                        </div>
                        <div className="space-y-1 text-xs text-muted-foreground">
                          <div className="flex justify-between">
                            <span>Execution Time:</span>
                            <span>{result.executionTime.toFixed(2)}ms</span>
                          </div>
                          <div className="flex justify-between">
                            <span>Operations:</span>
                            <span>{result.operations.toLocaleString()}</span>
                          </div>
                        </div>
                        <Progress 
                          value={Math.min((result.score / 1000) * 100, 100)} 
                          className="mt-2" 
                        />
                      </motion.div>
                    ))}
                  </div>

                  {benchmarkResults.length > 0 && (
                    <div className="p-4 bg-muted/50 rounded-lg">
                      <div className="flex items-center space-x-2 mb-2">
                        <Award className="w-5 h-5 text-primary" />
                        <span className="font-medium">Overall Score</span>
                      </div>
                      <div className="text-2xl font-bold text-primary">
                        {Math.round(benchmarkResults.reduce((sum, r) => sum + r.score, 0) / benchmarkResults.length)} points
                      </div>
                      <p className="text-sm text-muted-foreground mt-1">
                        System Performance Rating: {
                          benchmarkResults.reduce((sum, r) => sum + r.score, 0) / benchmarkResults.length > 600 
                            ? 'Excellent' 
                            : benchmarkResults.reduce((sum, r) => sum + r.score, 0) / benchmarkResults.length > 400 
                              ? 'Good' 
                              : 'Fair'
                        }
                      </p>
                    </div>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="operations" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Active WASM Operations</CardTitle>
              <CardDescription>Real-time monitoring of running tasks</CardDescription>
            </CardHeader>
            <CardContent>
              {activeTasks.size === 0 ? (
                <div className="text-center py-12">
                  <Code2 className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                  <p className="text-muted-foreground">No active operations</p>
                  <p className="text-sm text-muted-foreground mt-1">
                    Start some performance tests to see running operations
                  </p>
                </div>
              ) : (
                <div className="space-y-4">
                  {Array.from(activeTasks.values()).map((task) => {
                    const StatusIcon = getTaskStatusIcon(task.status);
                    return (
                      <motion.div
                        key={task.id}
                        initial={{ opacity: 0, scale: 0.95 }}
                        animate={{ opacity: 1, scale: 1 }}
                        className="flex items-center justify-between p-4 border rounded-lg"
                      >
                        <div className="flex items-center space-x-4">
                          <StatusIcon className={`w-5 h-5 ${getTaskStatusColor(task.status)} ${
                            task.status === 'running' ? 'animate-spin' : ''
                          }`} />
                          <div>
                            <h4 className="font-medium">{task.operation.replace('_', ' ')}</h4>
                            <div className="flex items-center space-x-2 text-sm text-muted-foreground">
                              <Badge variant="outline">{task.status}</Badge>
                              <span>Started {new Date(task.startTime).toLocaleTimeString()}</span>
                              {task.metrics && (
                                <span>{task.metrics.executionTime.toFixed(0)}ms</span>
                              )}
                            </div>
                          </div>
                        </div>

                        <div className="text-right space-y-1">
                          <div className="text-sm font-medium">{task.progress.toFixed(0)}%</div>
                          <Progress value={task.progress} className="w-24" />
                        </div>
                      </motion.div>
                    );
                  })}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="monitoring" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle>System Resources</CardTitle>
                <CardDescription>WASM runtime resource utilization</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div className="flex justify-between items-center">
                    <span className="text-sm">Memory Usage</span>
                    <span className="text-sm font-medium">
                      {capabilities ? Math.round(capabilities.memorySize / 1024 / 1024) : 0} MB
                    </span>
                  </div>
                  <Progress value={capabilities ? 40 : 0} />

                  <div className="flex justify-between items-center">
                    <span className="text-sm">Active Operations</span>
                    <span className="text-sm font-medium">{activeTasks.size}</span>
                  </div>
                  <Progress value={Math.min((activeTasks.size / 10) * 100, 100)} />

                  <div className="flex justify-between items-center">
                    <span className="text-sm">Thread Utilization</span>
                    <span className="text-sm font-medium">{threadPools.length} pools</span>
                  </div>
                  <Progress value={Math.min((executingTasks / 20) * 100, 100)} />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>Configuration</CardTitle>
                <CardDescription>WASM runtime settings</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div className="space-y-2">
                    <Label htmlFor="threadCount">Thread Count: {threadCount}</Label>
                    <Input
                      id="threadCount"
                      type="range"
                      min="1"
                      max={navigator.hardwareConcurrency || 8}
                      value={threadCount}
                      onChange={(e) => setThreadCount(parseInt(e.target.value))}
                    />
                    <p className="text-xs text-muted-foreground">
                      Hardware Concurrency: {navigator.hardwareConcurrency || 'Unknown'}
                    </p>
                  </div>

                  <div className="space-y-2">
                    <Label>Features</Label>
                    <div className="space-y-2">
                      <div className="flex items-center justify-between">
                        <span className="text-sm">SIMD Acceleration</span>
                        <Badge variant={capabilities?.simdSupported ? 'default' : 'secondary'}>
                          {capabilities?.simdSupported ? 'Enabled' : 'Disabled'}
                        </Badge>
                      </div>
                      <div className="flex items-center justify-between">
                        <span className="text-sm">Shared Array Buffer</span>
                        <Badge variant={capabilities?.threadsSupported ? 'default' : 'secondary'}>
                          {capabilities?.threadsSupported ? 'Supported' : 'Not Available'}
                        </Badge>
                      </div>
                      <div className="flex items-center justify-between">
                        <span className="text-sm">Memory Growth</span>
                        <Badge variant="default">Enabled</Badge>
                      </div>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

toolUseResult.content:
import { useState, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Core Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useMultithreading } from '@katalyst/hooks/use-multithreading';

// Custom Hook
import { useYogiWASM } from '../../hooks/use-yogi-wasm';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@katalyst/design-system/ui/card';
import { Progress } from '@katalyst/design-system/ui/progress';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@katalyst/design-system/ui/tabs';
import { Input } from '@katalyst/design-system/ui/input';
import { Label } from '@katalyst/design-system/ui/label';

// Icons
import {
  Zap, Cpu, Brain, Activity, Play, Square, RotateCcw,
  BarChart3, TrendingUp, Settings, Clock, Memory,
  HardDrive, Gauge, CheckCircle, AlertTriangle,
  Loader2, Sparkles, Code2, Image, Calculator,
  Target, Award
} from 'lucide-react';

interface BenchmarkResult {
  name: string;
  score: number;
  executionTime: number;
  operations: number;
}

export function WASMPerformanceDashboard() {
  const [activeTab, setActiveTab] = useState('overview');
  const [benchmarkProgress, setBenchmarkProgress] = useState(0);
  const [currentBenchmark, setCurrentBenchmark] = useState('');
  const [benchmarkResults, setBenchmarkResults] = useState<BenchmarkResult[]>([]);
  const [isBenchmarkRunning, setIsBenchmarkRunning] = useState(false);
  const [matrixSize, setMatrixSize] = useState(512);
  const [fibonacciN, setFibonacciN] = useState(35);
  const [threadCount, setThreadCount] = useState(4);

  // Katalyst Hooks
  const { executeTask } = useMultithreadingStore();
  const { threadPools, executingTasks, createWorkerPool } = useMultithreading();

  // WASM Hook
  const {
    isInitialized,
    isInitializing,
    capabilities,
    activeTasks,
    error,
    runMatrixMultiplication,
    processImage,
    runNeuralNetworkInference,
    calculateFibonacci,
    runBenchmarkSuite,
    runOperation,
    clearTasks,
    getPerformanceStats
  } = useYogiWASM();

  const performanceStats = getPerformanceStats();

  // Run comprehensive benchmark suite
  const runComprehensiveBenchmarks = useCallback(async () => {
    if (!isInitialized || isBenchmarkRunning) return;

    setIsBenchmarkRunning(true);
    setBenchmarkProgress(0);
    setBenchmarkResults([]);
    clearTasks();

    try {
      const results = await runBenchmarkSuite((progress, data) => {
        setBenchmarkProgress(progress);
        if (data?.currentBenchmark) {
          setCurrentBenchmark(data.currentBenchmark);
        }
      });

      setBenchmarkResults(results.benchmarks || []);
    } catch (error) {
      console.error('Benchmark suite failed:', error);
    } finally {
      setIsBenchmarkRunning(false);
      setCurrentBenchmark('');
    }
  }, [isInitialized, isBenchmarkRunning, runBenchmarkSuite, clearTasks]);

  // Run individual performance tests
  const runMatrixTest = useCallback(async () => {
    if (!isInitialized) return;

    try {
      // Create random matrices
      const matrixA = Array.from({ length: matrixSize }, () =>
        Array.from({ length: matrixSize }, () => Math.random())
      );
      const matrixB = Array.from({ length: matrixSize }, () =>
        Array.from({ length: matrixSize }, () => Math.random())
      );

      await runMatrixMultiplication(matrixA, matrixB, {
        useSimd: true,
        threadCount,
        priority: 'high'
      });
    } catch (error) {
      console.error('Matrix test failed:', error);
    }
  }, [isInitialized, matrixSize, threadCount, runMatrixMultiplication]);

  const runFibonacciTest = useCallback(async () => {
    if (!isInitialized) return;

    try {
      await calculateFibonacci(fibonacciN, threadCount);
    } catch (error) {
      console.error('Fibonacci test failed:', error);
    }
  }, [isInitialized, fibonacciN, threadCount, calculateFibonacci]);

  const runImageProcessingTest = useCallback(async () => {
    if (!isInitialized) return;

    try {
      // Create test image data
      const width = 1920;
      const height = 1080;
      const imageData = new ImageData(width, height);

      // Fill with test pattern
      for (let i = 0; i < imageData.data.length; i += 4) {
        imageData.data[i] = Math.floor(Math.random() * 256);     // R
        imageData.data[i + 1] = Math.floor(Math.random() * 256); // G
        imageData.data[i + 2] = Math.floor(Math.random() * 256); // B
        imageData.data[i + 3] = 255; // A
      }

      await processImage(imageData, 'edge_detect', {
        useSimd: true,
        threadCount,
        priority: 'high'
      });
    } catch (error) {
      console.error('Image processing test failed:', error);
    }
  }, [isInitialized, threadCount, processImage]);

  const runMLInferenceTest = useCallback(async () => {
    if (!isInitialized) return;

    try {
      // Mock neural network input and weights
      const inputSize = 784; // 28x28 image
      const weightsSize = inputSize * 128 + 128 * 64 + 64 * 10; // Simple 3-layer network

      const input = Array.from({ length: inputSize }, () => Math.random());
      const weights = Array.from({ length: weightsSize }, () => Math.random() - 0.5);

      await runNeuralNetworkInference(input, weights, 3, {
        useSimd: true,
        precision: 'f32',
        priority: 'high'
      });
    } catch (error) {
      console.error('ML inference test failed:', error);
    }
  }, [isInitialized, runNeuralNetworkInference]);

  // Get status color for tasks
  const getTaskStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'text-green-600';
      case 'running': return 'text-blue-600';
      case 'error': return 'text-red-600';
      default: return 'text-yellow-600';
    }
  };

  const getTaskStatusIcon = (status: string) => {
    switch (status) {
      case 'completed': return CheckCircle;
      case 'running': return Loader2;
      case 'error': return AlertTriangle;
      default: return Clock;
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
            WASM Performance Dashboard
          </h1>
          <p className="text-muted-foreground mt-1">
            High-performance computing with WebAssembly and multithreading
          </p>
        </div>

        <div className="flex items-center space-x-3">
          {/* WASM Status */}
          <div className={`flex items-center space-x-2 px-3 py-1 rounded-full ${
            isInitialized 
              ? 'bg-green-100 text-green-800' 
              : isInitializing 
                ? 'bg-yellow-100 text-yellow-800'
                : 'bg-red-100 text-red-800'
          }`}>
            {isInitializing ? (
              <Loader2 className="w-4 h-4 animate-spin" />
            ) : isInitialized ? (
              <Zap className="w-4 h-4" />
            ) : (
              <AlertTriangle className="w-4 h-4" />
            )}
            <span className="text-sm font-medium">
              {isInitializing ? 'Initializing...' : isInitialized ? 'WASM Ready' : 'WASM Error'}
            </span>
          </div>

          {/* Clear Tasks */}
          <Button
            variant="outline"
            size="sm"
            onClick={clearTasks}
            disabled={activeTasks.size === 0}
          >
            <RotateCcw className="w-4 h-4 mr-1" />
            Clear
          </Button>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <Card className="border-red-200 bg-red-50">
          <CardContent className="pt-4">
            <div className="flex items-center space-x-2 text-red-600">
              <AlertTriangle className="w-5 h-5" />
              <span className="font-medium">WASM Error:</span>
            </div>
            <p className="text-red-700 mt-1">{error}</p>
          </CardContent>
        </Card>
      )}

      {/* Capabilities Overview */}
      {capabilities && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">WASM Support</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2">
                <CheckCircle className="w-5 h-5 text-green-600" />
                <span className="font-medium">Enabled</span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                {capabilities.availableFunctions.length} functions available
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">SIMD Acceleration</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2">
                <Sparkles className="w-5 h-5 text-purple-600" />
                <span className="font-medium">
                  {capabilities.simdSupported ? 'Supported' : 'Not Available'}
                </span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                Vector processing enabled
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">Memory</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2">
                <Memory className="w-5 h-5 text-blue-600" />
                <span className="font-medium">
                  {Math.round(capabilities.memorySize / 1024 / 1024)} MB
                </span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                Linear memory allocated
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">Threading</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2">
                <Cpu className="w-5 h-5 text-orange-600" />
                <span className="font-medium">
                  {capabilities.threadsSupported ? 'Supported' : 'Single Thread'}
                </span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                {threadPools.length} Katalyst pools active
              </p>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Main Dashboard */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="benchmarks">Benchmarks</TabsTrigger>
          <TabsTrigger value="operations">Operations</TabsTrigger>
          <TabsTrigger value="monitoring">Monitoring</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Performance Stats */}
            <Card>
              <CardHeader>
                <CardTitle>Performance Statistics</CardTitle>
                <CardDescription>Real-time WASM operation metrics</CardDescription>
              </CardHeader>
              <CardContent>
                {performanceStats ? (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-muted-foreground">Operations Completed</span>
                        <div className="font-medium text-2xl">{performanceStats.operationsCount}</div>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Success Rate</span>
                        <div className="font-medium text-2xl">{performanceStats.successRate.toFixed(1)}%</div>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Avg Execution Time</span>
                        <div className="font-medium text-2xl">{performanceStats.averageExecutionTime.toFixed(0)}ms</div>
                      </div>
                      <div>
                        <span className="text-muted-foreground">SIMD Utilization</span>
                        <div className="font-medium text-2xl">{performanceStats.simdUtilization.toFixed(0)}%</div>
                      </div>
                    </div>

                    <div className="space-y-2">
                      <div className="flex justify-between text-sm">
                        <span>Memory Efficiency</span>
                        <span>{(performanceStats.totalMemoryUsed / 1024 / 1024).toFixed(1)} MB used</span>
                      </div>
                      <Progress value={Math.min((performanceStats.totalMemoryUsed / (16 * 1024 * 1024)) * 100, 100)} />
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <Activity className="mx-auto h-8 w-8 text-muted-foreground mb-2" />
                    <p className="text-muted-foreground">No performance data yet</p>
                    <p className="text-sm text-muted-foreground mt-1">Run some operations to see statistics</p>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Quick Operations */}
            <Card>
              <CardHeader>
                <CardTitle>Quick Performance Tests</CardTitle>
                <CardDescription>Run individual WASM operations</CardDescription>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="space-y-3">
                  <div className="space-y-2">
                    <Label htmlFor="matrixSize">Matrix Size: {matrixSize}x{matrixSize}</Label>
                    <Input
                      id="matrixSize"
                      type="range"
                      min="128"
                      max="1024"
                      step="128"
                      value={matrixSize}
                      onChange={(e) => setMatrixSize(parseInt(e.target.value))}
                    />
                  </div>

                  <Button 
                    className="w-full justify-start" 
                    variant="outline"
                    onClick={runMatrixTest}
                    disabled={!isInitialized}
                  >
                    <Calculator className="w-4 h-4 mr-2" />
                    Matrix Multiplication ({matrixSize}x{matrixSize})
                  </Button>
                </div>

                <div className="space-y-3">
                  <div className="space-y-2">
                    <Label htmlFor="fibonacciN">Fibonacci N: {fibonacciN}</Label>
                    <Input
                      id="fibonacciN"
                      type="range"
                      min="30"
                      max="45"
                      value={fibonacciN}
                      onChange={(e) => setFibonacciN(parseInt(e.target.value))}
                    />
                  </div>

                  <Button 
                    className="w-full justify-start" 
                    variant="outline"
                    onClick={runFibonacciTest}
                    disabled={!isInitialized}
                  >
                    <Target className="w-4 h-4 mr-2" />
                    Parallel Fibonacci ({fibonacciN})
                  </Button>
                </div>

                <Button 
                  className="w-full justify-start" 
                  variant="outline"
                  onClick={runImageProcessingTest}
                  disabled={!isInitialized}
                >
                  <Image className="w-4 h-4 mr-2" />
                  Image Processing (1920x1080)
                </Button>

                <Button 
                  className="w-full justify-start" 
                  variant="outline"
                  onClick={runMLInferenceTest}
                  disabled={!isInitialized}
                >
                  <Brain className="w-4 h-4 mr-2" />
                  Neural Network Inference
                </Button>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="benchmarks" className="space-y-6">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between">
              <div>
                <CardTitle>Comprehensive Benchmark Suite</CardTitle>
                <CardDescription>Full system performance evaluation</CardDescription>
              </div>
              <Button
                onClick={runComprehensiveBenchmarks}
                disabled={!isInitialized || isBenchmarkRunning}
                className="flex items-center space-x-2"
              >
                {isBenchmarkRunning ? (
                  <Loader2 className="w-4 h-4 animate-spin" />
                ) : (
                  <Play className="w-4 h-4" />
                )}
                <span>{isBenchmarkRunning ? 'Running...' : 'Start Benchmarks'}</span>
              </Button>
            </CardHeader>
            <CardContent>
              {isBenchmarkRunning && (
                <div className="space-y-4 mb-6">
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">
                      {currentBenchmark || 'Preparing benchmarks...'}
                    </span>
                    <span className="text-sm text-muted-foreground">
                      {benchmarkProgress.toFixed(0)}%
                    </span>
                  </div>
                  <Progress value={benchmarkProgress} />
                </div>
              )}

              {benchmarkResults.length > 0 && (
                <div className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {benchmarkResults.map((result, index) => (
                      <motion.div
                        key={index}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: index * 0.1 }}
                        className="p-4 border rounded-lg"
                      >
                        <div className="flex items-center justify-between mb-2">
                          <h4 className="font-medium text-sm">{result.name}</h4>
                          <Badge variant={result.score > 500 ? 'default' : 'secondary'}>
                            {result.score} pts
                          </Badge>
                        </div>
                        <div className="space-y-1 text-xs text-muted-foreground">
                          <div className="flex justify-between">
                            <span>Execution Time:</span>
                            <span>{result.executionTime.toFixed(2)}ms</span>
                          </div>
                          <div className="flex justify-between">
                            <span>Operations:</span>
                            <span>{result.operations.toLocaleString()}</span>
                          </div>
                        </div>
                        <Progress 
                          value={Math.min((result.score / 1000) * 100, 100)} 
                          className="mt-2" 
                        />
                      </motion.div>
                    ))}
                  </div>

                  {benchmarkResults.length > 0 && (
                    <div className="p-4 bg-muted/50 rounded-lg">
                      <div className="flex items-center space-x-2 mb-2">
                        <Award className="w-5 h-5 text-primary" />
                        <span className="font-medium">Overall Score</span>
                      </div>
                      <div className="text-2xl font-bold text-primary">
                        {Math.round(benchmarkResults.reduce((sum, r) => sum + r.score, 0) / benchmarkResults.length)} points
                      </div>
                      <p className="text-sm text-muted-foreground mt-1">
                        System Performance Rating: {
                          benchmarkResults.reduce((sum, r) => sum + r.score, 0) / benchmarkResults.length > 600 
                            ? 'Excellent' 
                            : benchmarkResults.reduce((sum, r) => sum + r.score, 0) / benchmarkResults.length > 400 
                              ? 'Good' 
                              : 'Fair'
                        }
                      </p>
                    </div>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="operations" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Active WASM Operations</CardTitle>
              <CardDescription>Real-time monitoring of running tasks</CardDescription>
            </CardHeader>
            <CardContent>
              {activeTasks.size === 0 ? (
                <div className="text-center py-12">
                  <Code2 className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                  <p className="text-muted-foreground">No active operations</p>
                  <p className="text-sm text-muted-foreground mt-1">
                    Start some performance tests to see running operations
                  </p>
                </div>
              ) : (
                <div className="space-y-4">
                  {Array.from(activeTasks.values()).map((task) => {
                    const StatusIcon = getTaskStatusIcon(task.status);
                    return (
                      <motion.div
                        key={task.id}
                        initial={{ opacity: 0, scale: 0.95 }}
                        animate={{ opacity: 1, scale: 1 }}
                        className="flex items-center justify-between p-4 border rounded-lg"
                      >
                        <div className="flex items-center space-x-4">
                          <StatusIcon className={`w-5 h-5 ${getTaskStatusColor(task.status)} ${
                            task.status === 'running' ? 'animate-spin' : ''
                          }`} />
                          <div>
                            <h4 className="font-medium">{task.operation.replace('_', ' ')}</h4>
                            <div className="flex items-center space-x-2 text-sm text-muted-foreground">
                              <Badge variant="outline">{task.status}</Badge>
                              <span>Started {new Date(task.startTime).toLocaleTimeString()}</span>
                              {task.metrics && (
                                <span>{task.metrics.executionTime.toFixed(0)}ms</span>
                              )}
                            </div>
                          </div>
                        </div>

                        <div className="text-right space-y-1">
                          <div className="text-sm font-medium">{task.progress.toFixed(0)}%</div>
                          <Progress value={task.progress} className="w-24" />
                        </div>
                      </motion.div>
                    );
                  })}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="monitoring" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle>System Resources</CardTitle>
                <CardDescription>WASM runtime resource utilization</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div className="flex justify-between items-center">
                    <span className="text-sm">Memory Usage</span>
                    <span className="text-sm font-medium">
                      {capabilities ? Math.round(capabilities.memorySize / 1024 / 1024) : 0} MB
                    </span>
                  </div>
                  <Progress value={capabilities ? 40 : 0} />

                  <div className="flex justify-between items-center">
                    <span className="text-sm">Active Operations</span>
                    <span className="text-sm font-medium">{activeTasks.size}</span>
                  </div>
                  <Progress value={Math.min((activeTasks.size / 10) * 100, 100)} />

                  <div className="flex justify-between items-center">
                    <span className="text-sm">Thread Utilization</span>
                    <span className="text-sm font-medium">{threadPools.length} pools</span>
                  </div>
                  <Progress value={Math.min((executingTasks / 20) * 100, 100)} />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>Configuration</CardTitle>
                <CardDescription>WASM runtime settings</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div className="space-y-2">
                    <Label htmlFor="threadCount">Thread Count: {threadCount}</Label>
                    <Input
                      id="threadCount"
                      type="range"
                      min="1"
                      max={navigator.hardwareConcurrency || 8}
                      value={threadCount}
                      onChange={(e) => setThreadCount(parseInt(e.target.value))}
                    />
                    <p className="text-xs text-muted-foreground">
                      Hardware Concurrency: {navigator.hardwareConcurrency || 'Unknown'}
                    </p>
                  </div>

                  <div className="space-y-2">
                    <Label>Features</Label>
                    <div className="space-y-2">
                      <div className="flex items-center justify-between">
                        <span className="text-sm">SIMD Acceleration</span>
                        <Badge variant={capabilities?.simdSupported ? 'default' : 'secondary'}>
                          {capabilities?.simdSupported ? 'Enabled' : 'Disabled'}
                        </Badge>
                      </div>
                      <div className="flex items-center justify-between">
                        <span className="text-sm">Shared Array Buffer</span>
                        <Badge variant={capabilities?.threadsSupported ? 'default' : 'secondary'}>
                          {capabilities?.threadsSupported ? 'Supported' : 'Not Available'}
                        </Badge>
                      </div>
                      <div className="flex items-center justify-between">
                        <span className="text-sm">Memory Growth</span>
                        <Badge variant="default">Enabled</Badge>
                      </div>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

