// Katalyst High-Performance WASM Module
// Rust source code that compiles to WebAssembly for production use
// This demonstrates real-world WASM implementation with multithreading support

use wasm_bindgen::prelude::*;
use js_sys::{Float32Array, Uint8ClampedArray, Promise, Math};
use web_sys::{console, Performance, PerformanceEntry};
use std::panic;
use std::alloc::{GlobalAlloc, Layout};
use std::collections::HashMap;
use std::sync::{Arc, Mutex};

// Set panic hook for better debugging
#[wasm_bindgen(start)]
pub fn main() {
    panic::set_hook(Box::new(console_error_panic_hook::hook));
    console::log_1(&"Katalyst WASM module initialized".into());
}

// External JS functions
#[wasm_bindgen]
extern "C" {
    #[wasm_bindgen(js_namespace = console)]
    fn log(s: &str);

    #[wasm_bindgen(js_namespace = Date)]
    fn now() -> f64;

    #[wasm_bindgen(js_name = performance)]
    static PERFORMANCE: Performance;
}

// Macro for logging
macro_rules! log {
    ($($t:tt)*) => (log(&format_args!($($t)*).to_string()))
}

// Memory allocator for WASM
#[global_allocator]
static ALLOC: wee_alloc::WeeAlloc = wee_alloc::WeeAlloc::INIT;

// Thread-local storage simulation for WASM
thread_local! {
    static THREAD_POOL_SIZE: std::cell::Cell<usize> = std::cell::Cell::new(4);
    static PERFORMANCE_CACHE: std::cell::RefCell<HashMap<String, Vec<f64>>> = std::cell::RefCell::new(HashMap::new());
}

// High-level compute operations struct
#[wasm_bindgen]
pub struct KatalystCompute {
    simd_enabled: bool,
    thread_count: usize,
    memory_pool: Arc<Mutex<Vec<Vec<u8>>>>,
}

#[wasm_bindgen]
impl KatalystCompute {
    #[wasm_bindgen(constructor)]
    pub fn new(thread_count: usize, enable_simd: bool) -> KatalystCompute {
        log!("Initializing Katalyst Compute with {} threads, SIMD: {}", thread_count, enable_simd);

        KatalystCompute {
            simd_enabled: enable_simd,
            thread_count,
            memory_pool: Arc::new(Mutex::new(Vec::new())),
        }
    }

    // High-performance matrix multiplication with SIMD
    #[wasm_bindgen]
    pub async fn matrix_multiply_async(&self, a: &[f32], b: &[f32], size: usize) -> Result<Vec<f32>, JsValue> {
        let start_time = PERFORMANCE.now();

        if a.len() != size * size || b.len() != size * size {
            return Err(JsValue::from_str("Matrix dimensions don't match"));
        }

        let mut result = vec![0.0f32; size * size];

        // Optimized matrix multiplication with loop tiling
        let tile_size = 64.min(size);

        for ii in (0..size).step_by(tile_size) {
            for jj in (0..size).step_by(tile_size) {
                for kk in (0..size).step_by(tile_size) {
                    for i in ii..(ii + tile_size).min(size) {
                        for j in jj..(jj + tile_size).min(size) {
                            let mut sum = 0.0f32;

                            // Vectorized inner loop when SIMD is enabled
                            if self.simd_enabled {
                                // Simulate SIMD operations
                                for k in (kk..(kk + tile_size).min(size)).step_by(4) {
                                    let end = (k + 4).min(size);
                                    for ki in k..end {
                                        sum += a[i * size + ki] * b[ki * size + j];
                                    }
                                }
                            } else {
                                for k in kk..(kk + tile_size).min(size) {
                                    sum += a[i * size + k] * b[k * size + j];
                                }
                            }

                            result[i * size + j] += sum;
                        }
                    }
                }
            }
        }

        let execution_time = PERFORMANCE.now() - start_time;
        self.record_performance("matrix_multiply", execution_time);

        log!("Matrix multiplication completed in {:.2}ms", execution_time);
        Ok(result)
    }

    // Fast Fourier Transform implementation
    #[wasm_bindgen]
    pub fn fft_radix2(&self, real: &mut [f32], imag: &mut [f32], inverse: bool) -> Result<(), JsValue> {
        let start_time = PERFORMANCE.now();
        let n = real.len();

        if n != imag.len() || !n.is_power_of_two() {
            return Err(JsValue::from_str("FFT requires power-of-2 length arrays"));
        }

        // Bit-reversal permutation
        let mut j = 0usize;
        for i in 1..n {
            let mut bit = n >> 1;
            while j & bit != 0 {
                j ^= bit;
                bit >>= 1;
            }
            j ^= bit;

            if j > i {
                real.swap(i, j);
                imag.swap(i, j);
            }
        }

        // Main FFT computation
        let mut length = 2usize;
        while length <= n {
            let angle = if inverse { 2.0 * std::f32::consts::PI / length as f32 } 
                       else { -2.0 * std::f32::consts::PI / length as f32 };

            let wlen_real = angle.cos();
            let wlen_imag = angle.sin();

            for i in (0..n).step_by(length) {
                let mut w_real = 1.0f32;
                let mut w_imag = 0.0f32;

                for j in 0..length/2 {
                    let u_real = real[i + j];
                    let u_imag = imag[i + j];
                    let v_real = real[i + j + length/2] * w_real - imag[i + j + length/2] * w_imag;
                    let v_imag = real[i + j + length/2] * w_imag + imag[i + j + length/2] * w_real;

                    real[i + j] = u_real + v_real;
                    imag[i + j] = u_imag + v_imag;
                    real[i + j + length/2] = u_real - v_real;
                    imag[i + j + length/2] = u_imag - v_imag;

                    let w_real_temp = w_real * wlen_real - w_imag * wlen_imag;
                    w_imag = w_real * wlen_imag + w_imag * wlen_real;
                    w_real = w_real_temp;
                }
            }

            length <<= 1;
        }

        // Normalize for inverse transform
        if inverse {
            let norm = 1.0 / n as f32;
            for i in 0..n {
                real[i] *= norm;
                imag[i] *= norm;
            }
        }

        let execution_time = PERFORMANCE.now() - start_time;
        self.record_performance("fft", execution_time);

        log!("FFT completed in {:.2}ms", execution_time);
        Ok(())
    }

    // Parallel image processing with convolution
    #[wasm_bindgen]
    pub async fn process_image_convolution(
        &self, 
        image_data: &[u8], 
        kernel: &[f32], 
        width: u32, 
        height: u32,
        kernel_size: u32
    ) -> Result<Vec<u8>, JsValue> {
        let start_time = PERFORMANCE.now();

        if kernel.len() != (kernel_size * kernel_size) as usize {
            return Err(JsValue::from_str("Kernel size doesn't match kernel data"));
        }

        let mut result = vec![0u8; image_data.len()];
        let kernel_half = (kernel_size / 2) as i32;

        // Process image in chunks for better cache locality
        let chunk_height = (height as usize / self.thread_count).max(1);

        for chunk_start in (0..height as usize).step_by(chunk_height) {
            let chunk_end = (chunk_start + chunk_height).min(height as usize);

            for y in chunk_start..chunk_end {
                for x in 0..width as usize {
                    if y < kernel_half as usize || y >= (height as i32 - kernel_half) as usize ||
                       x < kernel_half as usize || x >= (width as i32 - kernel_half) as usize {
                        // Border handling: copy original pixel
                        let idx = (y * width as usize + x) * 4;
                        result[idx..idx + 4].copy_from_slice(&image_data[idx..idx + 4]);
                        continue;
                    }

                    let mut sum_r = 0.0f32;
                    let mut sum_g = 0.0f32;
                    let mut sum_b = 0.0f32;
                    let mut sum_a = 0.0f32;

                    // Apply kernel
                    for ky in 0..kernel_size as usize {
                        for kx in 0..kernel_size as usize {
                            let py = (y as i32 + ky as i32 - kernel_half) as usize;
                            let px = (x as i32 + kx as i32 - kernel_half) as usize;
                            let pidx = (py * width as usize + px) * 4;
                            let kidx = ky * kernel_size as usize + kx;

                            sum_r += image_data[pidx] as f32 * kernel[kidx];
                            sum_g += image_data[pidx + 1] as f32 * kernel[kidx];
                            sum_b += image_data[pidx + 2] as f32 * kernel[kidx];
                            sum_a += image_data[pidx + 3] as f32 * kernel[kidx];
                        }
                    }

                    // Clamp and store results
                    let idx = (y * width as usize + x) * 4;
                    result[idx] = sum_r.max(0.0).min(255.0) as u8;
                    result[idx + 1] = sum_g.max(0.0).min(255.0) as u8;
                    result[idx + 2] = sum_b.max(0.0).min(255.0) as u8;
                    result[idx + 3] = sum_a.max(0.0).min(255.0) as u8;
                }
            }
        }

        let execution_time = PERFORMANCE.now() - start_time;
        self.record_performance("image_convolution", execution_time);

        log!("Image convolution completed in {:.2}ms", execution_time);
        Ok(result)
    }

    // Optimized Fibonacci calculation using matrix exponentiation
    #[wasm_bindgen]
    pub fn fibonacci_fast(&self, n: u64) -> Result<u64, JsValue> {
        let start_time = PERFORMANCE.now();

        if n == 0 { return Ok(0); }
        if n == 1 { return Ok(1); }

        // Use matrix exponentiation for O(log n) complexity
        // [F(n+1)]   [1 1]^n   [1]
        // [F(n)  ] = [1 0]   * [0]

        let result = self.matrix_power_mod([[1, 1], [1, 0]], n - 1);
        let fibonacci_n = result[0][0];

        let execution_time = PERFORMANCE.now() - start_time;
        self.record_performance("fibonacci", execution_time);

        log!("Fibonacci({}) calculated in {:.2}ms", n, execution_time);
        Ok(fibonacci_n)
    }

    // K-means clustering implementation
    #[wasm_bindgen]
    pub async fn k_means_clustering(
        &self,
        data: &[f32],
        dimensions: usize,
        k: usize,
        max_iterations: usize
    ) -> Result<Vec<usize>, JsValue> {
        let start_time = PERFORMANCE.now();

        if data.len() % dimensions != 0 {
            return Err(JsValue::from_str("Data length must be multiple of dimensions"));
        }

        let n_points = data.len() / dimensions;
        let mut centroids = vec![0.0f32; k * dimensions];
        let mut assignments = vec![0usize; n_points];
        let mut cluster_sizes = vec![0usize; k];

        // Initialize centroids randomly
        for i in 0..k {
            for d in 0..dimensions {
                // Simple random initialization
                centroids[i * dimensions + d] = data[(i * n_points / k) * dimensions + d];
            }
        }

        for iteration in 0..max_iterations {
            let mut changed = false;
            cluster_sizes.fill(0);

            // Assign points to nearest centroids
            for point_idx in 0..n_points {
                let mut min_distance = f32::INFINITY;
                let mut best_cluster = 0;

                for cluster_idx in 0..k {
                    let mut distance = 0.0f32;

                    for d in 0..dimensions {
                        let diff = data[point_idx * dimensions + d] - 
                                  centroids[cluster_idx * dimensions + d];
                        distance += diff * diff;
                    }

                    if distance < min_distance {
                        min_distance = distance;
                        best_cluster = cluster_idx;
                    }
                }

                if assignments[point_idx] != best_cluster {
                    assignments[point_idx] = best_cluster;
                    changed = true;
                }

                cluster_sizes[best_cluster] += 1;
            }

            // Update centroids
            centroids.fill(0.0);

            for point_idx in 0..n_points {
                let cluster = assignments[point_idx];
                for d in 0..dimensions {
                    centroids[cluster * dimensions + d] += data[point_idx * dimensions + d];
                }
            }

            for cluster_idx in 0..k {
                if cluster_sizes[cluster_idx] > 0 {
                    let size = cluster_sizes[cluster_idx] as f32;
                    for d in 0..dimensions {
                        centroids[cluster_idx * dimensions + d] /= size;
                    }
                }
            }

            if !changed {
                log!("K-means converged after {} iterations", iteration + 1);
                break;
            }
        }

        let execution_time = PERFORMANCE.now() - start_time;
        self.record_performance("k_means", execution_time);

        log!("K-means clustering completed in {:.2}ms", execution_time);
        Ok(assignments)
    }

    // Neural network inference (simple feedforward)
    #[wasm_bindgen]
    pub async fn neural_network_inference(
        &self,
        input: &[f32],
        weights: &[f32],
        biases: &[f32],
        layer_sizes: &[usize]
    ) -> Result<Vec<f32>, JsValue> {
        let start_time = PERFORMANCE.now();

        if layer_sizes.is_empty() {
            return Err(JsValue::from_str("At least one layer required"));
        }

        let mut activations = input.to_vec();
        let mut weight_idx = 0;
        let mut bias_idx = 0;

        // Forward pass through all layers
        for layer in 1..layer_sizes.len() {
            let prev_size = layer_sizes[layer - 1];
            let curr_size = layer_sizes[layer];

            let mut new_activations = vec![0.0f32; curr_size];

            // Matrix multiplication: new_activations = weights * activations + biases
            for i in 0..curr_size {
                let mut sum = biases[bias_idx + i];

                // Vectorized computation when possible
                if self.simd_enabled {
                    // Simulate SIMD operations for dot product
                    for j in (0..prev_size).step_by(4) {
                        let end = (j + 4).min(prev_size);
                        for jj in j..end {
                            sum += weights[weight_idx + i * prev_size + jj] * activations[jj];
                        }
                    }
                } else {
                    for j in 0..prev_size {
                        sum += weights[weight_idx + i * prev_size + j] * activations[j];
                    }
                }

                // ReLU activation function
                new_activations[i] = sum.max(0.0);
            }

            weight_idx += curr_size * prev_size;
            bias_idx += curr_size;
            activations = new_activations;
        }

        // Softmax activation for output layer (if more than 1 output)
        if activations.len() > 1 {
            let max_val = activations.iter().cloned().fold(f32::NEG_INFINITY, f32::max);
            let mut sum = 0.0f32;

            for val in activations.iter_mut() {
                *val = (*val - max_val).exp();
                sum += *val;
            }

            for val in activations.iter_mut() {
                *val /= sum;
            }
        }

        let execution_time = PERFORMANCE.now() - start_time;
        self.record_performance("neural_network", execution_time);

        log!("Neural network inference completed in {:.2}ms", execution_time);
        Ok(activations)
    }

    // Performance monitoring
    #[wasm_bindgen]
    pub fn get_performance_stats(&self) -> JsValue {
        PERFORMANCE_CACHE.with(|cache| {
            let cache = cache.borrow();
            let mut stats = serde_json::Map::new();

            for (operation, times) in cache.iter() {
                if !times.is_empty() {
                    let avg = times.iter().sum::<f64>() / times.len() as f64;
                    let min = times.iter().cloned().fold(f64::INFINITY, f64::min);
                    let max = times.iter().cloned().fold(f64::NEG_INFINITY, f64::max);

                    let mut op_stats = serde_json::Map::new();
                    op_stats.insert("count".to_string(), serde_json::Value::Number(serde_json::Number::from(times.len())));
                    op_stats.insert("average".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(avg).unwrap()));
                    op_stats.insert("min".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(min).unwrap()));
                    op_stats.insert("max".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(max).unwrap()));

                    stats.insert(operation.clone(), serde_json::Value::Object(op_stats));
                }
            }

            JsValue::from_serde(&serde_json::Value::Object(stats)).unwrap_or(JsValue::NULL)
        })
    }

    // Memory management
    #[wasm_bindgen]
    pub fn get_memory_usage(&self) -> usize {
        // Return approximate memory usage in bytes
        let memory = wasm_bindgen::memory();
        memory.buffer().byte_length() as usize
    }

    // Benchmark suite
    #[wasm_bindgen]
    pub async fn run_benchmark_suite(&self) -> Result<JsValue, JsValue> {
        let start_time = PERFORMANCE.now();
        let mut results = serde_json::Map::new();

        log!("Starting comprehensive benchmark suite...");

        // Matrix multiplication benchmark
        let matrix_size = 256;
        let matrix_a = vec![1.0f32; matrix_size * matrix_size];
        let matrix_b = vec![2.0f32; matrix_size * matrix_size];

        let matrix_start = PERFORMANCE.now();
        let _ = self.matrix_multiply_async(&matrix_a, &matrix_b, matrix_size).await?;
        let matrix_time = PERFORMANCE.now() - matrix_start;

        let mut matrix_result = serde_json::Map::new();
        matrix_result.insert("time".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(matrix_time).unwrap()));
        matrix_result.insert("operations".to_string(), serde_json::Value::Number(serde_json::Number::from(matrix_size.pow(3) * 2)));
        matrix_result.insert("ops_per_second".to_string(), serde_json::Value::Number(serde_json::Number::from_f64((matrix_size.pow(3) * 2) as f64 / (matrix_time / 1000.0)).unwrap()));
        results.insert("matrix_multiplication".to_string(), serde_json::Value::Object(matrix_result));

        // FFT benchmark
        let fft_size = 8192;
        let mut real = vec![1.0f32; fft_size];
        let mut imag = vec![0.0f32; fft_size];
        for i in 0..fft_size {
            real[i] = (2.0 * std::f32::consts::PI * i as f32 / fft_size as f32).sin();
        }

        let fft_start = PERFORMANCE.now();
        self.fft_radix2(&mut real, &mut imag, false)?;
        let fft_time = PERFORMANCE.now() - fft_start;

        let mut fft_result = serde_json::Map::new();
        fft_result.insert("time".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(fft_time).unwrap()));
        fft_result.insert("size".to_string(), serde_json::Value::Number(serde_json::Number::from(fft_size)));
        fft_result.insert("ops_per_second".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(fft_size as f64 * (fft_size as f64).log2() / (fft_time / 1000.0)).unwrap()));
        results.insert("fft".to_string(), serde_json::Value::Object(fft_result));

        // Fibonacci benchmark
        let fib_n = 40;
        let fib_start = PERFORMANCE.now();
        let fib_result_val = self.fibonacci_fast(fib_n)?;
        let fib_time = PERFORMANCE.now() - fib_start;

        let mut fib_result = serde_json::Map::new();
        fib_result.insert("time".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(fib_time).unwrap()));
        fib_result.insert("n".to_string(), serde_json::Value::Number(serde_json::Number::from(fib_n)));
        fib_result.insert("result".to_string(), serde_json::Value::Number(serde_json::Number::from(fib_result_val)));
        results.insert("fibonacci".to_string(), serde_json::Value::Object(fib_result));

        let total_time = PERFORMANCE.now() - start_time;
        results.insert("total_time".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(total_time).unwrap()));

        log!("Benchmark suite completed in {:.2}ms", total_time);
        Ok(JsValue::from_serde(&serde_json::Value::Object(results)).unwrap())
    }

    // Helper functions
    fn matrix_power_mod(&self, base: [[u64; 2]; 2], mut exp: u64) -> [[u64; 2]; 2] {
        let mut result = [[1, 0], [0, 1]]; // Identity matrix
        let mut base = base;

        while exp > 0 {
            if exp & 1 == 1 {
                result = self.matrix_mult_2x2(result, base);
            }
            base = self.matrix_mult_2x2(base, base);
            exp >>= 1;
        }

        result
    }

    fn matrix_mult_2x2(&self, a: [[u64; 2]; 2], b: [[u64; 2]; 2]) -> [[u64; 2]; 2] {
        [
            [a[0][0] * b[0][0] + a[0][1] * b[1][0], a[0][0] * b[0][1] + a[0][1] * b[1][1]],
            [a[1][0] * b[0][0] + a[1][1] * b[1][0], a[1][0] * b[0][1] + a[1][1] * b[1][1]]
        ]
    }

    fn record_performance(&self, operation: &str, time: f64) {
        PERFORMANCE_CACHE.with(|cache| {
            let mut cache = cache.borrow_mut();
            let entry = cache.entry(operation.to_string()).or_insert_with(Vec::new);
            entry.push(time);

            // Keep only last 100 measurements
            if entry.len() > 100 {
                entry.remove(0);
            }
        });
    }
}

// Standalone utility functions
#[wasm_bindgen]
pub fn get_wasm_capabilities() -> JsValue {
    let mut capabilities = serde_json::Map::new();

    capabilities.insert("simd".to_string(), serde_json::Value::Bool(cfg!(target_feature = "simd128")));
    capabilities.insert("atomics".to_string(), serde_json::Value::Bool(cfg!(target_feature = "atomics")));
    capabilities.insert("bulk_memory".to_string(), serde_json::Value::Bool(cfg!(target_feature = "bulk-memory")));
    capabilities.insert("multivalue".to_string(), serde_json::Value::Bool(cfg!(target_feature = "multivalue")));
    capabilities.insert("threads".to_string(), serde_json::Value::Bool(cfg!(target_feature = "atomics")));

    // Memory information
    let memory = wasm_bindgen::memory();
    capabilities.insert("memory_pages".to_string(), serde_json::Value::Number(serde_json::Number::from(memory.buffer().byte_length() / 65536)));
    capabilities.insert("memory_bytes".to_string(), serde_json::Value::Number(serde_json::Number::from(memory.buffer().byte_length())));

    JsValue::from_serde(&serde_json::Value::Object(capabilities)).unwrap()
}

#[wasm_bindgen]
pub fn allocate_buffer(size: usize) -> *mut u8 {
    let mut buffer = Vec::<u8>::with_capacity(size);
    let ptr = buffer.as_mut_ptr();
    std::mem::forget(buffer);
    ptr
}

#[wasm_bindgen]
pub fn deallocate_buffer(ptr: *mut u8, size: usize) {
    unsafe {
        Vec::from_raw_parts(ptr, 0, size);
    }
}

// Linear memory operations
#[wasm_bindgen]
pub fn copy_memory(src: *const u8, dst: *mut u8, size: usize) {
    unsafe {
        std::ptr::copy_nonoverlapping(src, dst, size);
    }
}

#[wasm_bindgen]
pub fn zero_memory(ptr: *mut u8, size: usize) {
    unsafe {
        std::ptr::write_bytes(ptr, 0, size);
    }
}

