// Build script for Katalyst WASM modules
// This script handles cross-compilation and optimization for WebAssembly targets

use std::process::Command;
use std::env;
use std::path::Path;
use std::fs;

fn main() -> Result<(), Box<dyn std::error::Error>> {
    println!("Building Katalyst WASM module...");

    // Get build environment
    let target = env::var("TARGET").unwrap_or_default();
    let profile = env::var("PROFILE").unwrap_or_default();
    let out_dir = env::var("OUT_DIR").unwrap_or_default();

    println!("cargo:rerun-if-changed=src/");
    println!("cargo:rerun-if-changed=Cargo.toml");

    // Configure for WASM target
    if target == "wasm32-unknown-unknown" {
        configure_wasm_build()?;
    }

    // Generate bindings and optimizations based on profile
    if profile == "release" {
        optimize_for_production()?;
    }

    // Copy WebAssembly text format if it exists
    copy_wat_files()?;

    println!("Build configuration complete");
    Ok(())
}

fn configure_wasm_build() -> Result<(), Box<dyn std::error::Error>> {
    println!("Configuring for WebAssembly target...");

    // Set WASM-specific compiler flags
    println!("cargo:rustc-link-arg=--export-memory");
    println!("cargo:rustc-link-arg=--import-memory");
    println!("cargo:rustc-link-arg=--max-memory=1073741824"); // 1GB max memory
    println!("cargo:rustc-link-arg=--initial-memory=16777216"); // 16MB initial

    // Enable WASM features
    println!("cargo:rustc-cfg=target_feature=\"simd128\"");
    println!("cargo:rustc-cfg=target_feature=\"atomics\"");
    println!("cargo:rustc-cfg=target_feature=\"bulk-memory\"");
    println!("cargo:rustc-cfg=target_feature=\"multivalue\"");

    // Enable threading if available
    if cfg!(feature = "parallel") {
        println!("cargo:rustc-link-arg=--shared-memory");
        println!("cargo:rustc-link-arg=--enable-threads");
        println!("cargo:rustc-cfg=target_feature=\"atomics\"");
    }

    Ok(())
}

fn optimize_for_production() -> Result<(), Box<dyn std::error::Error>> {
    println!("Applying production optimizations...");

    // Enable additional optimizations for release builds
    println!("cargo:rustc-link-arg=-O4");
    println!("cargo:rustc-link-arg=--strip-debug");
    println!("cargo:rustc-link-arg=--strip-producers");

    // Tree-shaking and dead code elimination
    println!("cargo:rustc-link-arg=--gc-sections");

    // WASM-specific optimizations
    println!("cargo:rustc-link-arg=--optimize-level=3");
    println!("cargo:rustc-link-arg=--shrink-level=2");

    Ok(())
}

fn copy_wat_files() -> Result<(), Box<dyn std::error::Error>> {
    let wat_path = Path::new("../katalyst-compute.wat");
    if wat_path.exists() {
        println!("Found WebAssembly text format file, copying to build directory...");

        let out_dir = env::var("OUT_DIR")?;
        let dest_path = Path::new(&out_dir).join("katalyst-compute.wat");

        fs::copy(wat_path, dest_path)?;
        println!("cargo:rerun-if-changed=../katalyst-compute.wat");
    }

    Ok(())
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_build_configuration() {
        // Test that build script functions don't panic
        assert!(configure_wasm_build().is_ok());
        assert!(optimize_for_production().is_ok());
        assert!(copy_wat_files().is_ok());
    }
}

