   119→    // Fast Fourier Transform implementation
   120→    #[wasm_bindgen]
   121→    pub fn fft_radix2(&self, real: &mut [f32], imag: &mut [f32], inverse: bool) -> Result<(), JsValue> {
   122→        let start_time = PERFORMANCE.now();
   123→        let n = real.len();
   124→        
   125→        if n != imag.len() || !n.is_power_of_two() {
   126→            return Err(JsValue::from_str("FFT requires power-of-2 length arrays"));
   127→        }
   128→        
   129→        // Bit-reversal permutation
   130→        let mut j = 0usize;
   131→        for i in 1..n {
   132→            let mut bit = n >> 1;
   133→            while j & bit != 0 {
   134→                j ^= bit;
   135→                bit >>= 1;
   136→            }
   137→            j ^= bit;
   138→            
   139→            if j > i {
   140→                real.swap(i, j);
   141→                imag.swap(i, j);
   142→            }
   143→        }
   144→        
   145→        // Main FFT computation
   146→        let mut length = 2usize;
   147→        while length <= n {
   148→            let angle = if inverse { 2.0 * std::f32::consts::PI / length as f32 } 
   149→                       else { -2.0 * std::f32::consts::PI / length as f32 };
   150→            
   151→            let wlen_real = angle.cos();
   152→            let wlen_imag = angle.sin();
   153→            
   154→            for i in (0..n).step_by(length) {
   155→                let mut w_real = 1.0f32;
   156→                let mut w_imag = 0.0f32;
   157→                
   158→                for j in 0..length/2 {
   159→                    let u_real = real[i + j];
   160→                    let u_imag = imag[i + j];
   161→                    let v_real = real[i + j + length/2] * w_real - imag[i + j + length/2] * w_imag;
   162→                    let v_imag = real[i + j + length/2] * w_imag + imag[i + j + length/2] * w_real;
   163→                    
   164→                    real[i + j] = u_real + v_real;
   165→                    imag[i + j] = u_imag + v_imag;
   166→                    real[i + j + length/2] = u_real - v_real;
   167→                    imag[i + j + length/2] = u_imag - v_imag;
   168→                    
   169→                    let w_real_temp = w_real * wlen_real - w_imag * wlen_imag;
   170→                    w_imag = w_real * wlen_imag + w_imag * wlen_real;
   171→                    w_real = w_real_temp;
   172→                }
   173→            }
   174→            
   175→            length <<= 1;
   176→        }
   177→        
   178→        // Normalize for inverse transform
   179→        if inverse {
   180→            let norm = 1.0 / n as f32;
   181→            for i in 0..n {
   182→                real[i] *= norm;
   183→                imag[i] *= norm;
   184→            }
   185→        }
   186→        
   187→        let execution_time = PERFORMANCE.now() - start_time;
   188→        self.record_performance("fft", execution_time);
   189→        
   190→        log!("FFT completed in {:.2}ms", execution_time);
   191→        Ok(())
   192→    }
