   194→    // Parallel image processing with convolution
   195→    #[wasm_bindgen]
   196→    pub async fn process_image_convolution(
   197→        &self, 
   198→        image_data: &[u8], 
   199→        kernel: &[f32], 
   200→        width: u32, 
   201→        height: u32,
   202→        kernel_size: u32
   203→    ) -> Result<Vec<u8>, JsValue> {
   204→        let start_time = PERFORMANCE.now();
   205→        
   206→        if kernel.len() != (kernel_size * kernel_size) as usize {
   207→            return Err(JsValue::from_str("Kernel size doesn't match kernel data"));
   208→        }
   209→        
   210→        let mut result = vec![0u8; image_data.len()];
   211→        let kernel_half = (kernel_size / 2) as i32;
   212→        
   213→        // Process image in chunks for better cache locality
   214→        let chunk_height = (height as usize / self.thread_count).max(1);
   215→        
   216→        for chunk_start in (0..height as usize).step_by(chunk_height) {
   217→            let chunk_end = (chunk_start + chunk_height).min(height as usize);
   218→            
   219→            for y in chunk_start..chunk_end {
   220→                for x in 0..width as usize {
   221→                    if y < kernel_half as usize || y >= (height as i32 - kernel_half) as usize ||
   222→                       x < kernel_half as usize || x >= (width as i32 - kernel_half) as usize {
   223→                        // Border handling: copy original pixel
   224→                        let idx = (y * width as usize + x) * 4;
   225→                        result[idx..idx + 4].copy_from_slice(&image_data[idx..idx + 4]);
   226→                        continue;
   227→                    }
   228→                    
   229→                    let mut sum_r = 0.0f32;
   230→                    let mut sum_g = 0.0f32;
   231→                    let mut sum_b = 0.0f32;
   232→                    let mut sum_a = 0.0f32;
   233→                    
   234→                    // Apply kernel
   235→                    for ky in 0..kernel_size as usize {
   236→                        for kx in 0..kernel_size as usize {
   237→                            let py = (y as i32 + ky as i32 - kernel_half) as usize;
   238→                            let px = (x as i32 + kx as i32 - kernel_half) as usize;
   239→                            let pidx = (py * width as usize + px) * 4;
   240→                            let kidx = ky * kernel_size as usize + kx;
   241→                            
   242→                            sum_r += image_data[pidx] as f32 * kernel[kidx];
   243→                            sum_g += image_data[pidx + 1] as f32 * kernel[kidx];
   244→                            sum_b += image_data[pidx + 2] as f32 * kernel[kidx];
   245→                            sum_a += image_data[pidx + 3] as f32 * kernel[kidx];
   246→                        }
   247→                    }
   248→                    
   249→                    // Clamp and store results
   250→                    let idx = (y * width as usize + x) * 4;
   251→                    result[idx] = sum_r.max(0.0).min(255.0) as u8;
   252→                    result[idx + 1] = sum_g.max(0.0).min(255.0) as u8;
   253→                    result[idx + 2] = sum_b.max(0.0).min(255.0) as u8;
   254→                    result[idx + 3] = sum_a.max(0.0).min(255.0) as u8;
   255→                }
   256→            }
   257→        }
   258→        
   259→        let execution_time = PERFORMANCE.now() - start_time;
   260→        self.record_performance("image_convolution", execution_time);
   261→        
   262→        log!("Image convolution completed in {:.2}ms", execution_time);
   263→        Ok(result)
   264→    }
