   266→    // Optimized Fibonacci calculation using matrix exponentiation
   267→    #[wasm_bindgen]
   268→    pub fn fibonacci_fast(&self, n: u64) -> Result<u64, JsValue> {
   269→        let start_time = PERFORMANCE.now();
   270→        
   271→        if n == 0 { return Ok(0); }
   272→        if n == 1 { return Ok(1); }
   273→        
   274→        // Use matrix exponentiation for O(log n) complexity
   275→        // [F(n+1)]   [1 1]^n   [1]
   276→        // [F(n)  ] = [1 0]   * [0]
   277→        
   278→        let result = self.matrix_power_mod([[1, 1], [1, 0]], n - 1);
   279→        let fibonacci_n = result[0][0];
   280→        
   281→        let execution_time = PERFORMANCE.now() - start_time;
   282→        self.record_performance("fibonacci", execution_time);
   283→        
   284→        log!("Fibonacci({}) calculated in {:.2}ms", n, execution_time);
   285→        Ok(fibonacci_n)
   286→    }
