   288→    // K-means clustering implementation
   289→    #[wasm_bindgen]
   290→    pub async fn k_means_clustering(
   291→        &self,
   292→        data: &[f32],
   293→        dimensions: usize,
   294→        k: usize,
   295→        max_iterations: usize
   296→    ) -> Result<Vec<usize>, JsValue> {
   297→        let start_time = PERFORMANCE.now();
   298→        
   299→        if data.len() % dimensions != 0 {
   300→            return Err(JsValue::from_str("Data length must be multiple of dimensions"));
   301→        }
   302→        
   303→        let n_points = data.len() / dimensions;
   304→        let mut centroids = vec![0.0f32; k * dimensions];
   305→        let mut assignments = vec![0usize; n_points];
   306→        let mut cluster_sizes = vec![0usize; k];
   307→        
   308→        // Initialize centroids randomly
   309→        for i in 0..k {
   310→            for d in 0..dimensions {
   311→                // Simple random initialization
   312→                centroids[i * dimensions + d] = data[(i * n_points / k) * dimensions + d];
   313→            }
   314→        }
   315→        
   316→        for iteration in 0..max_iterations {
   317→            let mut changed = false;
   318→            cluster_sizes.fill(0);
   319→            
   320→            // Assign points to nearest centroids
   321→            for point_idx in 0..n_points {
   322→                let mut min_distance = f32::INFINITY;
   323→                let mut best_cluster = 0;
   324→                
   325→                for cluster_idx in 0..k {
   326→                    let mut distance = 0.0f32;
   327→                    
   328→                    for d in 0..dimensions {
   329→                        let diff = data[point_idx * dimensions + d] - 
   330→                                  centroids[cluster_idx * dimensions + d];
   331→                        distance += diff * diff;
   332→                    }
   333→                    
   334→                    if distance < min_distance {
   335→                        min_distance = distance;
   336→                        best_cluster = cluster_idx;
   337→                    }
   338→                }
   339→                
   340→                if assignments[point_idx] != best_cluster {
   341→                    assignments[point_idx] = best_cluster;
   342→                    changed = true;
   343→                }
   344→                
   345→                cluster_sizes[best_cluster] += 1;
   346→            }
   347→            
   348→            // Update centroids
   349→            centroids.fill(0.0);
   350→            
   351→            for point_idx in 0..n_points {
   352→                let cluster = assignments[point_idx];
   353→                for d in 0..dimensions {
   354→                    centroids[cluster * dimensions + d] += data[point_idx * dimensions + d];
   355→                }
   356→            }
   357→            
   358→            for cluster_idx in 0..k {
   359→                if cluster_sizes[cluster_idx] > 0 {
   360→                    let size = cluster_sizes[cluster_idx] as f32;
   361→                    for d in 0..dimensions {
   362→                        centroids[cluster_idx * dimensions + d] /= size;
   363→                    }
   364→                }
   365→            }
   366→            
   367→            if !changed {
   368→                log!("K-means converged after {} iterations", iteration + 1);
   369→                break;
   370→            }
   371→        }
   372→        
   373→        let execution_time = PERFORMANCE.now() - start_time;
   374→        self.record_performance("k_means", execution_time);
   375→        
   376→        log!("K-means clustering completed in {:.2}ms", execution_time);
   377→        Ok(assignments)
   378→    }
