   380→    // Neural network inference (simple feedforward)
   381→    #[wasm_bindgen]
   382→    pub async fn neural_network_inference(
   383→        &self,
   384→        input: &[f32],
   385→        weights: &[f32],
   386→        biases: &[f32],
   387→        layer_sizes: &[usize]
   388→    ) -> Result<Vec<f32>, JsValue> {
   389→        let start_time = PERFORMANCE.now();
   390→        
   391→        if layer_sizes.is_empty() {
   392→            return Err(JsValue::from_str("At least one layer required"));
   393→        }
   394→        
   395→        let mut activations = input.to_vec();
   396→        let mut weight_idx = 0;
   397→        let mut bias_idx = 0;
   398→        
   399→        // Forward pass through all layers
   400→        for layer in 1..layer_sizes.len() {
   401→            let prev_size = layer_sizes[layer - 1];
   402→            let curr_size = layer_sizes[layer];
   403→            
   404→            let mut new_activations = vec![0.0f32; curr_size];
   405→            
   406→            // Matrix multiplication: new_activations = weights * activations + biases
   407→            for i in 0..curr_size {
   408→                let mut sum = biases[bias_idx + i];
   409→                
   410→                // Vectorized computation when possible
   411→                if self.simd_enabled {
   412→                    // Simulate SIMD operations for dot product
   413→                    for j in (0..prev_size).step_by(4) {
   414→                        let end = (j + 4).min(prev_size);
   415→                        for jj in j..end {
   416→                            sum += weights[weight_idx + i * prev_size + jj] * activations[jj];
   417→                        }
   418→                    }
   419→                } else {
   420→                    for j in 0..prev_size {
   421→                        sum += weights[weight_idx + i * prev_size + j] * activations[j];
   422→                    }
   423→                }
   424→                
   425→                // ReLU activation function
   426→                new_activations[i] = sum.max(0.0);
   427→            }
   428→            
   429→            weight_idx += curr_size * prev_size;
   430→            bias_idx += curr_size;
   431→            activations = new_activations;
   432→        }
   433→        
   434→        // Softmax activation for output layer (if more than 1 output)
   435→        if activations.len() > 1 {
   436→            let max_val = activations.iter().cloned().fold(f32::NEG_INFINITY, f32::max);
   437→            let mut sum = 0.0f32;
   438→            
   439→            for val in activations.iter_mut() {
   440→                *val = (*val - max_val).exp();
   441→                sum += *val;
   442→            }
   443→            
   444→            for val in activations.iter_mut() {
   445→                *val /= sum;
   446→            }
   447→        }
   448→        
   449→        let execution_time = PERFORMANCE.now() - start_time;
   450→        self.record_performance("neural_network", execution_time);
   451→        
   452→        log!("Neural network inference completed in {:.2}ms", execution_time);
   453→        Ok(activations)
   454→    }
