   456→    // Performance monitoring
   457→    #[wasm_bindgen]
   458→    pub fn get_performance_stats(&self) -> JsValue {
   459→        PERFORMANCE_CACHE.with(|cache| {
   460→            let cache = cache.borrow();
   461→            let mut stats = serde_json::Map::new();
   462→            
   463→            for (operation, times) in cache.iter() {
   464→                if !times.is_empty() {
   465→                    let avg = times.iter().sum::<f64>() / times.len() as f64;
   466→                    let min = times.iter().cloned().fold(f64::INFINITY, f64::min);
   467→                    let max = times.iter().cloned().fold(f64::NEG_INFINITY, f64::max);
   468→                    
   469→                    let mut op_stats = serde_json::Map::new();
   470→                    op_stats.insert("count".to_string(), serde_json::Value::Number(serde_json::Number::from(times.len())));
   471→                    op_stats.insert("average".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(avg).unwrap()));
   472→                    op_stats.insert("min".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(min).unwrap()));
   473→                    op_stats.insert("max".to_string(), serde_json::Value::Number(serde_json::Number::from_f64(max).unwrap()));
   474→                    
   475→                    stats.insert(operation.clone(), serde_json::Value::Object(op_stats));
   476→                }
   477→            }
   478→            
   479→            JsValue::from_serde(&serde_json::Value::Object(stats)).unwrap_or(JsValue::NULL)
   480→        })
   481→    }
