{
  "$schema": "https://turbo.build/schema.json",
  "globalDependencies": ["**/.env.*local"],
  "globalEnv": ["NODE_ENV", "VERCEL_URL", "CLOUDFLARE_ACCOUNT_ID"],
  "pipeline": {
    "build": {
      "dependsOn": ["^build"],
      "outputs": ["dist/**", ".next/**", "build/**", "target/**"],
      "env": ["NODE_ENV", "OPTIMIZE", "MINIFY", "TREE_SHAKE"],
      "cache": true
    },
    "dev": {
      "cache": false,
      "persistent": true,
      "dependsOn": ["^build"]
    },
    "test": {
      "dependsOn": ["build"],
      "outputs": ["coverage/**"],
      "cache": true
    },
    "test:unit": {
      "outputs": ["coverage/**"],
      "cache": true
    },
    "test:integration": {
      "dependsOn": ["build"],
      "cache": false
    },
    "test:performance": {
      "dependsOn": ["build"],
      "cache": false,
      "env": ["BENCHMARK_ITERATIONS"]
    },
    "lint": {
      "outputs": [],
      "cache": true
    },
    "typecheck": {
      "dependsOn": ["^build"],
      "outputs": ["tsconfig.tsbuildinfo"],
      "cache": true
    },
    "clean": {
      "cache": false
    }
  },
  "remoteCache": {
    "signature": true
  }
}

# Katalyst Framework Unified Build System
# Intelligent tool detection with automatic fallbacks

# Tool Detection
DENO := $(shell command -v deno 2> /dev/null)
BUN := $(shell command -v bun 2> /dev/null)
NODE := $(shell command -v node 2> /dev/null)
CARGO := $(shell command -v cargo 2> /dev/null)
MIX := $(shell command -v mix 2> /dev/null)

# Runtime Selection
ifdef DENO
  RUNTIME := deno
  PKG_MANAGER := deno
  PKG_INSTALL := deno install
  PKG_RUN := deno task
else ifdef BUN
  RUNTIME := bun
  PKG_MANAGER := bun
  PKG_INSTALL := bun install
  PKG_RUN := bun run
else
  RUNTIME := node
  PKG_MANAGER := npm
  PKG_INSTALL := npm install
  PKG_RUN := npm run
endif

# Colors for output
RED := \033[0;31m
GREEN := \033[0;32m
YELLOW := \033[1;33m
NC := \033[0m # No Color

# Environment
export NODE_ENV ?= development
export ENABLE_CACHE ?= true
export PARALLEL_JOBS ?= auto
export TURBO_TOKEN ?= $(TURBO_TOKEN)
export NX_CLOUD_ACCESS_TOKEN ?= $(NX_CLOUD_ACCESS_TOKEN)

.PHONY: help
help:
	@echo "$(GREEN)Katalyst Framework Build System$(NC)"
	@echo ""
	@echo "$(YELLOW)Setup:$(NC)"
	@echo "  make setup              - Install all dependencies"
	@echo "  make check-env          - Check environment and tools"
	@echo ""
	@echo "$(YELLOW)Development:$(NC)"
	@echo "  make dev                - Start all development servers"
	@echo "  make dev-core           - Start core app (port 20007)"
	@echo "  make dev-admin          - Start admin dashboard (port 20008)"
	@echo "  make dev-marketing      - Start marketing site (port 20009)"
	@echo "  make dev-phoenix        - Start Phoenix backend (port 4000)"
	@echo ""
	@echo "$(YELLOW)Build:$(NC)"
	@echo "  make build              - Build all applications"
	@echo "  make build-core         - Build core application"
	@echo "  make build-rust         - Build Rust components"
	@echo "  make build-prod         - Production build with optimizations"
	@echo ""
	@echo "$(YELLOW)Testing:$(NC)"
	@echo "  make test               - Run all tests"
	@echo "  make test-unit          - Run unit tests"
	@echo "  make test-integration   - Run integration tests"
	@echo "  make test-performance   - Run performance tests"
	@echo "  make test-multithreading - Test multithreading features"
	@echo ""
	@echo "$(YELLOW)Deployment:$(NC)"
	@echo "  make deploy             - Deploy to production"
	@echo "  make deploy-vercel      - Deploy frontend to Vercel"
	@echo "  make deploy-fly         - Deploy backend to Fly.io"
	@echo ""
	@echo "Detected runtime: $(GREEN)$(RUNTIME)$(NC)"
	@echo "Package manager: $(GREEN)$(PKG_MANAGER)$(NC)"

.PHONY: setup
setup:
	@echo "$(GREEN)Setting up Katalyst Framework...$(NC)"
	@echo "Runtime: $(RUNTIME)"
	@$(PKG_INSTALL)
ifdef CARGO
	@echo "$(GREEN)Setting up Rust components...$(NC)"
	@cargo build --release
endif
ifdef MIX
	@echo "$(GREEN)Setting up Phoenix server...$(NC)"
	@cd katalyst-server && mix deps.get && mix compile
endif
	@echo "$(GREEN)Setup complete!$(NC)"

.PHONY: check-env
check-env:
	@echo "$(GREEN)Environment Check:$(NC)"
	@echo "  Runtime: $(if $(DENO),âœ… Deno,$(if $(BUN),âœ… Bun,$(if $(NODE),âœ… Node,âŒ None)))"
	@echo "  Rust: $(if $(CARGO),âœ… Available,âš ï¸  Not found)"
	@echo "  Elixir: $(if $(MIX),âœ… Available,âš ï¸  Not found)"
	@echo "  CPU Cores: $$(nproc 2>/dev/null || sysctl -n hw.ncpu 2>/dev/null || echo 4)"
	@echo "  Cache: $(if $(ENABLE_CACHE),âœ… Enabled,âŒ Disabled)"

