  const processDebounced = useCallback(async (newState: T): Promise<T> => {
    return new Promise((resolve) => {
      if (updateTimerRef.current) {
        clearTimeout(updateTimerRef.current);
      }

      updateTimerRef.current = setTimeout(() => {
        resolve(newState);
      }, config.debounceMs || 300);
    });
  }, [config.debounceMs]);

  /**
   * Persist state to storage
   */
  const persistState = useCallback(async (state: T): Promise<void> => {
    // In real implementation, would use IndexedDB or similar
    if (typeof window !== 'undefined' && window.localStorage) {
      const key = `katalyst_state_${JSON.stringify(config.initialState).substring(0, 10)}`;
      window.localStorage.setItem(key, JSON.stringify(state));
    }
  }, [config.initialState]);

  /**
   * Restore persisted state on mount
   */
  useEffect(() => {
    if (config.persistence && typeof window !== 'undefined' && window.localStorage) {
      const key = `katalyst_state_${JSON.stringify(config.initialState).substring(0, 10)}`;
      const stored = window.localStorage.getItem(key);
      if (stored) {
        try {
          const parsed = JSON.parse(stored);
          setStateInternal(parsed);
        } catch (error) {
          console.error('Failed to restore persisted state:', error);
        }
      }
    }
  }, []);

  /**
   * Cleanup on unmount
   */
  useEffect(() => {
    return () => {
      if (updateTimerRef.current) {
        clearTimeout(updateTimerRef.current);
      }
    };
  }, []);

  // Public setState function
  const setState = useCallback((update: T | ((prev: T) => T)) => {
    return processUpdate(update);
  }, [processUpdate]);

  return [
    state,
    setState,
    {
      derived,
      isProcessing,
      updateCount,
      averageUpdateTime
    }
  ];
}

/**
 * Hook for managing complex nested state with automatic optimization
 */
export function useKatalystNestedState<T extends object>(
  initialState: T
): [
  T,
  (path: string, value: any) => Promise<void>,
  (paths: string[], values: any[]) => Promise<void>
] {
  const [state, setState] = useKatalystState({
    initialState,
    strategy: 'concurrent',
    concurrency: 4
  });

  /**
   * Update nested property by path
   */
  const updateNested = useCallback(async (path: string, value: any) => {
    await setState((prev) => {
      const newState = { ...prev };
      const keys = path.split('.');
      let current: any = newState;

      for (let i = 0; i < keys.length - 1; i++) {
        const key = keys[i];
        if (!(key in current)) {
          current[key] = {};
        }
        current = current[key];
      }

      current[keys[keys.length - 1]] = value;
      return newState;
    });
  }, [setState]);

  /**
   * Update multiple nested properties concurrently
   */
  const updateMultipleNested = useCallback(async (
    paths: string[],
    values: any[]
  ) => {
    if (paths.length !== values.length) {
      throw new Error('Paths and values arrays must have the same length');
    }

    // Use WebAssembly bridge for parallel updates
    const updates = paths.map((path, index) => ({
      path,
      value: values[index]
    }));

    const processedUpdates = await wasmBridge.executeExponentialFlow(
      [
        async (update: any) => update,
        async (update: any) => update
      ],
      updates,
      1 // Single level for simple parallel processing
    );

    await setState((prev) => {
      const newState = { ...prev };

      for (const update of processedUpdates) {
        if (Array.isArray(update)) {
          for (const u of update) {
            applyNestedUpdate(newState, u.path, u.value);
          }
        } else {
          applyNestedUpdate(newState, update.path, update.value);
        }
      }

      return newState;
    });
  }, [setState]);

  const applyNestedUpdate = (obj: any, path: string, value: any) => {
    const keys = path.split('.');
    let current = obj;

    for (let i = 0; i < keys.length - 1; i++) {
      const key = keys[i];
      if (!(key in current)) {
        current[key] = {};
      }
      current = current[key];
    }

    current[keys[keys.length - 1]] = value;
  };

  return [state, updateNested, updateMultipleNested];
}

/**
 * Hook for managing collections with automatic indexing and optimization
 */
export function useKatalystCollection<T extends { id: string }>(
  initialItems: T[] = []
): {
  items: T[];
  byId: Map<string, T>;
  add: (item: T) => Promise<void>;
  addMany: (items: T[]) => Promise<void>;
  update: (id: string, updates: Partial<T>) => Promise<void>;
  remove: (id: string) => Promise<void>;
  clear: () => Promise<void>;
  find: (predicate: (item: T) => boolean) => T | undefined;
  filter: (predicate: (item: T) => boolean) => T[];
} {
  const [items, setItems] = useKatalystState({
    initialState: initialItems,
    strategy: 'batched',
    persistence: true
  });

  // Create indexed map for O(1) lookups
  const byId = useMemo(() => {
    const map = new Map<string, T>();
    for (const item of items) {
      map.set(item.id, item);
    }
    return map;
  }, [items]);

  const add = useCallback(async (item: T) => {
    await setItems((prev) => [...prev, item]);
  }, [setItems]);

  const addMany = useCallback(async (newItems: T[]) => {
    // Process additions in parallel
    const processed = await katalyst.processParallel(
      newItems,
      async (item) => item
    );

    await setItems((prev) => [...prev, ...processed]);
  }, [setItems]);

  const update = useCallback(async (id: string, updates: Partial<T>) => {
    await setItems((prev) => 
      prev.map(item => 
        item.id === id ? { ...item, ...updates } : item
      )
    );
  }, [setItems]);

  const remove = useCallback(async (id: string) => {
    await setItems((prev) => prev.filter(item => item.id !== id));
  }, [setItems]);

  const clear = useCallback(async () => {
    await setItems([]);
  }, [setItems]);

  const find = useCallback((predicate: (item: T) => boolean) => {
    return items.find(predicate);
  }, [items]);

  const filter = useCallback((predicate: (item: T) => boolean) => {
    return items.filter(predicate);
  }, [items]);

  return {
    items,
    byId,
    add,
    addMany,
    update,
    remove,
    clear,
    find,
    filter
  };
}

