/**
 * Tool Stacking with Concurrency Patterns
 * 
 * Implements 1:2 tool stacking where commonly paired tools
 * can execute in parallel with exponential concurrency growth
 */

import type { ITool, ToolResult } from '../Tool';
import type { RequestContext } from '../types/RequestContext';
import { wasmBridge, ConcurrencyPattern } from '../katalyst-wasm-bridge';
import { katalyst } from '../katalyst-integration';

// Common tool pairing configurations
export const TOOL_STACKS = {
  // File operations: Read -> Edit + Write
  fileOperations: {
    primary: 'FileReadTool',
    secondary: ['FileEditTool', 'FileWriteTool'],
    pattern: 'fanout' as ConcurrencyPattern
  },

  // Code analysis: Glob -> Grep + Read
  codeAnalysis: {
    primary: 'GlobTool',
    secondary: ['GrepTool', 'FileReadTool'],
    pattern: 'fanout' as ConcurrencyPattern
  },

  // Refactoring: Read -> Edit + Test
  refactoring: {
    primary: 'FileReadTool',
    secondary: ['MultiEditTool', 'BashTool'],
    pattern: 'diamond' as ConcurrencyPattern
  },

  // Architecture: Architect -> Task + Memory
  architecture: {
    primary: 'ArchitectTool',
    secondary: ['TaskTool', 'MemoryWriteTool'],
    pattern: 'exponential' as ConcurrencyPattern
  },

  // Testing: Edit -> Bash + Grep
  testing: {
    primary: 'FileEditTool',
    secondary: ['BashTool', 'GrepTool'],
    pattern: 'fanout' as ConcurrencyPattern
  },

  // Documentation: Read -> Memory + Notebook
  documentation: {
    primary: 'FileReadTool',
    secondary: ['MemoryWriteTool', 'NotebookEditTool'],
    pattern: 'parallel' as ConcurrencyPattern
  }
} as const;

// Tool execution metrics
interface ToolMetrics {
  toolName: string;
  executionTime: number;
  memoryUsed: number;
  concurrentExecutions: number;
}

// Stacked execution result
export interface StackedExecutionResult {
  primary: {
    tool: string;
    result: ToolResult;
    metrics: ToolMetrics;
  };
  secondary: Array<{
    tool: string;
    result: ToolResult;
    metrics: ToolMetrics;
  }>;
  pattern: ConcurrencyPattern;
  totalTime: number;
  parallelSpeedup: number;
}

/**
 * Tool Stacking Orchestrator
 * Manages concurrent execution of tool stacks
 */
export class ToolStackingOrchestrator {
  private toolRegistry: Map<string, ITool> = new Map();
  private executionHistory: StackedExecutionResult[] = [];
  private activeStacks: Set<string> = new Set();

  constructor(tools: ITool[]) {
    // Register all tools
    for (const tool of tools) {
      this.toolRegistry.set(tool.name, tool);
    }

    // Pre-configure common stacks in WASM bridge
    this.initializeStacks();
  }

  /**
   * Initialize predefined tool stacks
   */
  private initializeStacks(): void {
    for (const [name, config] of Object.entries(TOOL_STACKS)) {
      wasmBridge.defineToolStack(name, {
        primary: config.primary,
        secondary: config.secondary,
        concurrency: config.pattern as any,
        maxDepth: 3
      });
    }
  }

  /**
   * Execute a predefined tool stack
   */
  async executeStack(
    stackName: keyof typeof TOOL_STACKS,
    params: any,
    context: RequestContext
  ): Promise<StackedExecutionResult> {
    const config = TOOL_STACKS[stackName];

    if (!config) {
      throw new Error(`Unknown tool stack: ${stackName}`);
    }

    // Mark stack as active
    this.activeStacks.add(stackName);

    try {
      const primaryTool = this.toolRegistry.get(config.primary);
      const secondaryTools = config.secondary.map(name => 
        this.toolRegistry.get(name)!
      );

      if (!primaryTool) {
        throw new Error(`Primary tool not found: ${config.primary}`);
      }

      // Execute based on pattern
      const result = await this.executePattern(
        config.pattern,
        primaryTool,
        secondaryTools,
        params,
        context
      );

      // Store in history
      this.executionHistory.push(result);

      // Keep only last 100 executions
      if (this.executionHistory.length > 100) {
        this.executionHistory.shift();
      }

      return result;
    } finally {
      this.activeStacks.delete(stackName);
    }
  }

  /**
   * Execute tools with specific concurrency pattern
   */
  private async executePattern(
    pattern: ConcurrencyPattern,
    primaryTool: ITool,
    secondaryTools: ITool[],
    params: any,
    context: RequestContext
  ): Promise<StackedExecutionResult> {
    const startTime = performance.now();

    switch (pattern) {
      case 'fanout':
        return this.executeFanout(primaryTool, secondaryTools, params, context, startTime);

      case 'fanin':
        return this.executeFanin(primaryTool, secondaryTools, params, context, startTime);

      case 'diamond':
        return this.executeDiamond(primaryTool, secondaryTools, params, context, startTime);

      case 'exponential':
        return this.executeExponential(primaryTool, secondaryTools, params, context, startTime);

      case 'fibonacci':
        return this.executeFibonacci(primaryTool, secondaryTools, params, context, startTime);

      default:
        return this.executeLinear(primaryTool, secondaryTools, params, context, startTime);
    }
  }

  /**
   * Fanout pattern: Primary -> [Secondary1, Secondary2, ...]
   */
  private async executeFanout(
    primaryTool: ITool,
    secondaryTools: ITool[],
    params: any,
    context: RequestContext,
    startTime: number
  ): Promise<StackedExecutionResult> {
    // Execute primary tool
    const primaryResult = await this.executeTool(primaryTool, params, context);

    // Fan out to secondary tools in parallel
    const secondaryPromises = secondaryTools.map(tool =>
      this.executeTool(tool, primaryResult.result, context)
    );

    const secondaryResults = await Promise.all(secondaryPromises);

    const totalTime = performance.now() - startTime;
    const sequentialTime = primaryResult.metrics.executionTime + 
      secondaryResults.reduce((sum, r) => sum + r.metrics.executionTime, 0);

    return {
      primary: primaryResult,
      secondary: secondaryResults,
      pattern: 'fanout',
      totalTime,
      parallelSpeedup: sequentialTime / totalTime
    };
  }

  /**
   * Diamond pattern: Primary -> [Secondary] -> Reduce
   */
  private async executeDiamond(
    primaryTool: ITool,
    secondaryTools: ITool[],
    params: any,
    context: RequestContext,
    startTime: number
  ): Promise<StackedExecutionResult> {
    // Execute with WASM bridge for optimized diamond pattern
    const result = await wasmBridge.executeDiamondPattern(
      async (data) => {
        const tool = secondaryTools[Math.floor(Math.random() * secondaryTools.length)];
        return this.executeTool(tool, data, context);
      },
      async (results) => {
        // Reduce results
        return results[0]; // Simplified - would aggregate in real implementation
      },
      params,
      secondaryTools.length
    );

    const totalTime = performance.now() - startTime;

    return {
      primary: {
        tool: primaryTool.name,
        result: result.result,
        metrics: result.metrics
      },
      secondary: [],
      pattern: 'diamond',
      totalTime,
      parallelSpeedup: 1.0
    };
  }

  /**
   * Exponential pattern: Each level doubles concurrency
   */
  private async executeExponential(
    primaryTool: ITool,
    secondaryTools: ITool[],
    params: any,
    context: RequestContext,
    startTime: number
  ): Promise<StackedExecutionResult> {
    // Start with primary
    const primaryResult = await this.executeTool(primaryTool, params, context);

    // Execute exponential flow with secondary tools
    const operations = secondaryTools.map(tool => 
      async (data: any) => this.executeTool(tool, data, context)
    );

    const exponentialResults = await wasmBridge.executeExponentialFlow(
      operations,
      primaryResult.result,
      2 // 2 levels: 1 -> 2 -> 4
    );

    const totalTime = performance.now() - startTime;

    return {
      primary: primaryResult,
      secondary: exponentialResults.flat().filter(r => r && r.tool),
      pattern: 'exponential',
      totalTime,
      parallelSpeedup: exponentialResults.length / (totalTime / 1000)
    };
  }

  /**
   * Fibonacci pattern: 1, 1, 2, 3, 5, 8...
   */
  private async executeFibonacci(
    primaryTool: ITool,
    secondaryTools: ITool[],
    params: any,
    context: RequestContext,
    startTime: number
  ): Promise<StackedExecutionResult> {
    const primaryResult = await this.executeTool(primaryTool, params, context);

    const fibResults = await wasmBridge.executeFibonacciFlow(
      async (data, index) => {
        const tool = secondaryTools[index % secondaryTools.length];
        return this.executeTool(tool, data, context);
      },
      primaryResult.result,
      3 // 3 levels: 1, 1, 2
    );

    const totalTime = performance.now() - startTime;

    return {
      primary: primaryResult,
      secondary: fibResults.filter(r => r && r.tool),
      pattern: 'fibonacci',
      totalTime,
      parallelSpeedup: fibResults.length / (totalTime / 1000)
    };
  }

  /**
   * Fanin pattern: [Secondary] -> Primary
   */
  private async executeFanin(
    primaryTool: ITool,
    secondaryTools: ITool[],
    params: any,
    context: RequestContext,
    startTime: number
  ): Promise<StackedExecutionResult> {
    // Execute secondary tools first in parallel
    const secondaryPromises = secondaryTools.map(tool =>
      this.executeTool(tool, params, context)
    );

    const secondaryResults = await Promise.all(secondaryPromises);

    // Aggregate results and pass to primary
    const aggregated = {
      results: secondaryResults.map(r => r.result)
    };

    const primaryResult = await this.executeTool(primaryTool, aggregated, context);

    const totalTime = performance.now() - startTime;

    return {
      primary: primaryResult,
      secondary: secondaryResults,
      pattern: 'fanin',
      totalTime,
      parallelSpeedup: 1.0
    };
  }

  /**
   * Linear pattern: Primary -> Secondary1 -> Secondary2 -> ...
   */
  private async executeLinear(
    primaryTool: ITool,
    secondaryTools: ITool[],
    params: any,
    context: RequestContext,
    startTime: number
  ): Promise<StackedExecutionResult> {
    let currentResult = await this.executeTool(primaryTool, params, context);
    const secondaryResults = [];

    for (const tool of secondaryTools) {
      const result = await this.executeTool(tool, currentResult.result, context);
      secondaryResults.push(result);
      currentResult = result;
    }

    const totalTime = performance.now() - startTime;

    return {
      primary: currentResult,
      secondary: secondaryResults,
      pattern: 'linear',
      totalTime,
      parallelSpeedup: 1.0 // No parallelism in linear execution
    };
  }

  /**
   * Execute a single tool and collect metrics
   */
  private async executeTool(
    tool: ITool,
    params: any,
    context: RequestContext
  ): Promise<{
    tool: string;
    result: ToolResult;
    metrics: ToolMetrics;
  }> {
    const startTime = performance.now();
    const startMemory = performance.memory?.usedJSHeapSize || 0;

    // Execute tool
    const generator = tool.call(params, context);
    let result: ToolResult | undefined;

    for await (const progress of generator) {
      result = progress as ToolResult;
    }

    const executionTime = performance.now() - startTime;
    const memoryUsed = (performance.memory?.usedJSHeapSize || 0) - startMemory;

    return {
      tool: tool.name,
      result: result!,
      metrics: {
        toolName: tool.name,
        executionTime,
        memoryUsed,
        concurrentExecutions: this.activeStacks.size
      }
    };
  }

  /**
   * Get execution history analytics
   */
  getAnalytics(): {
    totalExecutions: number;
    averageSpeedup: number;
    mostUsedPattern: ConcurrencyPattern;
    toolUsageStats: Map<string, number>;
  } {
    const patternCounts = new Map<ConcurrencyPattern, number>();
    const toolCounts = new Map<string, number>();
    let totalSpeedup = 0;

    for (const execution of this.executionHistory) {
      // Count patterns
      const count = patternCounts.get(execution.pattern) || 0;
      patternCounts.set(execution.pattern, count + 1);

      // Count tool usage
      const primaryCount = toolCounts.get(execution.primary.tool) || 0;
      toolCounts.set(execution.primary.tool, primaryCount + 1);

      for (const secondary of execution.secondary) {
        const secCount = toolCounts.get(secondary.tool) || 0;
        toolCounts.set(secondary.tool, secCount + 1);
      }

      // Sum speedup
      totalSpeedup += execution.parallelSpeedup;
    }

    // Find most used pattern
    let mostUsedPattern: ConcurrencyPattern = 'linear';
    let maxCount = 0;
    for (const [pattern, count] of patternCounts.entries()) {
      if (count > maxCount) {
        maxCount = count;
        mostUsedPattern = pattern;
      }
    }

    return {
      totalExecutions: this.executionHistory.length,
      averageSpeedup: this.executionHistory.length > 0 
        ? totalSpeedup / this.executionHistory.length 
        : 0,
      mostUsedPattern,
      toolUsageStats: toolCounts
    };
  }

  /**
   * Create custom tool stack
   */
  createCustomStack(
    name: string,
    primary: string,
    secondary: string[],
    pattern: ConcurrencyPattern = 'fanout'
  ): void {
    wasmBridge.defineToolStack(name, {
      primary,
      secondary,
      concurrency: pattern as any,
      maxDepth: 3
    });
  }
}

// Export singleton instance
export const toolStacking = new ToolStackingOrchestrator([]);

// Helper function to execute stacked tools
export async function executeStackedTools(
  stack: keyof typeof TOOL_STACKS,
  params: any,
  context: RequestContext
): Promise<StackedExecutionResult> {
  return toolStacking.executeStack(stack, params, context);
}

