pub mod builtin;
pub mod codegen;
pub mod hir;
pub mod thir;
pub mod schemantics_compiler;  // Schemantics evolution compiler

pub use codegen::compile;
pub use schemantics_compiler::SchemanticsCompiler;

pub mod test {
    use internal_schema_diagnostics::Diagnostics;
    use internal_schema_parser_database::{parse_and_diagnostics, ParserDatabase};

    /// Shim helper function for testing.
    pub fn ast(source: &'static str) -> anyhow::Result<ParserDatabase> {
        let (parser_db, diagnostics) = parse_and_diagnostics(source)?;

        if diagnostics.has_errors() {
            let errors = diagnostics.to_pretty_string();
            anyhow::bail!("{errors}");
        }

        Ok(parser_db)
    }

    /// Shim helper function for testing.
    pub fn ast_and_diagnostics(
        source: &'static str,
    ) -> anyhow::Result<(ParserDatabase, Diagnostics)> {
        let (parser_db, diagnostics) =
            parse_and_diagnostics(source).expect("Failed to parse source");
        Ok((parser_db, diagnostics))
    }
}

//! Schemantics Semantics-lang: Transformer-Native Interpretability Engine
//! 
//! This module implements the semantics-lang interpretability framework for
//! understanding and manipulating transformer attention patterns, reasoning
//! chains, and emergent behaviors in language models.

use anyhow::Result;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;

pub mod commands;
pub mod interpretability;
pub mod neural_attribution;
pub mod reasoning;
pub mod protocol_shells;
pub mod deep_attribution;
pub mod advanced_reasoning;
pub mod symbolic_residue;

#[cfg(feature = "python")]
pub mod python_bindings;

/// Core Semantics-lang runtime context
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SemanticsContext {
    /// Active protocol shells for this context
    pub protocol_shells: Vec<ProtocolShell>,

    /// Neural attribution mappings
    pub attribution_map: AttributionMap,

    /// Reasoning chain tracker
    pub reasoning_chain: ReasoningChain,

    /// Emergence detection state
    pub emergence_state: EmergenceState,

    /// Meta-recursive depth
    pub recursion_depth: usize,
}

/// Protocol shell definition
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ProtocolShell {
    pub name: String,
    pub pattern: String,
    pub triggers: Vec<String>,
    pub effects: Vec<String>,
    pub diagnostic_alignment: Vec<String>,
}

/// Neural attribution mapping
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AttributionMap {
    pub attention_weights: HashMap<String, f32>,
    pub token_influences: Vec<TokenInfluence>,
    pub causal_chains: Vec<CausalChain>,
}

/// Token influence tracking
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct TokenInfluence {
    pub token: String,
    pub position: usize,
    pub influence_score: f32,
    pub downstream_effects: Vec<String>,
}

/// Causal chain in reasoning
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct CausalChain {
    pub steps: Vec<ReasoningStep>,
    pub confidence: f32,
    pub alternative_paths: Vec<Vec<ReasoningStep>>,
}

/// Individual reasoning step
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ReasoningStep {
    pub description: String,
    pub evidence: Vec<String>,
    pub confidence: f32,
    pub counterfactuals: Vec<String>,
}

/// Reasoning chain tracker
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ReasoningChain {
    pub current_chain: Vec<ReasoningStep>,
    pub historical_chains: Vec<Vec<ReasoningStep>>,
    pub branch_points: Vec<BranchPoint>,
}

/// Branch point in reasoning
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct BranchPoint {
    pub position: usize,
    pub alternatives: Vec<String>,
    pub selected: String,
    pub reasoning: String,
}

/// Emergence detection state
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct EmergenceState {
    pub detected_patterns: Vec<EmergentPattern>,
    pub capability_boundaries: Vec<CapabilityBoundary>,
    pub recursive_depth: usize,
}

/// Emergent pattern detection
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct EmergentPattern {
    pub pattern_type: String,
    pub description: String,
    pub confidence: f32,
    pub first_detected: String,
}

/// Capability boundary mapping
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct CapabilityBoundary {
    pub capability: String,
    pub boundary_type: String,
    pub threshold: f32,
    pub current_state: f32,
}

impl SemanticsContext {
    /// Create a new Semantics context
    pub fn new() -> Self {
        Self {
            protocol_shells: Vec::new(),
            attribution_map: AttributionMap {
                attention_weights: HashMap::new(),
                token_influences: Vec::new(),
                causal_chains: Vec::new(),
            },
            reasoning_chain: ReasoningChain {
                current_chain: Vec::new(),
                historical_chains: Vec::new(),
                branch_points: Vec::new(),
            },
            emergence_state: EmergenceState {
                detected_patterns: Vec::new(),
                capability_boundaries: Vec::new(),
                recursive_depth: 0,
            },
            recursion_depth: 0,
        }

