//! Natural Language Parser for Schemantics
//! 
//! This module implements human-friendly parsing and interpretation
//! based on "The Structure Behind Self-Expression" principles.
//! It allows developers to write prompts in natural, conversational language
//! while maintaining the type safety and parallelization capabilities of Schemantics.

use anyhow::{Result, Context};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use regex::Regex;

/// Natural Language Pattern Recognition Engine
#[derive(Debug, Clone)]
pub struct NaturalLanguageParser {
    /// Pattern matchers for common human expressions
    patterns: HashMap<String, PatternMatcher>,

    /// Context understanding stack
    context_stack: Vec<ContextFrame>,

    /// Intent recognition model
    intent_recognizer: IntentRecognizer,

    /// Semantic sugar mappings
    sugar_mappings: HashMap<String, String>,
}

/// Pattern matcher for natural language constructs
#[derive(Debug, Clone)]
pub struct PatternMatcher {
    /// Human-readable pattern description
    description: String,

    /// Regex pattern for matching
    pattern: Regex,

    /// Transformation to Schemantics syntax
    transform: TransformFunction,

    /// Confidence score for this pattern
    confidence: f32,
}

/// Context frame for understanding nested expressions
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ContextFrame {
    /// Current semantic context
    semantic_context: String,

    /// Active pronouns and their referents
    pronouns: HashMap<String, String>,

    /// Implied types from context
    implied_types: HashMap<String, String>,

    /// Conversation flow state
    flow_state: FlowState,
}

/// Conversation flow state
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum FlowState {
    Question,
    Statement,
    Command,
    Continuation,
    Elaboration,
}

/// Intent recognition for natural language
#[derive(Debug, Clone)]
pub struct IntentRecognizer {
    /// Intent patterns
    intents: Vec<IntentPattern>,

    /// Confidence threshold
    threshold: f32,
}

/// Intent pattern definition
#[derive(Debug, Clone)]
pub struct IntentPattern {
    /// Intent name
    name: String,

    /// Keywords that trigger this intent
    keywords: Vec<String>,

    /// Contextual clues
    context_clues: Vec<String>,

    /// Schemantics construct to generate
    construct: SchemanticsConstruct,
}

/// Schemantics language constructs
#[derive(Debug, Clone)]
pub enum SchemanticsConstruct {
    Function,
    Semantic,
    Evolution,
    Parallel,
    Test,
    Client,
}

/// Transformation function type
type TransformFunction = fn(&str, &ContextFrame) -> Result<String>;

impl NaturalLanguageParser {
    /// Create a new natural language parser
    pub fn new() -> Self {
        let mut parser = Self {
            patterns: HashMap::new(),
            context_stack: vec![ContextFrame::default()],
            intent_recognizer: IntentRecognizer::new(),
            sugar_mappings: Self::default_sugar_mappings(),
        };

        parser.initialize_patterns();
        parser
    }

    /// Parse natural language into Schemantics
    pub fn parse(&mut self, input: &str) -> Result<String> {
        // Stage 1: Intent recognition
        let intent = self.recognize_intent(input)?;

        // Stage 2: Pattern matching
        let patterns = self.match_patterns(input);

        // Stage 3: Context understanding
        let context = self.understand_context(input, &patterns)?;

        // Stage 4: Transform to Schemantics
        let schemantics = self.transform_to_schemantics(input, intent, patterns, context)?;

        Ok(schemantics)
    }

    /// Recognize the intent of natural language input
    fn recognize_intent(&self, input: &str) -> Result<SchemanticsConstruct> {
        let input_lower = input.to_lowercase();

        // Check for function-like expressions
        if input_lower.contains("extract") || input_lower.contains("process") || 
           input_lower.contains("analyze") || input_lower.contains("generate") {
            return Ok(SchemanticsConstruct::Function);
        }

        // Check for semantic type definitions
        if input_lower.contains("type") || input_lower.contains("schema") || 
           input_lower.contains("structure") || input_lower.contains("define") {
            return Ok(SchemanticsConstruct::Semantic);
        }

        // Check for evolution patterns
        if input_lower.contains("evolve") || input_lower.contains("adapt") || 
           input_lower.contains("mutate") || input_lower.contains("transform over time") {
            return Ok(SchemanticsConstruct::Evolution);
        }

        // Check for parallel processing
        if input_lower.contains("parallel") || input_lower.contains("simultaneously") || 
           input_lower.contains("distribute") || input_lower.contains("across gpus") {
            return Ok(SchemanticsConstruct::Parallel);
        }

        // Default to function
        Ok(SchemanticsConstruct::Function)
    }

    /// Match patterns in the input
    fn match_patterns(&self, input: &str) -> Vec<&PatternMatcher> {
        self.patterns
            .values()
            .filter(|p| p.pattern.is_match(input))
            .collect()
    }

    /// Understand context from input and patterns
    fn understand_context(&mut self, input: &str, patterns: &[&PatternMatcher]) -> Result<ContextFrame> {
        let mut context = self.context_stack.last().cloned()
            .unwrap_or_default();

        // Update flow state based on punctuation and keywords
        if input.ends_with('?') {
            context.flow_state = FlowState::Question;
        } else if input.starts_with("because") || input.starts_with("since") {
            context.flow_state = FlowState::Elaboration;
        } else if input.starts_with("and") || input.starts_with("also") {
            context.flow_state = FlowState::Continuation;
        } else if input.contains("!") {
            context.flow_state = FlowState::Command;
        } else {
            context.flow_state = FlowState::Statement;
        }

        // Extract pronouns and their potential referents
        self.extract_pronouns(input, &mut context);

        // Infer types from context
        self.infer_types(input, patterns, &mut context);

        self.context_stack.push(context.clone());
        Ok(context)
    }

    /// Transform natural language to Schemantics syntax
    fn transform_to_schemantics(
        &self,
        input: &str,
        intent: SchemanticsConstruct,
        patterns: Vec<&PatternMatcher>,
        context: ContextFrame,
    ) -> Result<String> {
        let mut output = String::new();

        match intent {
            SchemanticsConstruct::Function => {
                output.push_str(&self.generate_function(input, &context)?);
            },
            SchemanticsConstruct::Semantic => {
                output.push_str(&self.generate_semantic(input, &context)?);
            },
            SchemanticsConstruct::Evolution => {
                output.push_str(&self.generate_evolution(input, &context)?);
            },
            SchemanticsConstruct::Parallel => {
                output.push_str(&self.generate_parallel(input, &context)?);
            },
            _ => {
                output.push_str(&self.generate_generic(input, &context)?);
            }
        }

        // Apply semantic sugar transformations
        for (sugar, replacement) in &self.sugar_mappings {
            output = output.replace(sugar, replacement);
        }

        Ok(output)
    }

    /// Generate a function from natural language
    fn generate_function(&self, input: &str, context: &ContextFrame) -> Result<String> {
        let function_name = self.extract_function_name(input)?;
        let parameters = self.extract_parameters(input, context)?;
        let return_type = self.infer_return_type(input, context)?;

        Ok(format!(
            r#"function {}({}) -> {} @interpretable {{
    client "anthropic/claude-3-sonnet"

    // Natural language intent: {}
    .p/trace(on)
    .p/explain("reasoning_process")

    prompt #"
        {}

        {{{{ ctx.output_format }}}}
    "#
}}"#,
            function_name,
            parameters,
            return_type,
            input,
            self.clean_prompt_text(input)
        ))
    }

    /// Generate a semantic type from natural language
    fn generate_semantic(&self, input: &str, context: &ContextFrame) -> Result<String> {
        let type_name = self.extract_type_name(input)?;
        let fields = self.extract_fields(input, context)?;

        Ok(format!(
            r#"semantic {} @interpretable @evolving {{
    // Generated from: {}
    {}

    interpretability {{
        .p/trace: {{ enabled: true }}
        .p/inspect: {{ depth: 3 }}
        @safety: high
    }}
}}"#,
            type_name,
            input,
            fields
        ))
    }

    /// Generate evolution rules from natural language
    fn generate_evolution(&self, input: &str, context: &ContextFrame) -> Result<String> {
        let evolution_name = self.extract_evolution_name(input)?;
        let rules = self.extract_evolution_rules(input, context)?;

        Ok(format!(
            r#"evolution {} {{
    // Natural language specification: {}
    {}

    @constraint("fitness > 0.7")
    @recursive({}, infinite)
}}"#,
            evolution_name,
            input,
            rules,
            evolution_name
        ))
    }

    /// Generate parallel execution block
    fn generate_parallel(&self, input: &str, context: &ContextFrame) -> Result<String> {
        Ok(format!(
            r#"parallel {{
    // Intent: {}
    @bend: {{
        split_strategy: "recursive"
        auto_parallelize: true
    }}
    @orchestration: elixir
    @recursion: {{ depth: infinite }}
}}"#,
            input
        ))
    }

    /// Generate generic Schemantics code
    fn generate_generic(&self, input: &str, context: &ContextFrame) -> Result<String> {
        Ok(format!(
            r#"// Natural language input: {}
// Context: {:?}
// TODO: Implement specific transformation
{}"#,
            input,
            context.flow_state,
            input
        ))
    }

    /// Initialize pattern matchers
    fn initialize_patterns(&mut self) {
        // Pattern: "I want to..." -> function
        self.add_pattern(
            "want_to",
            r"(?i)i want to (\w+)",
            "Function request",
            transform_want_to,
            0.9
        );

        // Pattern: "Extract all..." -> extraction function
        self.add_pattern(
            "extract_all",
            r"(?i)extract all (\w+)",
            "Extraction function",
            transform_extract_all,
            0.95
        );

        // Pattern: "Define a..." -> semantic type
        self.add_pattern(
            "define_a",
            r"(?i)define a(n)? (\w+)",
            "Type definition",
            transform_define_a,
            0.9
        );

        // Pattern: "Process...in parallel" -> parallel block
        self.add_pattern(
            "in_parallel",
            r"(?i)(\w+).*in parallel",
            "Parallel processing",
            transform_parallel,
            0.85
        );
    }

    /// Add a pattern matcher
    fn add_pattern(&mut self, name: &str, regex: &str, desc: &str, transform: TransformFunction, confidence: f32) {
        if let Ok(pattern) = Regex::new(regex) {
            self.patterns.insert(
                name.to_string(),
                PatternMatcher {
                    description: desc.to_string(),
                    pattern,
                    transform,
                    confidence,
                }
            );
        }
    }

    /// Extract function name from natural language
    fn extract_function_name(&self, input: &str) -> Result<String> {
        // Simple heuristic: first verb becomes function name
        let words: Vec<&str> = input.split_whitespace().collect();
        for word in words {
            if self.is_verb(word) {
                return Ok(self.to_camel_case(word));
            }
        }
        Ok("Process".to_string())
    }

    /// Extract parameters from natural language
    fn extract_parameters(&self, input: &str, context: &ContextFrame) -> Result<String> {
        // Look for "from", "with", "using" to identify parameters
        if input.contains("from") || input.contains("with") || input.contains("using") {
            // Simple extraction - can be enhanced
            return Ok("input: string");
        }
        Ok("")
    }

    /// Infer return type from context
    fn infer_return_type(&self, input: &str, context: &ContextFrame) -> Result<String> {
        if input.contains("list") || input.contains("all") {
            return Ok("any[]");
        }
        if input.contains("text") || input.contains("string") {
            return Ok("string");
        }
        if input.contains("number") || input.contains("count") {
            return Ok("int");
        }
        Ok("any")
    }

    /// Extract type name from input
    fn extract_type_name(&self, input: &str) -> Result<String> {
        let words: Vec<&str> = input.split_whitespace().collect();
        for (i, word) in words.iter().enumerate() {
            if *word == "a" || *word == "an" {
                if i + 1 < words.len() {
                    return Ok(self.to_pascal_case(words[i + 1]));
                }
            }
        }
        Ok("CustomType".to_string())
    }

    /// Extract fields from natural language description
    fn extract_fields(&self, input: &str, context: &ContextFrame) -> Result<String> {
        let mut fields = Vec::new();

        // Look for "has", "contains", "with" to identify fields
        if input.contains("has") || input.contains("contains") || input.contains("with") {
            // Simple field extraction
            fields.push("    id: string @traced");
            fields.push("    value: any @observable");
        }

        Ok(fields.join("\n"))
    }

    /// Extract evolution name
    fn extract_evolution_name(&self, input: &str) -> Result<String> {
        Ok("NaturalEvolution".to_string())
    }

    /// Extract evolution rules
    fn extract_evolution_rules(&self, input: &str, context: &ContextFrame) -> Result<String> {
        Ok("    state => evolve(state)".to_string())
    }

    /// Extract pronouns and their referents
    fn extract_pronouns(&self, input: &str, context: &mut ContextFrame) {
        // Simple pronoun extraction
        for pronoun in &["it", "they", "this", "that", "these", "those"] {
            if input.contains(pronoun) {
                // Track pronoun usage for context
                context.pronouns.insert(pronoun.to_string(), "referent".to_string());
            }
        }
    }

    /// Infer types from patterns
    fn infer_types(&self, input: &str, patterns: &[&PatternMatcher], context: &mut ContextFrame) {
        // Infer types based on keywords
        if input.contains("person") || input.contains("user") {
            context.implied_types.insert("default".to_string(), "Person".to_string());
        }
        if input.contains("text") || input.contains("string") {
            context.implied_types.insert("input".to_string(), "string".to_string());
        }
    }

    /// Clean prompt text for embedding
    fn clean_prompt_text(&self, input: &str) -> String {
        input
            .trim()
            .replace("I want to", "")
            .replace("Please", "")
            .trim()
            .to_string()
    }

    /// Check if a word is a verb
    fn is_verb(&self, word: &str) -> bool {
        // Simple verb detection - can be enhanced with NLP
        let verbs = ["extract", "process", "analyze", "generate", "create", "build", "find", "search"];
        verbs.contains(&word.to_lowercase().as_str())
    }

    /// Convert to camel case
    fn to_camel_case(&self, s: &str) -> String {
        let mut result = String::new();
        let mut capitalize_next = false;

        for (i, ch) in s.chars().enumerate() {
            if i == 0 {
                result.push(ch.to_ascii_lowercase());
            } else if ch == '_' || ch == '-' {
                capitalize_next = true;
            } else if capitalize_next {
                result.push(ch.to_ascii_uppercase());
                capitalize_next = false;
            } else {
                result.push(ch);
            }
        }

        result
    }

    /// Convert to Pascal case
    fn to_pascal_case(&self, s: &str) -> String {
        let camel = self.to_camel_case(s);
        let mut chars = camel.chars();
        match chars.next() {
            None => String::new(),
            Some(first) => first.to_uppercase().collect::<String>() + chars.as_str(),
        }
    }

    /// Default semantic sugar mappings
    fn default_sugar_mappings() -> HashMap<String, String> {
        let mut mappings = HashMap::new();

        // Common conversational patterns to Schemantics
        mappings.insert("basically".to_string(), "".to_string());
        mappings.insert("like".to_string(), "".to_string());
        mappings.insert("you know".to_string(), "".to_string());
        mappings.insert("I mean".to_string(), "".to_string());
        mappings.insert("sort of".to_string(), "~".to_string());
        mappings.insert("kind of".to_string(), "~".to_string());
        mappings.insert("and so on".to_string(), "...".to_string());
        mappings.insert("etc".to_string(), "...".to_string());
        mappings.insert("for example".to_string(), "// Example:".to_string());

        mappings
    }
}

impl Default for ContextFrame {
    fn default() -> Self {
        Self {
            semantic_context: String::new(),
