}

fn transform_define_a(input: &str, context: &ContextFrame) -> Result<String> {
    Ok(format!("// Type definition: {}", input))
}

fn transform_parallel(input: &str, context: &ContextFrame) -> Result<String> {
    Ok(format!("// Parallel processing: {}", input))
