        // Test function generation
        let input = "I want to extract all people from text";
        let result = parser.parse(input).unwrap();
        assert!(result.contains("function"));
        assert!(result.contains("extract"));

        // Test semantic type generation
        let input2 = "Define a Person with name and age";
        let result2 = parser.parse(input2).unwrap();
        assert!(result2.contains("semantic"));

        // Test parallel processing
        let input3 = "Process all images in parallel";
        let result3 = parser.parse(input3).unwrap();
