        let intent1 = parser.recognize_intent("extract data from file").unwrap();
        assert!(matches!(intent1, SchemanticsConstruct::Function));

        let intent2 = parser.recognize_intent("define a new type for users").unwrap();
        assert!(matches!(intent2, SchemanticsConstruct::Semantic));

        let intent3 = parser.recognize_intent("evolve the schema over time").unwrap();
        assert!(matches!(intent3, SchemanticsConstruct::Evolution));
    }
}

