//! Terminal Interface and REPL for Schemantics
//! 
//! Interactive Read-Eval-Print Loop with syntax highlighting, 
//! real-time interpretation feedback, and context-aware suggestions.
//! This creates a native terminal experience for the Schemantics language.

use anyhow::{Result, Context};
use colored::*;
use rustyline::error::ReadlineError;
use rustyline::{CompletionType, Config, Editor};
use rustyline::completion::{Completer, Pair};
use rustyline::highlight::{Highlighter, MatchingBracketHighlighter};
use rustyline::hint::{Hinter, HistoryHinter};
use rustyline::validate::{Validator, ValidationResult, ValidationContext};
use rustyline_derive::Helper;
use std::borrow::Cow::{self, Borrowed, Owned};
use std::collections::HashMap;
use syntect::easy::HighlightLines;
use syntect::highlighting::{ThemeSet, Style};
use syntect::parsing::SyntaxSet;
use syntect::util::{as_24_bit_terminal_escaped, LinesWithEndings};

/// Schemantics REPL - Interactive terminal interface
pub struct SchemanticsRepl {
    /// Rustyline editor
    editor: Editor<SchemanticsHelper>,

    /// Interpretation engine
    interpreter: InterpreterState,

    /// Syntax highlighting
    syntax_highlighter: SyntaxHighlighter,

    /// Context tracker
    context: ReplContext,

    /// Display settings
    settings: ReplSettings,
}

/// REPL interpreter state
pub struct InterpreterState {
    /// Natural language parser
    nl_parser: Box<dyn NaturalLanguageParser>,

    /// Interpretation engine
    engine: Box<dyn InterpretationEngine>,

    /// Loaded modules
    modules: HashMap<String, Module>,

    /// Execution history
    history: Vec<ExecutionRecord>,
}

/// Natural language parser trait
trait NaturalLanguageParser: Send + Sync {
    fn parse(&mut self, input: &str) -> Result<String>;
    fn suggest(&self, partial: &str) -> Vec<String>;
}

/// Interpretation engine trait
trait InterpretationEngine: Send + Sync {
    fn interpret(&mut self, code: &str) -> Result<InterpretationResult>;
    fn get_context(&self) -> Context;
}

/// Interpretation result
struct InterpretationResult {
    output: String,
    reasoning: Vec<String>,
    attributions: HashMap<String, f32>,
    confidence: f32,
}

/// Context for interpretation
struct Context {
    variables: HashMap<String, Value>,
    functions: HashMap<String, Function>,
    types: HashMap<String, Type>,
}

/// Module representation
struct Module {
    name: String,
    exports: Vec<String>,
    code: String,
}

/// Execution record
struct ExecutionRecord {
    input: String,
    output: String,
    timestamp: std::time::Instant,
    success: bool,
}

/// Syntax highlighter for Schemantics
pub struct SyntaxHighlighter {
    /// Syntax definitions
    ps: SyntaxSet,

    /// Color themes
    ts: ThemeSet,

    /// Current theme
    theme_name: String,
}

/// REPL context tracker
pub struct ReplContext {
    /// Current mode
    mode: ReplMode,

    /// Active completions
    completions: Vec<String>,

    /// Multiline buffer
    multiline_buffer: String,

    /// Depth of nested constructs
    nesting_depth: usize,

    /// Current semantic context
    semantic_context: String,
}

/// REPL modes
#[derive(Debug, Clone, PartialEq)]
pub enum ReplMode {
    /// Normal mode
    Normal,

    /// Natural language mode
    NaturalLanguage,

    /// Debug mode
    Debug,

    /// Multiline input mode
    Multiline,

    /// Help mode
    Help,
}

/// REPL display settings
pub struct ReplSettings {
    /// Show neural attributions
    show_attributions: bool,

    /// Show reasoning chain
    show_reasoning: bool,

    /// Show performance metrics
    show_metrics: bool,

    /// Syntax highlighting enabled
    syntax_highlighting: bool,

    /// Auto-suggestions enabled
    auto_suggest: bool,

    /// Color scheme
    color_scheme: ColorScheme,
}

/// Color scheme for the REPL
pub struct ColorScheme {
    prompt: Color,
    input: Color,
    output: Color,
    error: Color,
    warning: Color,
    info: Color,
    success: Color,
}

/// Helper for rustyline
#[derive(Helper)]
struct SchemanticsHelper {
    completer: SchemanticsCompleter,
    highlighter: MatchingBracketHighlighter,
    hinter: HistoryHinter,
    validator: SchemanticsValidator,
}

/// Completer for Schemantics
struct SchemanticsCompleter {
    /// Keywords
    keywords: Vec<String>,

    /// Functions
    functions: Vec<String>,

    /// Types
    types: Vec<String>,

    /// Context-aware completions
    context_completions: HashMap<String, Vec<String>>,
}

/// Validator for Schemantics syntax
struct SchemanticsValidator;

/// Value types
enum Value {
    String(String),
    Number(f64),
    Boolean(bool),
    Array(Vec<Value>),
    Object(HashMap<String, Value>),
}

/// Function definition
struct Function {
    name: String,
    params: Vec<String>,
    body: String,
}

/// Type definition
struct Type {
    name: String,
    fields: HashMap<String, String>,
}

impl SchemanticsRepl {
    /// Create a new REPL instance
    pub fn new() -> Result<Self> {
        let config = Config::builder()
            .history_ignore_space(true)
            .completion_type(CompletionType::Circular)
            .build();

        let helper = SchemanticsHelper {
            completer: SchemanticsCompleter::new(),
            highlighter: MatchingBracketHighlighter::new(),
            hinter: HistoryHinter {},
            validator: SchemanticsValidator,
        };

        let mut editor = Editor::with_config(config)?;
        editor.set_helper(Some(helper));
        editor.load_history(".schemantics_history").ok();

        Ok(Self {
            editor,
            interpreter: InterpreterState::new(),
            syntax_highlighter: SyntaxHighlighter::new(),
            context: ReplContext::new(),
            settings: ReplSettings::default(),
        })
    }

    /// Run the REPL
    pub fn run(&mut self) -> Result<()> {
        self.print_welcome();

        loop {
            let prompt = self.get_prompt();

            match self.editor.readline(&prompt) {
                Ok(line) => {
                    if let Err(e) = self.process_input(&line) {
                        self.print_error(&format!("Error: {}", e));
                    }
                    self.editor.add_history_entry(&line)?;
                }
                Err(ReadlineError::Interrupted) => {
                    self.print_info("Interrupted (Ctrl-C). Use Ctrl-D to exit.");
                    continue;
                }
                Err(ReadlineError::Eof) => {
                    self.print_info("Exiting Schemantics REPL...");
                    break;
                }
                Err(err) => {
                    self.print_error(&format!("Readline error: {}", err));
                    break;
                }
            }
        }

        self.editor.save_history(".schemantics_history")?;
        Ok(())
    }

    /// Process user input
    fn process_input(&mut self, input: &str) -> Result<()> {
        // Handle special commands
        if input.starts_with(':') {
            return self.handle_command(input);
        }

        // Handle multiline input
        if self.context.mode == ReplMode::Multiline {
            self.context.multiline_buffer.push_str(input);
            self.context.multiline_buffer.push('\n');

            if self.is_complete_expression(&self.context.multiline_buffer) {
                let code = self.context.multiline_buffer.clone();
                self.context.multiline_buffer.clear();
                self.context.mode = ReplMode::Normal;
                return self.execute(&code);
            }
            return Ok(());
        }

        // Check if we need multiline mode
        if self.needs_multiline(input) {
            self.context.mode = ReplMode::Multiline;
            self.context.multiline_buffer = input.to_string();
            self.context.multiline_buffer.push('\n');
            return Ok(());
        }

        // Execute single line
        self.execute(input)
    }

    /// Execute code
    fn execute(&mut self, code: &str) -> Result<()> {
        // Parse natural language if in NL mode
        let processed_code = if self.context.mode == ReplMode::NaturalLanguage {
            self.print_info("Parsing natural language...");
            self.interpreter.nl_parser.parse(code)?
        } else {
            code.to_string()
        };

        // Show processed code if different
        if processed_code != code && self.settings.show_reasoning {
            self.print_info("Translated to Schemantics:");
            self.print_code(&processed_code);
        }

        // Interpret the code
        let start = std::time::Instant::now();
        let result = self.interpreter.engine.interpret(&processed_code)?;
        let duration = start.elapsed();

        // Display results
        self.display_result(&result, duration);

        // Record execution
        self.interpreter.history.push(ExecutionRecord {
            input: code.to_string(),
            output: result.output.clone(),
            timestamp: std::time::Instant::now(),
            success: true,
        });

        Ok(())
    }

    /// Display interpretation result
    fn display_result(&self, result: &InterpretationResult, duration: std::time::Duration) {
        // Main output
        println!("{}", result.output.green());

        // Show reasoning if enabled
        if self.settings.show_reasoning && !result.reasoning.is_empty() {
            println!("\n{}", "Reasoning Chain:".yellow());
            for (i, step) in result.reasoning.iter().enumerate() {
                println!("  {}. {}", i + 1, step);
            }
        }

        // Show attributions if enabled
        if self.settings.show_attributions && !result.attributions.is_empty() {
            println!("\n{}", "Neural Attributions:".cyan());
            for (token, score) in result.attributions.iter().take(5) {
                let bar = self.create_attribution_bar(*score);
                println!("  {}: {} {:.2}", token, bar, score);
            }
        }

        // Show confidence
        println!("\n{}: {:.1}%", "Confidence".blue(), result.confidence * 100.0);

        // Show metrics if enabled
        if self.settings.show_metrics {
            println!("{}: {:.2}ms", "Execution time".magenta(), duration.as_secs_f64() * 1000.0);
        }
    }

    /// Create attribution visualization bar
    fn create_attribution_bar(&self, score: f32) -> String {
        let width = 20;
        let filled = (score * width as f32) as usize;
        let empty = width - filled;

        format!("{}{}",
            "‚ñà".repeat(filled).green(),
            "‚ñë".repeat(empty).dark_grey()
        )
    }

    /// Handle special commands
    fn handle_command(&mut self, command: &str) -> Result<()> {
        let parts: Vec<&str> = command.split_whitespace().collect();

        match parts.first().map(|s| *s) {
            Some(":help") | Some(":h") => self.show_help(),
            Some(":quit") | Some(":q") => std::process::exit(0),
            Some(":nl") => {
                self.context.mode = ReplMode::NaturalLanguage;
                self.print_info("Switched to Natural Language mode");
            },
            Some(":normal") => {
                self.context.mode = ReplMode::Normal;
                self.print_info("Switched to Normal mode");
            },
            Some(":debug") => {
                self.context.mode = ReplMode::Debug;
                self.settings.show_attributions = true;
                self.settings.show_reasoning = true;
                self.settings.show_metrics = true;
                self.print_info("Debug mode enabled");
            },
            Some(":clear") => {
                print!("\x1B[2J\x1B[1;1H");
                self.print_welcome();
            },
            Some(":history") => self.show_history(),
            Some(":load") if parts.len() > 1 => self.load_file(parts[1])?,
            Some(":save") if parts.len() > 1 => self.save_session(parts[1])?,
            Some(":theme") if parts.len() > 1 => self.set_theme(parts[1])?,
            Some(":set") if parts.len() > 2 => self.set_option(parts[1], parts[2])?,
            _ => self.print_error("Unknown command. Type :help for available commands."),
        }

        Ok(())
    }

    /// Show help information
    fn show_help(&self) {
        let help = r#"
Schemantics REPL Commands:

  :help, :h          Show this help message
  :quit, :q          Exit the REPL
  :nl                Switch to Natural Language mode
  :normal            Switch to Normal mode
  :debug             Enable debug mode (shows all information)
  :clear             Clear the screen
  :history           Show command history
  :load <file>       Load a Schemantics file
  :save <file>       Save current session
  :theme <name>      Change color theme
  :set <opt> <val>   Set option (e.g., :set reasoning on)

Keyboard Shortcuts:
  Ctrl-C             Cancel current input
  Ctrl-D             Exit REPL
  Ctrl-L             Clear screen
  Tab                Auto-complete
  ‚Üë/‚Üì               Navigate history

Natural Language Examples:
  "I want to extract all people from text"
  "Define a Person with name and age"
  "Process all images in parallel"
        "#;

        println!("{}", help.blue());
    }

    /// Show execution history
    fn show_history(&self) {
        println!("{}", "Execution History:".yellow());
        for (i, record) in self.interpreter.history.iter().rev().take(10).enumerate() {
            let status = if record.success { "‚úì".green() } else { "‚úó".red() };
            println!("  {} [{}] {}", i + 1, status, record.input.truncate_to(60));
        }
    }

    /// Load a file
    fn load_file(&mut self, path: &str) -> Result<()> {
        let content = std::fs::read_to_string(path)
            .context(format!("Failed to read file: {}", path))?;

        self.print_info(&format!("Loading file: {}", path));
        self.execute(&content)?;

        Ok(())
    }

    /// Save session to file
    fn save_session(&self, path: &str) -> Result<()> {
        let mut content = String::new();

        for record in &self.interpreter.history {
            content.push_str(&format!("// Input:\n{}\n", record.input));
            content.push_str(&format!("// Output:\n{}\n\n", record.output));
        }

        std::fs::write(path, content)
            .context(format!("Failed to save session to: {}", path))?;

        self.print_success(&format!("Session saved to: {}", path));
        Ok(())
    }

    /// Set color theme
    fn set_theme(&mut self, theme: &str) -> Result<()> {
        self.syntax_highlighter.theme_name = theme.to_string();
        self.print_info(&format!("Theme changed to: {}", theme));
        Ok(())
    }

    /// Set option
    fn set_option(&mut self, option: &str, value: &str) -> Result<()> {
        let enabled = value == "on" || value == "true" || value == "1";

        match option {
            "reasoning" => self.settings.show_reasoning = enabled,
            "attributions" => self.settings.show_attributions = enabled,
            "metrics" => self.settings.show_metrics = enabled,
            "highlighting" => self.settings.syntax_highlighting = enabled,
            "suggestions" => self.settings.auto_suggest = enabled,
            _ => {
                self.print_error(&format!("Unknown option: {}", option));
                return Ok(());
            }
        }

        self.print_info(&format!("Set {} to {}", option, value));
        Ok(())
    }

    /// Get prompt string
    fn get_prompt(&self) -> String {
        let mode_indicator = match self.context.mode {
            ReplMode::Normal => "Œª",
            ReplMode::NaturalLanguage => "üó£",
            ReplMode::Debug => "üîç",
            ReplMode::Multiline => "...",
            ReplMode::Help => "?",
        };

        if self.context.mode == ReplMode::Multiline {
            format!("{}{}‚îÇ ",
                " ".repeat(self.context.nesting_depth * 2),
                mode_indicator.cyan()
            )
        } else {
            format!("{}schemantics{}‚ùØ ",
                "[".dark_grey(),
                "]".dark_grey()
            )
        }
    }

    /// Check if expression needs multiline input
    fn needs_multiline(&self, input: &str) -> bool {
        input.ends_with('{') || 
        input.ends_with('(') || 
        input.ends_with('[') ||
        input.starts_with("function") ||
        input.starts_with("semantic") ||
        input.starts_with("evolution")
    }

    /// Check if multiline expression is complete
    fn is_complete_expression(&self, code: &str) -> bool {
        let open_braces = code.chars().filter(|c| *c == '{').count();
        let close_braces = code.chars().filter(|c| *c == '}').count();
        let open_parens = code.chars().filter(|c| *c == '(').count();
        let close_parens = code.chars().filter(|c| *c == ')').count();
        let open_brackets = code.chars().filter(|c| *c == '[').count();
        let close_brackets = code.chars().filter(|c| *c == ']').count();

        open_braces == close_braces && 
        open_parens == close_parens && 
        open_brackets == close_brackets
    }

    /// Print welcome message
    fn print_welcome(&self) {
        let welcome = r#"
‚ïî‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïó
‚ïë                                                               ‚ïë
‚ïë   ‚ö° SCHEMANTICS - Supercomputer Programming Language ‚ö°      ‚ïë
‚ïë                                                               ‚ïë
‚ïë   Natural Language ‚Üí Semantic Understanding ‚Üí Parallel GPU   ‚ïë
‚ïë                                                               ‚ïë
‚ïö‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïù

Type :help for commands, :nl for natural language mode
"#;
        println!("{}", welcome.bright_cyan());
    }

    /// Print code with syntax highlighting
    fn print_code(&self, code: &str) {
        if self.settings.syntax_highlighting {
            println!("{}", self.syntax_highlighter.highlight(code));
        } else {
            println!("{}", code);
        }
    }

    /// Print error message
    fn print_error(&self, msg: &str) {
        println!("{} {}", "‚úó".red(), msg.red());
    }

    /// Print warning message
    fn print_warning(&self, msg: &str) {
        println!("{} {}", "‚ö†".yellow(), msg.yellow());
    }

    /// Print info message
    fn print_info(&self, msg: &str) {
        println!("{} {}", "‚Ñπ".blue(), msg.blue());
    }

    /// Print success message
    fn print_success(&self, msg: &str) {
        println!("{} {}", "‚úì".green(), msg.green());
    }
}

// Implementation of helper structures

impl InterpreterState {
    fn new() -> Self {
        Self {
            nl_parser: Box::new(DummyNLParser),
            engine: Box::new(DummyEngine),
            modules: HashMap::new(),
            history: Vec::new(),
        }
    }
}

impl ReplContext {
    fn new() -> Self {
        Self {
            mode: ReplMode::Normal,
            completions: Vec::new(),
            multiline_buffer: String::new(),
            nesting_depth: 0,
            semantic_context: String::new(),
        }
    }
}

impl Default for ReplSettings {
    fn default() -> Self {
        Self {
            show_attributions: false,
            show_reasoning: false,
            show_metrics: false,
            syntax_highlighting: true,
            auto_suggest: true,
            color_scheme: ColorScheme::default(),
        }
    }
}

impl Default for ColorScheme {
    fn default() -> Self {
        Self {
            prompt: Color::Cyan,
            input: Color::White,
            output: Color::Green,
            error: Color::Red,
            warning: Color::Yellow,
            info: Color::Blue,
            success: Color::Green,
        }
    }
}

impl SyntaxHighlighter {
    fn new() -> Self {
        let ps = SyntaxSet::load_defaults_newlines();
        let ts = ThemeSet::load_defaults();

        Self {
            ps,
            ts,
            theme_name: "base16-ocean.dark".to_string(),
        }
    }

    fn highlight(&self, code: &str) -> String {
        // Simple keyword highlighting for now
        let mut highlighted = code.to_string();

        let keywords = vec![
            "function", "semantic", "evolution", "parallel", "client",
            "test", "class", "enum", "import", "return", "if", "else",
            "for", "while", "let", "const", "var"
        ];

        for keyword in keywords {
            let colored = keyword.bright_blue().to_string();
            highlighted = highlighted.replace(keyword, &colored);
        }

        highlighted
    }
}

impl SchemanticsCompleter {
    fn new() -> Self {
        Self {
            keywords: vec![
                "function".to_string(),
                "semantic".to_string(),
                "evolution".to_string(),
                "parallel".to_string(),
                "client".to_string(),
                "test".to_string(),
                "class".to_string(),
                "enum".to_string(),
            ],
            functions: vec![],
            types: vec![],
            context_completions: HashMap::new(),
        }
    }
}

impl Completer for SchemanticsHelper {
    type Candidate = Pair;

    fn complete(
        &self,
        line: &str,
        pos: usize,
        _ctx: &rustyline::Context<'_>,
    ) -> Result<(usize, Vec<Pair>), ReadlineError> {
        let mut candidates = Vec::new();

        let start = line[..pos].rfind(' ').map(|i| i + 1).unwrap_or(0);
        let prefix = &line[start..pos];

        // Add keyword completions
        for keyword in &self.completer.keywords {
            if keyword.starts_with(prefix) {
                candidates.push(Pair {
                    display: keyword.clone(),
                    replacement: keyword.clone(),
                });
            }
        }

        Ok((start, candidates))
    }
}

impl Highlighter for SchemanticsHelper {
    fn highlight<'l>(&self, line: &'l str, pos: usize) -> Cow<'l, str> {
        self.highlighter.highlight(line, pos)
    }

    fn highlight_prompt<'b, 's: 'b, 'p: 'b>(
        &'s self,
        prompt: &'p str,
        default: bool,
    ) -> Cow<'b, str> {
        if default {
            Borrowed(prompt)
        } else {
            Owned(prompt.bright_cyan().to_string())
        }
    }

    fn highlight_hint<'h>(&self, hint: &'h str) -> Cow<'h, str> {
        Owned(hint.dark_grey().to_string())
    }

    fn highlight_candidate<'c>(
        &self,
        candidate: &'c str,
        completion: CompletionType,
    ) -> Cow<'c, str> {
        match completion {
            CompletionType::Circular => Owned(candidate.bright_blue().to_string()),
            CompletionType::List => Borrowed(candidate),
        }
    }

    fn highlight_char(&self, line: &str, pos: usize, forced: bool) -> bool {
        self.highlighter.highlight_char(line, pos, forced)
    }
}

impl Hinter for SchemanticsHelper {
    type Hint = String;

    fn hint(&self, line: &str, pos: usize, ctx: &rustyline::Context<'_>) -> Option<String> {
        self.hinter.hint(line, pos, ctx)
    }
}

impl Validator for SchemanticsValidator {
    fn validate(&self, ctx: &mut ValidationContext) -> Result<ValidationResult, ReadlineError> {
        let input = ctx.input();

        // Check for incomplete expressions
        let open_braces = input.chars().filter(|c| *c == '{').count();
        let close_braces = input.chars().filter(|c| *c == '}').count();

        if open_braces > close_braces {
            Ok(ValidationResult::Incomplete)
        } else {
            Ok(ValidationResult::Valid(None))
        }
    }
}

// Dummy implementations for testing

struct DummyNLParser;

impl NaturalLanguageParser for DummyNLParser {
    fn parse(&mut self, input: &str) -> Result<String> {
        Ok(format!("// Parsed from: {}\nfunction Process() {{}}", input))
    }

    fn suggest(&self, partial: &str) -> Vec<String> {
        vec![]
    }
}

struct DummyEngine;

impl InterpretationEngine for DummyEngine {
    fn interpret(&mut self, code: &str) -> Result<InterpretationResult> {
        Ok(InterpretationResult {
            output: format!("Executed: {}", code.lines().next().unwrap_or("")),
            reasoning: vec!["Parsed input".to_string(), "Generated output".to_string()],
            attributions: HashMap::from([
                ("function".to_string(), 0.8),
                ("Process".to_string(), 0.6),
            ]),
            confidence: 0.85,
        })
    }

    fn get_context(&self) -> Context {
        Context {
            variables: HashMap::new(),
            functions: HashMap::new(),
            types: HashMap::new(),
        }
    }
}

// String truncation helper
trait Truncate {
    fn truncate_to(&self, max_len: usize) -> String;
}

impl Truncate for String {
    fn truncate_to(&self, max_len: usize) -> String {
        if self.len() <= max_len {
            self.clone()
        } else {
            format!("{}...", &self[..max_len - 3])
        }
    }
}

impl Truncate for str {
    fn truncate_to(&self, max_len: usize) -> String {
        if self.len() <= max_len {
            self.to_string()
        } else {
            format!("{}...", &self[..max_len - 3])
        }
    }
}

/// Main entry point for the REPL
pub fn run_repl() -> Result<()> {
    let mut repl = SchemanticsRepl::new()?;
    repl.run()
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_repl_creation() {
        let repl = SchemanticsRepl::new();
        assert!(repl.is_ok());
    }

    #[test]
    fn test_multiline_detection() {
        let repl = SchemanticsRepl::new().unwrap();
        assert!(repl.needs_multiline("function test() {"));
        assert!(repl.needs_multiline("semantic Person {"));
        assert!(!repl.needs_multiline("print('hello')"));
    }

    #[test]
    fn test_complete_expression() {
        let repl = SchemanticsRepl::new().unwrap();
        assert!(repl.is_complete_expression("{ }"));
        assert!(!repl.is_complete_expression("{ { }"));
        assert!(repl.is_complete_expression("()"));
        assert!(!repl.is_complete_expression("(()"));
    }
}

