// =============================================================================
// SCHEMANTICS SHOWCASE: Natural Language Programming
// =============================================================================
// This example demonstrates how Schemantics bridges natural human expression
// with type-safe computational contracts and massively parallel GPU execution.
// 
// Based on "The Structure Behind Self-Expression" for deep semantic understanding
// =============================================================================

// -----------------------------------------------------------------------------
// SECTION 1: Natural Language to Semantic Types
// -----------------------------------------------------------------------------

// Natural: "I want to define a person who can learn and evolve"
semantic Person @interpretable @evolving {
    // Core attributes with neural tracking
    name: string @traced
    age: int @observable
    skills: map<string, float> @parallel  // Skill -> Proficiency (0-1)
    knowledge: KnowledgeGraph @recursive
    personality: PersonalityVector @evolving

    // Deep interpretability configuration
    interpretability {
        .p/trace: { enabled: true, depth: 5 }
        .p/inspect: { layers: all, granularity: fine }
        .p/attribute: { track_all: true, threshold: 0.1 }
        @attribution: { 
            method: "integrated_gradients"
            baseline: "zero"
        }
        @safety: critical
    }

    // Evolution: People learn and grow
    evolution {
        // Skills improve with practice
        skills => improve_skills(skills, experience)

        // Knowledge expands recursively
        knowledge => knowledge + learn_from_interaction()

        // Personality adapts to context
        personality ~> adapt_personality(interactions, feedback)

        // Age naturally increases (time-based)
        age => age + 1 if yearly_update()

        // Constraints for realistic evolution
        @constraint("skill_values BETWEEN 0.0 AND 1.0")
        @constraint("age >= 0 AND age <= 150")
        @recursive(Person, depth: infinite)
    }
}

// Knowledge representation as a graph
semantic KnowledgeGraph @recursive {
    concepts: Concept[] @parallel
    relationships: Relationship[] @parallel

    semantic Concept {
        id: string
        name: string
        definition: string
        embedding: float[768]  // Semantic embedding vector
        confidence: float @observable
    }

    semantic Relationship {
        from: Concept
        to: Concept
        type: RelationType
        strength: float @range(0, 1)
    }

    evolution {
        // Knowledge graphs grow through connection
        concepts +> discover_related_concepts()
        relationships => strengthen_valid_connections()
        @recursive(KnowledgeGraph, infinite)
    }
}

// Personality as a high-dimensional vector
semantic PersonalityVector {
    dimensions: map<string, float> = {
        "openness": 0.5,
        "conscientiousness": 0.5,
        "extraversion": 0.5,
        "agreeableness": 0.5,
        "neuroticism": 0.5,
        "creativity": 0.5,
        "analytical": 0.5
    }

    evolution {
        dimensions => balance_personality(feedback, context)
    }
}

// -----------------------------------------------------------------------------
