   148‚Üí            return Err(AmdCoreError::InsufficientCUs(
   149‚Üí                level, required_cus, self.device_info.compute_units
   150‚Üí            ));
   151‚Üí        }
   152‚Üí        
   153‚Üí        self.compute_units_active = required_cus;
   154‚Üí        
   155‚Üí        let start = std::time::Instant::now();
   156‚Üí        
   157‚Üí        match level {
   158‚Üí            1 => self.execute_alpha_dense(input, output)?,
   159‚Üí            2 => self.execute_beta_dense(input, output)?,
   160‚Üí            3 => self.execute_gamma_dense(input, output)?,
