   163‚Üí            _ => return Err(AmdCoreError::InsufficientCUs(level, 0, 0)),
   164‚Üí        }
   165‚Üí        
   166‚Üí        let execution_time = start.elapsed().as_secs_f32();
   167‚Üí        
   168‚Üí        debug!("Dense processing level {} completed: {:.6}s with {} CUs", 
   169‚Üí               level, execution_time, required_cus);
   170‚Üí        
   171‚Üí        Ok(execution_time)
   172‚Üí    }
