   293‚Üí                    }
   294‚Üí                    
   295‚Üí                    let coherence = coherence_sum / chunk.len() as f32;
   296‚Üí                    chunk_output[i] = chunk[i] * 15.625 + coherence * (chunk[i] * coherence).sin();
   297‚Üí                }
   298‚Üí            }
   299‚Üí        }
   300‚Üí        
   301‚Üí        Ok(())
   302‚Üí    }
