   384‚Üí    fn query_device_info(backend: &ComputeBackend) -> Result<AmdDeviceInfo> {
   385‚Üí        match backend {
   386‚Üí            ComputeBackend::OpenCL { device, .. } => {
   387‚Üí                // Query actual OpenCL device properties
   388‚Üí                let name = device.name().unwrap_or("Unknown AMD GPU".to_string());
   389‚Üí                let compute_units = device.max_compute_units().unwrap_or(64) as u32;
   390‚Üí                let max_work_group_size = device.max_work_group_size().unwrap_or(256) as u32;
   391‚Üí                let local_memory_size = device.local_mem_size().unwrap_or(64 * 1024) as u64;
   392‚Üí                let global_memory_size = device.global_mem_size().unwrap_or(16 * 1024 * 1024 * 1024) as u64;
   393‚Üí                
   394‚Üí                let architecture = if name.contains("RX 7") { 
   395‚Üí                    AmdArchitecture::RDNA3 
   396‚Üí                } else if name.contains("RX 6") {
   397‚Üí                    AmdArchitecture::RDNA2
   398‚Üí                } else if name.contains("MI3") {
   399‚Üí                    AmdArchitecture::CDNA3
   400‚Üí                } else if name.contains("MI2") {
   401‚Üí                    AmdArchitecture::CDNA2
   402‚Üí                } else {
   403‚Üí                    AmdArchitecture::RDNA3 // Default to latest
   404‚Üí                };
   405‚Üí                
   406‚Üí                let peak_teraflops = Self::calculate_peak_tflops(&architecture, compute_units);
   407‚Üí                let wavefront_size = match architecture {
   408‚Üí                    AmdArchitecture::RDNA2 | AmdArchitecture::RDNA3 => 32,
   409‚Üí                    AmdArchitecture::CDNA2 | AmdArchitecture::CDNA3 => 64,
   410‚Üí                };
   411‚Üí                
   412‚Üí                Ok(AmdDeviceInfo {
   413‚Üí                    name,
   414‚Üí                    architecture,
   415‚Üí                    compute_units,
   416‚Üí                    max_work_group_size,
   417‚Üí                    local_memory_size,
   418‚Üí                    global_memory_size,
   419‚Üí                    max_clock_frequency: 2500, // MHz, would query actual
   420‚Üí                    peak_teraflops,
   421‚Üí                    wavefront_size,
   422‚Üí                })
   423‚Üí            },
   424‚Üí            _ => {
   425‚Üí                // Default high-end AMD configuration
   426‚Üí                Ok(AmdDeviceInfo {
   427‚Üí                    name: "AMD RDNA3 Dense Compute".to_string(),
   428‚Üí                    architecture: AmdArchitecture::RDNA3,
   429‚Üí                    compute_units: 128,
   430‚Üí                    max_work_group_size: 1024,
   431‚Üí                    local_memory_size: 128 * 1024,
   432‚Üí                    global_memory_size: 24 * 1024 * 1024 * 1024,
   433‚Üí                    max_clock_frequency: 2700,
   434‚Üí                    peak_teraflops: 75.0,
   435‚Üí                    wavefront_size: 32,
   436‚Üí                })
   437‚Üí            }
   438‚Üí        }
   439‚Üí    }
   440‚Üí    
   441‚Üí    fn calculate_peak_tflops(arch: &AmdArchitecture, cus: u32) -> f64 {
   442‚Üí        match arch {
   443‚Üí            AmdArchitecture::RDNA2 => (cus as f64) * 2.5 * 0.128,      // ~20-30 TFLOPS for RX 6900 XT
   444‚Üí            AmdArchitecture::RDNA3 => (cus as f64) * 2.7 * 0.150,      // ~35-75 TFLOPS for RX 7900 XTX
   445‚Üí            AmdArchitecture::CDNA2 => (cus as f64) * 1.7 * 0.400,      // ~45-95 TFLOPS for MI210-250X
   446‚Üí            AmdArchitecture::CDNA3 => (cus as f64) * 2.1 * 0.600,      // ~100-165 TFLOPS for MI300X
   447‚Üí        }
   448‚Üí    }
